import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { ChannelSearchResult, MessageSearchResult, FileSearchResult } from '../../../types';

const ResultsContainer = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  right: 0;
  background: ${props => props.theme.colors.background};
  border: 2px solid ${props => props.theme.colors.border};
  margin-top: 4px;
  max-height: 400px;
  overflow-y: auto;
  z-index: 1000;
  font-family: 'VT323', monospace;
`;

const Section = styled.div`
  padding: 8px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  
  &:last-child {
    border-bottom: none;
  }
`;

const SectionTitle = styled.h3`
  margin: 0 0 8px 0;
  font-size: 0.875rem;
  color: ${props => props.theme.colors.primary};
  display: flex;
  align-items: center;
  gap: 8px;
`;

const ResultItem = styled.div<{ isSelected?: boolean }>`
  padding: 4px;
  cursor: pointer;
  font-size: 0.875rem;
  display: flex;
  align-items: center;
  gap: 8px;
  background: ${props => props.isSelected ? props.theme.colors.border : 'transparent'};

  &:hover {
    background: ${props => props.theme.colors.border};
  }
`;

const NoResults = styled.div`
  padding: 8px;
  text-align: center;
  color: ${props => props.theme.colors.textSecondary};
`;

const LoadingText = styled.div`
  padding: 8px;
  text-align: center;
  color: ${props => props.theme.colors.primary};
  animation: blink 1s infinite;

  @keyframes blink {
    0% { opacity: 1; }
    50% { opacity: 0.5; }
    100% { opacity: 1; }
  }
`;

const Icon = styled.span`
  font-size: 1rem;
  color: ${props => props.theme.colors.textSecondary};
`;

const ViewAllButton = styled.button`
  width: 100%;
  padding: 8px;
  background: none;
  border: none;
  border-top: 2px solid ${props => props.theme.colors.border};
  color: ${props => props.theme.colors.primary};
  font-family: 'VT323', monospace;
  cursor: pointer;
  text-transform: uppercase;

  &:hover {
    background: ${props => props.theme.colors.border};
  }
`;

interface SearchResultsProps {
  isLoading: boolean;
  channels: ChannelSearchResult[];
  messages: MessageSearchResult[];
  files: FileSearchResult[];
  onSelectChannel: (channelId: string) => void;
  onSelectMessage: (channelId: string, messageId: string) => void;
  onSelectFile: (fileId: string) => void;
}

type ResultType = 'channel' | 'message' | 'file';

interface FlatResult {
  type: ResultType;
  id: string;
  channelId?: string;
}

const SearchResults: React.FC<SearchResultsProps> = ({
  isLoading,
  channels,
  messages,
  files,
  onSelectChannel,
  onSelectMessage,
  onSelectFile,
}) => {
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [flatResults, setFlatResults] = useState<FlatResult[]>([]);

  // Flatten results for keyboard navigation
  useEffect(() => {
    const newFlatResults: FlatResult[] = [
      ...channels.slice(0, 5).map(c => ({ type: 'channel' as const, id: c.id })),
      ...messages.slice(0, 5).map(m => ({ type: 'message' as const, id: m.id, channelId: m.channel_id })),
      ...files.slice(0, 5).map(f => ({ type: 'file' as const, id: f.id, channelId: f.channel_id }))
    ];
    setFlatResults(newFlatResults);
    setSelectedIndex(0);
  }, [channels, messages, files]);

  // Handle keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          setSelectedIndex(i => Math.min(i + 1, flatResults.length - 1));
          break;
        case 'ArrowUp':
          e.preventDefault();
          setSelectedIndex(i => Math.max(i - 1, 0));
          break;
        case 'Enter':
          e.preventDefault();
          const selected = flatResults[selectedIndex];
          if (selected) {
            handleSelect(selected);
          }
          break;
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [flatResults, selectedIndex]);

  const handleSelect = (result: FlatResult) => {
    switch (result.type) {
      case 'channel':
        onSelectChannel(result.id);
        break;
      case 'message':
        if (result.channelId) onSelectMessage(result.channelId, result.id);
        break;
      case 'file':
        onSelectFile(result.id);
        break;
    }
  };

  if (isLoading) {
    return (
      <ResultsContainer>
        <LoadingText>SEARCHING...</LoadingText>
      </ResultsContainer>
    );
  }

  const hasResults = channels.length > 0 || messages.length > 0 || files.length > 0;

  if (!hasResults) {
    return (
      <ResultsContainer>
        <NoResults>NO RESULTS FOUND</NoResults>
      </ResultsContainer>
    );
  }

  const totalResults = channels.length + messages.length + files.length;
  const shownResults = Math.min(channels.length, 5) + Math.min(messages.length, 5) + Math.min(files.length, 5);

  return (
    <ResultsContainer>
      {channels.length > 0 && (
        <Section>
          <SectionTitle>
            <Icon>#</Icon>
            CHANNELS
          </SectionTitle>
          {channels.slice(0, 5).map((channel, idx) => (
            <ResultItem
              key={channel.id}
              onClick={() => onSelectChannel(channel.id)}
              isSelected={selectedIndex === idx}
            >
              <Icon>#</Icon>
              {channel.name} ({channel.member_count} members)
            </ResultItem>
          ))}
        </Section>
      )}

      {messages.length > 0 && (
        <Section>
          <SectionTitle>
            <Icon>ðŸ’¬</Icon>
            MESSAGES
          </SectionTitle>
          {messages.slice(0, 5).map((message, idx) => (
            <ResultItem
              key={message.id}
              onClick={() => onSelectMessage(message.channel_id, message.id)}
              isSelected={selectedIndex === channels.slice(0, 5).length + idx}
            >
              <Icon>ðŸ’¬</Icon>
              {message.content.substring(0, 50)}
              {message.content.length > 50 ? '...' : ''} in #{message.channel_name}
            </ResultItem>
          ))}
        </Section>
      )}

      {files.length > 0 && (
        <Section>
          <SectionTitle>
            <Icon>ðŸ“Ž</Icon>
            FILES
          </SectionTitle>
          {files.slice(0, 5).map((file, idx) => (
            <ResultItem
              key={file.id}
              onClick={() => onSelectFile(file.id)}
              isSelected={selectedIndex === channels.slice(0, 5).length + messages.slice(0, 5).length + idx}
            >
              <Icon>ðŸ“Ž</Icon>
              {file.filename} in #{file.channel_name}
            </ResultItem>
          ))}
        </Section>
      )}

      {totalResults > shownResults && (
        <ViewAllButton onClick={() => console.log('View all results')}>
          View All Results ({totalResults})
        </ViewAllButton>
      )}
    </ResultsContainer>
  );
};

export default SearchResults;
