// User Types
export type UserStatus = 'online' | 'offline' | 'away' | 'busy';

export interface User {
  id: string;
  username: string;
  status: UserStatus;
  avatar_url?: string;
}

// Base types
export interface Channel {
  id: string;
  name: string;
  description?: string;
  is_direct_message: boolean;
  is_public: boolean;
  created_at: string;
  created_by_id: string;
  members: string[];
  unreadCount: number;
}

export interface RawMessage {
  id: number;
  content: string;
  user_id: number;
  channel_id: number;
  created_at: string;
  updated_at: string;
  parent_id?: number;
}

export interface Message {
  id: number;
  content: string;
  user_id: number;
  channel_id: number;
  created_at: string;
  updated_at?: string;
  parent_id?: number;
  reply_count?: number;
  is_expanded?: boolean;
  replies_loaded?: boolean;
  replies?: Message[];
}

// Store Message type
export interface StoreMessage {
  id: number;
  content: string;
  userId: number;
  channelId: number;
  createdAt: string;
  updatedAt?: string;
  parentId?: number;
  replyCount: number;
  isExpanded: boolean;
  repliesLoaded: boolean;
  replies?: StoreMessage[];
}

// Attachment type
export interface Attachment {
  id: string;
  type: 'image' | 'file';
  url: string;
  name: string;
  size: number;
}

// Reaction type
export interface Reaction {
  id: string;
  emoji: string;
  userId: string;
}

// Auth types
export interface AuthResponse {
  token: string;
  user: User;
}

export interface ApiAuthResponse {
  access_token: string;
  token_type: string;
  user: {
    id: string;
    username: string;
    email: string;
    full_name: string;
    status: 'online' | 'offline' | 'away' | 'busy';
    last_seen: string;
  };
}

// State types
export interface AuthState {
  isAuthenticated: boolean;
  user: User | null;
  token: string | null;
  loading: boolean;
  error: string | null;
}

export interface ChannelsState {
  channels: Channel[];
  activeChannel: Channel | null;
  loading: boolean;
  error: string | null;
}

export interface ChatState {
  activeChannelId: string | null;
  channels: Channel[];
  users: { [key: string]: User };
  loading: boolean;
  error: string | null;
}

export interface MessagesState {
  messagesByChannel: {
    [channelId: string]: StoreMessage[];
  };
  loading: boolean;
  error: string | null;
}

export interface UsersState {
  users: User[];
  onlineUsers: string[];
  loading: boolean;
  error: string | null;
}

// WebSocket message types
export type WebSocketMessageType = 
  | 'new_message'
  | 'message_updated'
  | 'message_deleted'
  | 'new_reply'
  | 'channel_joined'
  | 'join_channel'
  | 'channel_created'
  | 'channel_updated'
  | 'channel_deleted'
  | 'unread_count_updated'
  | 'user_status'
  | 'error'
  | 'pong';

// Base WebSocket message data interface
interface BaseWebSocketData {
  channelId?: string;
  channel_id?: number | string;
  messageId?: string;
  message_id?: string;
  parentMessageId?: string;
  parent_id?: string;
  error?: string;
  user_id?: string | number;
  status?: UserStatus;
  count?: number;
}

// Message-specific WebSocket data
interface MessageWebSocketData extends BaseWebSocketData {
  message: Message | RawMessage;
  reply?: Message | RawMessage;
}

// Channel-specific WebSocket data
interface ChannelWebSocketData extends BaseWebSocketData {
  message: Channel;
}

// Combined WebSocket data type
export type WebSocketMessageData = BaseWebSocketData & {
  message?: Message | RawMessage | Channel;
  reply?: Message | RawMessage;
};

// WebSocket message interface
export interface WebSocketMessage {
  type: WebSocketMessageType;
  data?: WebSocketMessageData;
}

// Type guards
export function isMessageData(data: any): data is Message | RawMessage {
  return (
    data &&
    typeof data === 'object' &&
    'id' in data &&
    'content' in data &&
    'user_id' in data &&
    'channel_id' in data
  );
}

export function isChannelData(data: WebSocketMessageData): data is ChannelWebSocketData {
  if (!data.message) return false;
  return 'is_direct_message' in data.message && 'is_public' in data.message;
}

// Root State type
export interface RootState {
  auth: AuthState;
  chat: ChatState;
  messages: MessagesState;
  users: UsersState;
}

export interface SearchResult {
  channels: ChannelSearchResult[];
  messages: MessageSearchResult[];
  files: FileSearchResult[];
}

export interface ChannelSearchResult {
  id: string;
  name: string;
  description?: string;
  is_direct_message: boolean;
  member_count: number;
}

export interface MessageSearchResult {
  id: string;
  content: string;
  created_at: string;
  sender_id: string;
  channel_id: string;
  channel_name: string;
}

export interface FileSearchResult {
  id: string;
  filename: string;
  file_type: string;
  file_path: string;
  created_at: string;
  channel_id: string;
  channel_name: string;
}
