import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../../../store/auth/authSlice';
import {
  setActiveChannel,
  setChannels,
  setUsers,
  updateUserStatus
} from '../../../store/chat/chatSlice';
import { addMessage } from '../../../store/messages/messagesSlice';
import Button from '../../common/Button';
import UserListItem from '../../common/UserListItem';
import ChannelListItem from '../../common/ChannelListItem';
import CreateChannelModal from '../../chat/CreateChannelModal';
import MessageInput from '../../chat/MessageInput';
import MessageList from '../../chat/MessageList';
import wsService, { WebSocketMessage } from '../../../services/websocket';
import { getChannels, getChannelUsers, joinChannel } from '../../../services/api/chat';
import { StoreMessage } from '../../../store/types';
import { RootState } from '../../../store/rootReducer';
import { Channel, User } from '../../../types';

const MainContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  font-family: 'VT323', monospace;
`;

const Sidebar = styled.div`
  width: 200px;
  border-right: 2px solid ${props => props.theme.colors.border};
  display: flex;
  flex-direction: column;
`;

const ChannelList = styled.div`
  flex: 1;
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const UserList = styled.div`
  height: 200px;
  padding: 16px;
  border-top: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const ChatArea = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
`;

const ChatHeader = styled.div`
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  display: flex;
  justify-content: space-between;
  align-items: center;

  h1 {
    margin: 0;
    text-transform: uppercase;
  }
`;

const ChatInput = styled.div`
  border-top: 2px solid ${props => props.theme.colors.border};
`;

const LogoutButton = styled.button`
  background: none;
  border: 2px solid ${props => props.theme.colors.error};
  color: ${props => props.theme.colors.error};
  padding: 4px 8px;
  font-family: 'VT323', monospace;
  cursor: pointer;
  text-transform: uppercase;

  &:hover {
    background: ${props => props.theme.colors.error};
    color: ${props => props.theme.colors.background};
  }
`;

const ChannelHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;

  h2 {
    margin: 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const CreateChannelButton = styled(Button)`
  padding: 2px 8px;
  font-size: 0.875rem;
`;

const MainLayout: React.FC = () => {
  const dispatch = useDispatch();
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);

  const activeChannelId = useSelector((state: RootState) => state.chat.activeChannelId);
  const channels = useSelector((state: RootState) => state.chat.channels);
  const users = useSelector((state: RootState) => state.chat.users);
  const activeChannel = channels.find((channel: any) => channel.id === activeChannelId);
  const currentUser = useSelector((state: RootState) => state.auth.user);
  const [channelMembers, setChannelMembers] = useState<{ [channelId: number]: User[] }>({});

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const fetchedChannels = await getChannels();
        dispatch(setChannels(fetchedChannels));

        if (fetchedChannels.length > 0) {
          const channelUsers = await getChannelUsers(fetchedChannels[0].id);
          dispatch(setUsers(channelUsers));
          dispatch(setActiveChannel(fetchedChannels[0].id));
        }
      } catch (error) {
        console.error('Failed to fetch initial data:', error);
      }
    };

    fetchInitialData();

    // Connect to WebSocket
    console.log('Connecting to WebSocket...');
    wsService.connect();

    // Check WebSocket connection status
    const checkConnection = setInterval(() => {
      const wsState = wsService.getChatSocketState();
      console.log('WebSocket state:', wsState);
      if (wsState !== WebSocket.OPEN) {
        console.log('Reconnecting WebSocket...');
        wsService.connect();
      }
    }, 5000);

    return () => {
      clearInterval(checkConnection);
      wsService.disconnect();
    };
  }, [dispatch]);

  // Handle WebSocket messages in a separate useEffect
  useEffect(() => {
    const handleMessage = (message: WebSocketMessage) => {
      console.log('Received WebSocket message:', message);

      // Handle both new message and message_sent events
      if ((message.type === 'message' || message.type === 'message_sent') && message.message) {
        try {
          const { id, content, channel_id, sender_id, created_at } = message.message;
          console.log('Processing message:', { id, content, channel_id, sender_id, created_at });

          if (!id || !content || !channel_id || !sender_id) {
            console.error('Invalid message format:', message);
            return;
          }

          const transformedMessage: StoreMessage = {
            id: String(id),
            content: content,
            channelId: String(channel_id),
            userId: String(sender_id),
            reactions: [],
            attachments: [],
            createdAt: created_at || new Date().toISOString(),
            updatedAt: created_at || new Date().toISOString()
          };

          console.log('Dispatching transformed message:', transformedMessage);
          dispatch(addMessage(transformedMessage));
        } catch (error) {
          console.error('Error handling WebSocket message:', error);
        }
      } else if (message.type === 'presence_update' && message.user_id && message.status) {
        dispatch(updateUserStatus({
          userId: message.user_id,
          status: message.status
        }));
      }
    };

    // onMessage returns a cleanup function
    const cleanup = wsService.onMessage(handleMessage);
    return cleanup;
  }, [dispatch]);

  useEffect(() => {
    const fetchChannelMembers = async () => {
      for (const channel of channels) {
        try {
          const members = await getChannelUsers(channel.id);
          setChannelMembers(prev => ({
            ...prev,
            [channel.id]: members
          }));
        } catch (error) {
          console.error(`Failed to fetch members for channel ${channel.id}:`, error);
        }
      }
    };

    fetchChannelMembers();
  }, [channels]);

  const handleChannelClick = (channelId: number) => {
    if (activeChannelId === channelId) {
      dispatch(setActiveChannel(null));
    } else {
      dispatch(setActiveChannel(channelId));
    }
  };

  const handleJoinChannel = async (channelId: number) => {
    try {
      await joinChannel(channelId);
      const members = await getChannelUsers(channelId);
      setChannelMembers(prev => ({
        ...prev,
        [channelId]: members
      }));
      dispatch(setActiveChannel(channelId));
    } catch (error) {
      console.error('Failed to join channel:', error);
    }
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  return (
    <MainContainer>
      <Sidebar>
        <ChannelList>
          <ChannelHeader>
            <h2>Channels</h2>
            <CreateChannelButton
              variant="secondary"
              size="small"
              onClick={() => setIsCreateModalOpen(true)}
            >
              +New
            </CreateChannelButton>
          </ChannelHeader>
          {channels.map((channel: Channel) => {
            const isJoined = channelMembers[channel.id]?.some(member => member.id === currentUser?.id) ?? false;
            const isActive = Boolean(activeChannelId && channel.id === activeChannelId);

            const handleClick = isJoined ? () => {
              const currentChannelId = channel.id;
              if (activeChannelId === currentChannelId) {
                dispatch(setActiveChannel(null));
              } else {
                dispatch(setActiveChannel(currentChannelId));
              }
            } : undefined;

            const handleJoin = !isJoined ? () => handleJoinChannel(channel.id) : undefined;

            return (
              <ChannelListItem
                key={channel.id}
                name={channel.name}
                isActive={isActive}
                hasUnread={channel.unread_count ? channel.unread_count > 0 : false}
                isDirect={channel.is_direct_message}
                isJoined={isJoined}
                onClick={handleClick}
                onJoin={handleJoin}
              />
            );
          })}
        </ChannelList>
        <UserList>
          <h2>Online Users</h2>
          {Object.values(users).map((user: User) => (
            <UserListItem
              key={user.id}
              username={user.username}
              status={user.status}
            />
          ))}
        </UserList>
      </Sidebar>
      <ChatArea>
        <ChatHeader>
          <h1>{activeChannel ? `${activeChannel.is_direct_message ? '@' : '#'}${activeChannel.name}` : 'Select a channel'}</h1>
          <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
        </ChatHeader>
        {activeChannelId !== null ? (
          <>
            <MessageList channelId={activeChannelId} />
            <ChatInput>
              <MessageInput channelId={activeChannelId} />
            </ChatInput>
          </>
        ) : (
          <div style={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#666' }}>
            Select a channel to start chatting
          </div>
        )}
      </ChatArea>
      {isCreateModalOpen && (
        <CreateChannelModal
          isOpen={isCreateModalOpen}
          onClose={() => setIsCreateModalOpen(false)}
        />
      )}
    </MainContainer>
  );
};

export default MainLayout;
