import React from 'react';
import styled from 'styled-components';
import { StoreMessage } from '../../../types';

interface ChatMessageProps {
  message: StoreMessage;
  isSelected: boolean;
}

const MessageContainer = styled.div<{ $isSelected: boolean }>`
  padding: 8px;
  margin: 4px 0;
  border-radius: 4px;
  background-color: ${props => props.$isSelected ? props.theme.colors.backgroundDark : 'transparent'};
  transition: background-color 0.3s ease;
`;

const MessageContent = styled.div`
  font-family: 'VT323', monospace;
  color: ${props => props.theme.colors.text};
  white-space: pre-wrap;
  word-break: break-word;
`;

const MessageMeta = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 4px;
  font-size: 0.875rem;
  color: ${props => props.theme.colors.textLight};
`;

const Username = styled.span`
  font-weight: bold;
  color: ${props => props.theme.colors.primary};
`;

const Timestamp = styled.span`
  color: ${props => props.theme.colors.textLight};
  font-size: 0.75rem;
`;

const Message: React.FC<ChatMessageProps> = ({ message, isSelected }) => {
  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    return date.toLocaleString();
  };

  return (
    <MessageContainer $isSelected={isSelected}>
      <MessageMeta>
        <Username>{message.userId}</Username>
        <Timestamp>{formatTimestamp(message.createdAt)}</Timestamp>
      </MessageMeta>
      <MessageContent>{message.content}</MessageContent>
    </MessageContainer>
  );
};

export default Message;
