import { Message } from '../types/websocket';

export interface Reaction {
  emoji: string;
  users: string[];
}

export interface StoreMessage {
  id: string;
  content: string;
  channelId: string;
  userId: string;
  createdAt: string;
  updatedAt: string;
  parentId?: string;
  reactions: Reaction[];
  attachments: string[];
  isEdited: boolean;
  mentions: string[];
  replyCount: number;
  repliesLoaded: boolean;
  isExpanded: boolean;
  replies: StoreMessage[];
}

export function transformMessage(message: Message): StoreMessage {
  // Convert reactions object to array
  const reactions: Reaction[] = Object.entries(message.reactions || {}).map(([emoji, users]) => ({
    emoji,
    users
  }));

  return {
    id: message.id,
    content: message.content,
    channelId: message.channel_id,
    userId: message.sender_id,
    createdAt: message.created_at,
    updatedAt: message.updated_at,
    parentId: message.parent_id,
    reactions,
    attachments: message.attachments || [],
    isEdited: message.is_edited || false,
    mentions: message.mentions || [],
    replyCount: 0,
    repliesLoaded: false,
    isExpanded: false,
    replies: []
  };
}
