import { WebSocketMessage } from '../../types';
import { getAuthToken } from '../api/auth';
import { store } from '../../store';
import { addMessage } from '../../store/messages/messagesSlice';

class WebSocketService {
  private socket: WebSocket | null = null;
  private messageHandlers: ((message: WebSocketMessage) => void)[] = [];
  private errorHandlers: ((error: { code: string; message: string }) => void)[] = [];
  private currentChannelId: string | null = null;
  private processedMessageIds: Set<string> = new Set();
  private reconnectAttempts: number = 0;
  private maxReconnectAttempts: number = 5;
  private reconnectTimeout: NodeJS.Timeout | null = null;
  private isReconnecting: boolean = false;

  private getReconnectDelay(): number {
    // Exponential backoff: 1s, 2s, 4s, 8s, 16s
    return Math.min(1000 * Math.pow(2, this.reconnectAttempts), 16000);
  }

  private async attemptReconnect(channelId: string) {
    if (this.isReconnecting) {
      console.log('Already attempting to reconnect...');
      return;
    }

    this.isReconnecting = true;
    
    if (this.reconnectAttempts >= this.maxReconnectAttempts) {
      console.error('Max reconnection attempts reached');
      this.errorHandlers.forEach(handler => handler({
        code: 'MAX_RECONNECT_ATTEMPTS',
        message: 'Unable to reconnect after multiple attempts. Please refresh the page.'
      }));
      this.isReconnecting = false;
      return;
    }

    console.log(`Attempting to reconnect (attempt ${this.reconnectAttempts + 1}/${this.maxReconnectAttempts})...`);
    
    try {
      await this.connect(channelId);
      this.reconnectAttempts = 0;
      this.isReconnecting = false;
    } catch (error) {
      this.reconnectAttempts++;
      this.isReconnecting = false;
      
      // Schedule next reconnection attempt
      const delay = this.getReconnectDelay();
      console.log(`Scheduling next reconnection attempt in ${delay}ms`);
      this.reconnectTimeout = setTimeout(() => {
        this.attemptReconnect(channelId);
      }, delay);
    }
  }

  connect(channelId: string) {
    return new Promise<void>((resolve, reject) => {
      // Clear any existing reconnection timeout
      if (this.reconnectTimeout) {
        clearTimeout(this.reconnectTimeout);
        this.reconnectTimeout = null;
      }

      // If already connected to this channel, do nothing
      if (this.socket?.readyState === WebSocket.OPEN && this.currentChannelId === channelId) {
        console.log('WebSocket already connected to channel', channelId);
        resolve();
        return;
      }

      // If connected to a different channel, disconnect first
      if (this.socket?.readyState === WebSocket.OPEN && this.currentChannelId !== channelId) {
        console.log('Disconnecting from channel', this.currentChannelId);
        this.disconnect();
      }

      const token = getAuthToken();
      if (!token) {
        const error = new Error('No auth token available for WebSocket connection');
        console.error(error);
        reject(error);
        return;
      }

      const baseWsUrl = process.env.REACT_APP_WS_URL || 'ws://localhost:8000/ws';
      const wsUrl = `${baseWsUrl}/chat?token=${token}`;
      console.log('Connecting to WebSocket:', wsUrl);
      
      try {
        this.socket = new WebSocket(wsUrl);
        this.currentChannelId = channelId;
        this.processedMessageIds.clear();

        // Set a connection timeout
        const connectionTimeout = setTimeout(() => {
          if (this.socket?.readyState !== WebSocket.OPEN) {
            console.error('WebSocket connection timeout');
            this.socket?.close();
            reject(new Error('WebSocket connection timeout'));
          }
        }, 10000);

        this.socket.onopen = () => {
          clearTimeout(connectionTimeout);
          console.log('WebSocket connection opened');
          this.joinChannel(channelId).then(resolve).catch(reject);
        };

        this.socket.onmessage = this.handleMessage;

        this.socket.onerror = (error) => {
          console.error('WebSocket error:', error);
          this.errorHandlers.forEach(handler => handler({
            code: 'WEBSOCKET_ERROR',
            message: 'WebSocket connection error'
          }));
          reject(error);
        };

        this.socket.onclose = (event) => {
          clearTimeout(connectionTimeout);
          console.log('WebSocket disconnected with code:', event.code);
          this.socket = null;

          // Handle specific close codes
          switch (event.code) {
            case 1000: // Normal closure
              this.currentChannelId = null;
              break;
            case 1003: // Unsupported
              this.errorHandlers.forEach(handler => handler({
                code: 'UNSUPPORTED',
                message: 'WebSocket connection not supported'
              }));
              break;
            case 4001: // Channel access denied
              this.errorHandlers.forEach(handler => handler({
                code: 'CHANNEL_ACCESS_DENIED',
                message: 'Access to the channel was denied'
              }));
              break;
            case 4002: // Authentication failed
              this.errorHandlers.forEach(handler => handler({
                code: 'AUTH_FAILED',
                message: 'Authentication failed'
              }));
              break;
            default:
              // Attempt to reconnect for unexpected closures
              if (this.currentChannelId) {
                this.attemptReconnect(this.currentChannelId);
              }
          }
        };
      } catch (error) {
        console.error('Error creating WebSocket:', error);
        this.errorHandlers.forEach(handler => handler({
          code: 'WEBSOCKET_ERROR',
          message: 'Failed to create WebSocket connection'
        }));
        reject(error);
      }
    });
  }

  disconnect() {
    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }
    
    if (this.socket) {
      if (this.currentChannelId && this.socket.readyState === WebSocket.OPEN) {
        const message = {
          type: 'leave_channel',
          channel_id: this.currentChannelId
        };
        this.socket.send(JSON.stringify(message));
      }
      this.socket.close(1000); // Normal closure
      this.socket = null;
      this.currentChannelId = null;
      this.reconnectAttempts = 0;
      this.isReconnecting = false;
    }
  }

  sendMessage(channelId: string, content: string) {
    // If not connected to the right channel, connect first
    if (this.currentChannelId !== channelId) {
      console.log('Connecting to channel before sending message');
      this.connect(channelId);
    }

    if (this.socket?.readyState === WebSocket.OPEN) {
      const message = {
        type: 'message',
        channel_id: channelId,
        content: content
      };
      this.socket.send(JSON.stringify(message));
    } else {
      console.error('WebSocket is not connected');
      this.errorHandlers.forEach(handler => handler({
        code: 'NOT_CONNECTED',
        message: 'WebSocket is not connected'
      }));
    }
  }

  async joinChannel(channelId: string) {
    if (this.socket?.readyState === WebSocket.OPEN) {
      console.log('Joining channel:', channelId);
      const message = {
        type: 'join_channel',
        channel_id: parseInt(channelId)
      };
      this.socket.send(JSON.stringify(message));
    } else {
      console.error('WebSocket is not connected');
      this.errorHandlers.forEach(handler => handler({
        code: 'NOT_CONNECTED',
        message: 'WebSocket is not connected'
      }));
    }
  }

  leaveChannel(channelId: string) {
    if (this.socket?.readyState === WebSocket.OPEN && this.currentChannelId === channelId) {
      const message = {
        type: 'leave_channel',
        channel_id: channelId
      };
      this.socket.send(JSON.stringify(message));
      this.disconnect();
    }
  }

  onMessage(handler: (message: WebSocketMessage) => void) {
    this.messageHandlers.push(handler);
    return () => {
      this.messageHandlers = this.messageHandlers.filter(h => h !== handler);
    };
  }

  onError(handler: (error: { code: string; message: string }) => void) {
    this.errorHandlers.push(handler);
    return () => {
      this.errorHandlers = this.errorHandlers.filter(h => h !== handler);
    };
  }

  getChatSocketState() {
    return this.socket?.readyState || WebSocket.CLOSED;
  }

  getCurrentChannelId() {
    return this.currentChannelId;
  }

  private handleMessage = (event: MessageEvent) => {
    try {
      const message = JSON.parse(event.data) as WebSocketMessage;
      console.log('WebSocket message received:', message);

      // Handle error messages
      if (message.type === 'error') {
        console.error('WebSocket error message:', message);
        this.errorHandlers.forEach(handler => handler({
          code: message.code || 'UNKNOWN_ERROR',
          message: message.content || 'Unknown error occurred'
        }));
        return;
      }

      // Handle channel access errors
      if (message.type === 'channel_access_denied') {
        console.error('Channel access denied:', message);
        this.errorHandlers.forEach(handler => handler({
          code: 'CHANNEL_ACCESS_DENIED',
          message: message.content || 'Access to this channel was denied'
        }));
        return;
      }

      // Filter out message_sent confirmations
      if (message.type === 'message_sent') {
        console.log('Ignoring message_sent confirmation');
        return;
      }

      // Handle new messages
      if (message.type === 'message' && message.message) {
        const messageId = message.message.id;
        if (messageId) {
          if (this.processedMessageIds.has(messageId)) {
            console.log('Ignoring duplicate message:', messageId);
            return;
          }
          this.processedMessageIds.add(messageId);

          // Limit the size of the Set to prevent memory growth
          if (this.processedMessageIds.size > 1000) {
            const idsArray = Array.from(this.processedMessageIds);
            this.processedMessageIds = new Set(idsArray.slice(-500));
          }

          // Transform and dispatch the message to Redux store
          const storeMessage = {
            id: String(message.message.id),
            content: message.message.content,
            channelId: String(message.message.channel_id),
            userId: String(message.message.sender_id),
            reactions: [],
            attachments: [],
            createdAt: message.message.created_at,
            updatedAt: message.message.created_at
          };
          store.dispatch(addMessage(storeMessage));
        }
      }

      // Handle successful messages
      this.messageHandlers.forEach(handler => handler(message));
    } catch (error) {
      console.error('Error parsing WebSocket message:', error);
    }
  };
}

const wsService = new WebSocketService();
export default wsService;
