import React, { useState } from 'react';
import styled from 'styled-components';
import MessageOptions from '../../chat/MessageOptions';

export interface ChatMessageProps {
  content: string;
  sender: string;
  timestamp: string;
  userId: string;
  currentUserId?: string;
  onDelete: () => void;
  replyCount: number;
  isExpanded: boolean;
  onToggleReplies: () => void;
  onReply: () => void;
  isReply?: boolean;
}

const MessageContainer = styled.div<{ isReply?: boolean }>`
  font-family: 'Courier New', monospace;
  padding: 2px 4px;
  color: #fff;
  position: relative;
  margin-left: ${({ isReply }) => isReply ? '24px' : '0'};
  
  &:hover {
    background-color: #2a2a2a;
  }
`;

const MessageContent = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 4px;
  white-space: pre-wrap;
  word-wrap: break-word;
`;

const Timestamp = styled.span`
  color: #888;
`;

const Sender = styled.span`
  color: #0f0;
  font-weight: bold;
`;

const ReplyCount = styled.button`
  background: none;
  border: none;
  color: #888;
  cursor: pointer;
  font-family: inherit;
  padding: 0;
  font-size: inherit;
  margin: 0 4px;

  &:hover {
    color: #fff;
  }
`;

const MenuButton = styled.button`
  background: none;
  border: none;
  color: #888;
  cursor: pointer;
  font-family: inherit;
  padding: 0;
  font-size: inherit;
  margin: 0 4px;
  visibility: hidden;

  ${MessageContainer}:hover & {
    visibility: visible;
  }

  &:hover {
    color: #fff;
  }
`;

const ReplyIndicator = styled.span`
  color: #888;
`;

const MessageText = styled.span`
  flex: 1;
`;

const Controls = styled.div`
  display: flex;
  align-items: center;
  gap: 4px;
  margin-left: 4px;
`;

const ChatMessage: React.FC<ChatMessageProps> = ({
  content,
  sender,
  timestamp,
  userId,
  currentUserId,
  onDelete,
  replyCount,
  isExpanded,
  onToggleReplies,
  onReply,
  isReply = false,
}) => {
  const [showOptions, setShowOptions] = useState(false);
  const formattedTime = new Date(timestamp).toLocaleTimeString([], { 
    hour: '2-digit', 
    minute: '2-digit',
    second: '2-digit',
    hour12: false 
  });

  return (
    <MessageContainer isReply={isReply}>
      <MessageContent>
        <Timestamp>[{formattedTime}]</Timestamp>
        {isReply && <ReplyIndicator>└─</ReplyIndicator>}
        <Sender>&lt;{sender}&gt;</Sender>
        <MessageText>{content}</MessageText>
        <Controls>
          {!isReply && replyCount > 0 && (
            <ReplyCount onClick={onToggleReplies}>
              [{isExpanded ? '-' : '+'} {replyCount}]
            </ReplyCount>
          )}
          {userId === currentUserId && (
            <>
              <MenuButton onClick={() => setShowOptions(!showOptions)}>[*]</MenuButton>
              {showOptions && (
                <MessageOptions 
                  onDelete={onDelete} 
                  onReply={onReply} 
                  onClose={() => setShowOptions(false)}
                />
              )}
            </>
          )}
        </Controls>
      </MessageContent>
    </MessageContainer>
  );
};

export default ChatMessage;
