import { WebSocketMessage } from '../../types';
import { getAuthToken } from '../api/auth';
import { store } from '../../store';
import { addMessage, setReplies } from '../../store/messages/messagesSlice';
import messageQueue from './messageQueue';

// Constants
const INITIAL_RECONNECT_DELAY = 1000;
const MAX_RECONNECT_DELAY = 30000;
const HEARTBEAT_INTERVAL = 15000;
const HEARTBEAT_TIMEOUT = 30000;
const CONNECTION_TIMEOUT = 5000;

class WebSocketService {
  private socket: WebSocket | null = null;
  private messageHandlers: ((message: WebSocketMessage) => void)[] = [];
  private errorHandlers: ((error: Error) => void)[] = [];
  private currentChannelId: string | null = null;
  private joinedChannels: Set<string> = new Set();
  private reconnectAttempts = 0;
  private reconnectTimeout: NodeJS.Timeout | null = null;
  private isReconnecting = false;
  private connectionPromise: Promise<void> | null = null;
  private joinPromise: Promise<void> | null = null;
  private lastMessageId: string | null = null;
  private processingQueue = false;
  private heartbeatInterval: NodeJS.Timeout | null = null;
  private lastHeartbeatResponse: number = Date.now();
  private connectionStartTime: number = 0;

  public async connect(channelId: string): Promise<void> {
    if (this.isConnected() && this.isConnectedToChannel(channelId)) {
      return;
    }

    // Always disconnect before connecting to ensure clean state
    this.disconnect();
    this.currentChannelId = channelId;
    this.isReconnecting = false;
    this.reconnectAttempts = 0;
    this.connectionStartTime = Date.now();

    try {
      const token = getAuthToken();
      if (!token) {
        throw new Error('No authentication token found');
      }

      this.socket = new WebSocket(`ws://localhost:8000/ws/chat?token=${token}`);

      // Set up socket event handlers immediately
      this.socket.onclose = () => {
        console.error('[DEBUG] WebSocket closed unexpectedly');
        this.handleReconnect();
      };

      this.socket.onerror = (error) => {
        console.error('[DEBUG] WebSocket error:', error);
        this.handleError(error instanceof Error ? error : new Error('WebSocket error'));
      };

      await this.waitForConnection();

      if (this.socket) {
        this.socket.onmessage = this.handleWebSocketMessage;
        await this.joinChannel(channelId);

        // Start heartbeat after successful connection
        this.startHeartbeat();

        // Process any queued messages after successful connection
        await this.processMessageQueue();
      }

    } catch (error) {
      console.error('[DEBUG] WebSocket connection failed:', error);
      this.handleError(error instanceof Error ? error : new Error('WebSocket connection failed'));
      throw error;
    }
  }

  private async waitForConnection(): Promise<void> {
    return new Promise<void>((resolve, reject) => {
      const timeout = setTimeout(() => {
        reject(new Error('WebSocket connection timeout'));
      }, CONNECTION_TIMEOUT);

      if (this.socket) {
        this.socket.onopen = () => {
          console.log('[DEBUG] WebSocket connection opened');
          clearTimeout(timeout);
          this.startHeartbeat();
          resolve();
        };

        this.socket.onerror = (error) => {
          console.error('[DEBUG] WebSocket connection error:', error);
          clearTimeout(timeout);
          reject(error);
        };

        this.socket.onclose = this.handleClose;
      }
    });
  }

  private startHeartbeat(): void {
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
    }

    this.heartbeatInterval = setInterval(() => {
      if (this.socket?.readyState === WebSocket.OPEN) {
        this.socket.send(JSON.stringify({ type: 'ping' }));

        // Check if we haven't received a response in too long
        if (Date.now() - this.lastHeartbeatResponse > HEARTBEAT_TIMEOUT) {
          console.error('[DEBUG] Heartbeat timeout, reconnecting...');
          this.handleReconnect();
        }
      }
    }, HEARTBEAT_INTERVAL);
  }

  private async handleReconnect(): Promise<void> {
    if (this.isReconnecting || !this.currentChannelId) return;

    this.isReconnecting = true;
    const delay = Math.min(INITIAL_RECONNECT_DELAY * Math.pow(2, this.reconnectAttempts), MAX_RECONNECT_DELAY);

    console.log(`[DEBUG] Attempting to reconnect in ${delay}ms (attempt ${this.reconnectAttempts + 1})`);

    this.reconnectTimeout = setTimeout(async () => {
      try {
        this.reconnectAttempts++;
        await this.connect(this.currentChannelId!);
        this.isReconnecting = false;
        this.reconnectAttempts = 0;
        console.log('[DEBUG] Reconnection successful');
      } catch (error) {
        console.error('[DEBUG] Reconnection failed:', error);
        this.handleReconnect();
      }
    }, delay);
  }

  private async processMessageQueue(): Promise<void> {
    if (this.processingQueue) return;

    this.processingQueue = true;
    try {
      while (this.isConnected()) {
        const channelId = this.currentChannelId;
        if (!channelId) break;

        const queuedMessage = messageQueue.getNextMessage(channelId);
        if (!queuedMessage) break;

        try {
          const message = {
            type: queuedMessage.type,
            data: {
              channel_id: parseInt(queuedMessage.channelId),
              content: queuedMessage.content,
              parent_id: queuedMessage.parentId
            }
          };

          if (this.socket?.readyState === WebSocket.OPEN) {
            messageQueue.markMessageAttempted(channelId, queuedMessage.id);
            this.socket.send(JSON.stringify(message));
            // Don't wait for acknowledgment, let the message handler deal with it
          } else {
            break;
          }
        } catch (error) {
          console.error('[DEBUG] Error sending queued message:', error);
          break;
        }
      }
    } finally {
      this.processingQueue = false;
    }
  }

  private handleClose = (event: CloseEvent) => {
    console.error(`[DEBUG] WebSocket closed with code ${event.code}`);
    this.handleReconnect();
  };

  public async sendMessage(channelId: string, content: string, parentId?: string): Promise<void> {
    // Always queue the message first
    const messageId = messageQueue.addMessage(channelId, content, parentId ? 'reply' : 'message', parentId);

    // Create the message object
    const message = {
      type: parentId ? 'reply' : 'message',
      data: {
        channel_id: parseInt(channelId),
        content: content,
        parent_id: parentId,
        message: {
          id: messageId,
          content: content,
          channel_id: channelId,
          sender_id: store.getState().auth.user?.id,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
          parent_id: parentId
        }
      }
    };

    // Broadcast the message immediately to all handlers
    this.messageHandlers.forEach(handler => {
      try {
        handler({
          type: 'new_message',
          data: message.data
        });
      } catch (error) {
        console.error('[DEBUG] Error in message handler:', error);
      }
    });

    // Connect if not connected
    if (!this.isConnectedToChannel(channelId)) {
      try {
        await this.connect(channelId);
      } catch (error) {
        console.error('[DEBUG] Failed to connect:', error);
        return; // Message is already queued and displayed, will be sent after reconnection
      }
    }

    // Send the message if connected
    if (this.socket?.readyState === WebSocket.OPEN) {
      try {
        messageQueue.markMessageAttempted(channelId, messageId);
        this.socket.send(JSON.stringify(message));
      } catch (error) {
        console.error('[DEBUG] Error sending message:', error);
      }
    }
  }

  public async joinChannel(channelId: string): Promise<void> {
    if (!this.socket || this.socket.readyState !== WebSocket.OPEN) {
      throw new Error('Cannot join channel: WebSocket is not connected');
    }

    if (this.isConnectedToChannel(channelId)) {
      console.log('[DEBUG] Already joined channel:', channelId);
      return;
    }

    return new Promise((resolve, reject) => {
      const joinMessage = {
        type: 'join',
        data: {
          channel_id: parseInt(channelId)
        }
      };

      const joinTimeout = setTimeout(() => {
        this.socket?.removeEventListener('message', handleJoinResponse);
        reject(new Error('Channel join timeout'));
      }, 10000);

      const handleJoinResponse = (event: MessageEvent) => {
        try {
          const data = JSON.parse(event.data);
          console.log('[DEBUG] Received join response:', data);

          if (data.type === 'joined' && data.data?.channel_id?.toString() === channelId) {
            clearTimeout(joinTimeout);
            this.socket?.removeEventListener('message', handleJoinResponse);
            this.joinedChannels.add(channelId);
            console.log('[DEBUG] Successfully joined channel:', channelId);
            resolve();
          } else if (data.type === 'error') {
            clearTimeout(joinTimeout);
            this.socket?.removeEventListener('message', handleJoinResponse);
            console.error('[DEBUG] Error joining channel:', data.message);
            reject(new Error(data.message));
          }
        } catch (error) {
          console.error('[DEBUG] Error handling join response:', error);
        }
      };

      if (this.socket) {
        this.socket.addEventListener('message', handleJoinResponse);
        console.log('[DEBUG] Sending join message:', joinMessage);
        this.socket.send(JSON.stringify(joinMessage));
      } else {
        clearTimeout(joinTimeout);
        reject(new Error('WebSocket not available'));
      }
    });
  }

  private handleError(error: Error): void {
    console.error('WebSocket error:', error);
    this.errorHandlers.forEach(handler => handler(error));
  }

  public isConnected(): boolean {
    return this.socket !== null && this.socket.readyState === WebSocket.OPEN;
  }

  private isConnectedToChannel(channelId: string): boolean {
    return this.isConnected() && this.joinedChannels.has(channelId) && this.currentChannelId === channelId;
  }

  public onMessage(handler: (message: WebSocketMessage) => void): () => void {
    this.messageHandlers.push(handler);
    return () => {
      this.messageHandlers = this.messageHandlers.filter(h => h !== handler);
    };
  }

  public onError(handler: (error: Error) => void): () => void {
    this.errorHandlers.push(handler);
    return () => {
      this.errorHandlers = this.errorHandlers.filter(h => h !== handler);
    };
  }

  public disconnect(): void {
    console.log('[DEBUG] Disconnecting WebSocket...');
    this.isReconnecting = false;
    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }
    if (this.heartbeatInterval) {
      clearInterval(this.heartbeatInterval);
      this.heartbeatInterval = null;
    }
    if (this.socket && this.socket.readyState === WebSocket.OPEN) {
      this.socket.close(1000);
    }
    this.socket = null;
    this.joinedChannels.clear();
    this.messageHandlers = [];
    this.errorHandlers = [];
    this.reconnectAttempts = 0;
    this.connectionPromise = null;
    this.joinPromise = null;
    this.currentChannelId = null;
    console.log('[DEBUG] WebSocket disconnected and state cleared');
  }

  private handleWebSocketMessage = (event: MessageEvent) => {
    try {
      const message = JSON.parse(event.data) as WebSocketMessage;

      // Update heartbeat timestamp for any message
      this.lastHeartbeatResponse = Date.now();

      if (message.type === 'pong') {
        return;
      }

      // Handle message acknowledgments and update message ID
      if (message.type === 'message_sent') {
        const channelId = message.data?.channel_id?.toString();
        const messageId = message.data?.message?.id?.toString();
        const tempMessageId = message.data?.message?.content;

        if (channelId && messageId) {
          messageQueue.markMessageSent(channelId, messageId);

          // Update message ID in store if needed
          if (tempMessageId !== messageId) {
            store.dispatch(updateMessage({
              channelId,
              id: tempMessageId,
              message: {
                ...message.data.message,
                id: messageId
              }
            }));
          }
        }
      } else if (message.type === 'error' && message.data?.error) {
        console.error('[DEBUG] Message send error:', message.data.error);
        this.handleError(new Error(message.data.error));
      } else {
        // Broadcast other messages to all handlers immediately
        this.messageHandlers.forEach(handler => {
          try {
            handler(message);
          } catch (error) {
            console.error('[DEBUG] Error in message handler:', error);
          }
        });
      }
    } catch (error) {
      console.error('[DEBUG] Error handling WebSocket message:', error);
    }
  };

  public offMessage(handler: (message: WebSocketMessage) => void): void {
    this.messageHandlers = this.messageHandlers.filter(h => h !== handler);
  }

  public offError(handler: (error: Error) => void): void {
    this.errorHandlers = this.errorHandlers.filter(h => h !== handler);
  }

  public getChatSocketState(): number {
    return this.socket?.readyState || WebSocket.CLOSED;
  }
}

const wsService = new WebSocketService();
export default wsService;
