import React, { useEffect, useRef, forwardRef } from 'react';
import styled, { keyframes } from 'styled-components';
import { useSelector } from 'react-redux';
import { RootState, StoreMessage } from '../../../types';
import ChatMessage from '../../common/ChatMessage';

const MessageListContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const highlightAnimation = keyframes`
  0% {
    background-color: ${props => props.theme.colors.primary}40;
  }
  50% {
    background-color: ${props => props.theme.colors.primary}20;
  }
  100% {
    background-color: transparent;
  }
`;

const MessageWrapper = styled.div<{ isSelected?: boolean }>`
  padding: 4px;
  border-radius: 4px;
  transition: background-color 0.3s ease;

  &.highlight {
    animation: ${highlightAnimation} 2s ease;
  }

  ${props => props.isSelected && `
    background-color: ${props.theme.colors.border}20;
    border: 1px solid ${props.theme.colors.border};
  `}
`;

interface MessageListProps {
  selectedMessageId?: string | null;
}

const MessageList = forwardRef<HTMLDivElement, MessageListProps>(
  ({ selectedMessageId }, ref) => {
    const { messages, activeChannelId, currentUser, users } = useSelector((state: RootState) => ({
      messages: state.messages.messagesByChannel?.[state.chat.activeChannelId || ''] || [],
      activeChannelId: state.chat.activeChannelId,
      currentUser: state.auth.user,
      users: state.chat.users,
    }));

    const scrollRef = useRef<HTMLDivElement>(null);
    const actualRef = (ref as React.RefObject<HTMLDivElement>) || scrollRef;

    // Auto-scroll to bottom when new messages arrive
    useEffect(() => {
      if (actualRef.current) {
        actualRef.current.scrollTop = actualRef.current.scrollHeight;
      }
    }, [messages]);

    const handleDeleteMessage = (messageId: string) => {
      // TODO: Implement delete functionality
      console.log('Delete message:', messageId);
    };

    const handleToggleReplies = (messageId: string) => {
      // TODO: Implement toggle replies functionality
      console.log('Toggle replies:', messageId);
    };

    const handleReply = (messageId: string) => {
      // TODO: Implement reply functionality
      console.log('Reply to message:', messageId);
    };

    return (
      <MessageListContainer ref={actualRef}>
        {messages.map((message: StoreMessage) => (
          <MessageWrapper
            key={message.id}
            id={`message-${message.id}`}
            isSelected={message.id === selectedMessageId}
          >
            <ChatMessage 
              content={message.content}
              sender={users[message.userId]?.username || message.userId}
              timestamp={message.createdAt}
              userId={message.userId}
              currentUserId={currentUser?.id}
              onDelete={() => handleDeleteMessage(message.id)}
              replyCount={message.replyCount}
              isExpanded={message.isExpanded || false}
              onToggleReplies={() => handleToggleReplies(message.id)}
              onReply={() => handleReply(message.id)}
            />
          </MessageWrapper>
        ))}
      </MessageListContainer>
    );
  }
);

MessageList.displayName = 'MessageList';

export default MessageList;
