import React from 'react';
import styled from 'styled-components';
import MessageOptions from '../../chat/MessageOptions';

export interface ChatMessageProps {
  content: string;
  sender: string;
  timestamp: string;
  userId: string;
  currentUserId?: string;
  onDelete: () => void;
  replyCount: number;
  isExpanded: boolean;
  onToggleReplies: () => void;
  onReply: () => void;
  isReply?: boolean;
}

const MessageContainer = styled.div<{ isReply?: boolean }>`
  font-family: 'VT323', monospace;
  padding: 4px 0;
  color: ${props => props.theme.colors.text};
  position: relative;
  display: flex;
  flex-direction: column;

  &:hover {
    background-color: ${props => props.theme.colors.hover};
  }
`;

const MessageContent = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 8px;
  white-space: pre-wrap;
  word-wrap: break-word;
  padding: 2px 4px;
`;

const Timestamp = styled.span`
  color: ${props => props.theme.colors.textSecondary};
  font-family: inherit;
`;

const Sender = styled.span`
  color: ${props => props.theme.colors.primary};
  font-weight: bold;
  font-family: inherit;
`;

const ReplyCount = styled.button`
  background: none;
  border: none;
  color: ${props => props.theme.colors.textSecondary};
  cursor: pointer;
  font-family: inherit;
  padding: 0 4px;
  font-size: inherit;
  display: flex;
  align-items: center;
  gap: 4px;

  &:hover {
    color: ${props => props.theme.colors.primary};
  }
`;

const ReplyIndicator = styled.span`
  color: ${props => props.theme.colors.textSecondary};
  margin-right: 4px;
`;

const MessageText = styled.span`
  color: ${props => props.theme.colors.text};
  font-family: inherit;
`;

const ChatMessage: React.FC<ChatMessageProps> = ({
  content,
  sender,
  timestamp,
  userId,
  currentUserId,
  onDelete,
  replyCount,
  isExpanded,
  onToggleReplies,
  onReply,
  isReply = false,
}) => {
  const formattedTime = new Date(timestamp).toLocaleTimeString([], { 
    hour: '2-digit', 
    minute: '2-digit',
    second: '2-digit',
    hour12: false 
  });

  return (
    <MessageContainer isReply={isReply}>
      <MessageContent>
        <Timestamp>[{formattedTime}]</Timestamp>
        <Sender>&lt;{sender}&gt;</Sender>
        {isReply && <ReplyIndicator>└─</ReplyIndicator>}
        <MessageText>{content}</MessageText>
        {!isReply && replyCount > 0 && (
          <ReplyCount onClick={onToggleReplies}>
            [{isExpanded ? '-' : '+'} {replyCount} {replyCount === 1 ? 'reply' : 'replies'}]
          </ReplyCount>
        )}
        {userId === currentUserId && (
          <MessageOptions onDelete={onDelete} onReply={onReply} />
        )}
      </MessageContent>
    </MessageContainer>
  );
};

export default ChatMessage;
