import React, { useEffect, useRef, forwardRef, useState } from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { RootState, StoreMessage, User } from '../../../types';
import Message, { ChatMessageProps } from '../Message';
import MessageReplies from '../MessageReplies';

interface MessageListProps {
  messages: StoreMessage[];
  selectedMessageId?: string | null;
  initialScrollComplete?: boolean;
}

const MessageListContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 16px;
  display: flex;
  flex-direction: column;
  height: 100%;
`;

const MessagesWrapper = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-top: auto;
`;

const LoadingIndicator = styled.div`
  text-align: center;
  padding: 8px;
  color: ${props => props.theme.colors.textLight};
  font-family: 'Courier New', monospace;
`;

const MessageWrapper = styled.div<{ $isSelected?: boolean }>`
  transition: all 0.3s ease;
  padding: 4px;
  border-radius: 4px;
  background-color: ${props => props.$isSelected ? '#3a3a3a' : 'transparent'};
  border-left: ${props => props.$isSelected ? '2px solid #666' : '2px solid transparent'};

  &.highlight {
    animation: flash 1s;
  }

  @keyframes flash {
    0% { background-color: #4a4a4a; }
    100% { background-color: ${props => props.$isSelected ? '#3a3a3a' : 'transparent'}; }
  }
`;

const MessageList = forwardRef<HTMLDivElement, MessageListProps>((props, ref) => {
  const { messages, selectedMessageId, initialScrollComplete: propInitialScrollComplete } = props;
  const containerRef = useRef<HTMLDivElement>(null);
  const highlightTimeoutRef = useRef<NodeJS.Timeout>();
  const scrollTimeoutRef = useRef<NodeJS.Timeout>();
  const [shouldScrollToMessage, setShouldScrollToMessage] = useState(false);
  const [initialScrollComplete, setInitialScrollComplete] = useState(!!propInitialScrollComplete);
  const prevMessagesRef = useRef(messages);
  const isScrolledToBottom = useRef(true);

  const { currentUser, users } = useSelector((state: RootState) => ({
    currentUser: state.auth.user,
    users: state.chat.users as { [key: string]: User }
  }));

  // Track scroll position
  const handleScroll = () => {
    if (!containerRef.current) return;

    const { scrollTop, scrollHeight, clientHeight } = containerRef.current;
    const scrolledToBottom = Math.abs(scrollHeight - clientHeight - scrollTop) < 10;
    isScrolledToBottom.current = scrolledToBottom;
  };

  // Handle scroll behavior
  useEffect(() => {
    if (!containerRef.current) return;

    // Clear any existing scroll timeout
    if (scrollTimeoutRef.current) {
      clearTimeout(scrollTimeoutRef.current);
    }

    // Case 1: Scroll to selected message
    if (selectedMessageId && !initialScrollComplete) {
      const messageElement = containerRef.current.querySelector(
        `[data-message-id="${selectedMessageId}"]`
      ) as HTMLElement;

      if (messageElement) {
        messageElement.scrollIntoView({
          behavior: 'smooth',
          block: 'center'
        });

        // Add highlight effect
        messageElement.classList.add('highlight');

        if (highlightTimeoutRef.current) {
          clearTimeout(highlightTimeoutRef.current);
        }

        highlightTimeoutRef.current = setTimeout(() => {
          messageElement.classList.remove('highlight');
        }, 1000);

        setInitialScrollComplete(true);
      }
    } 
    // Case 2: Initial load or new messages when scrolled to bottom
    else if ((!initialScrollComplete || isScrolledToBottom.current) && messages.length > 0) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
      setInitialScrollComplete(true);
    }
  }, [selectedMessageId, messages, initialScrollComplete]);

  // Reset scroll state when channel changes (detected by first message changing)
  useEffect(() => {
    const channelChanged = messages[0]?.channelId !== prevMessagesRef.current[0]?.channelId;
    if (channelChanged) {
      setInitialScrollComplete(false);
      isScrolledToBottom.current = true;
    }
    prevMessagesRef.current = messages;
  }, [messages]);

  // Cleanup effect
  useEffect(() => {
    return () => {
      if (highlightTimeoutRef.current) {
        clearTimeout(highlightTimeoutRef.current);
      }
      if (scrollTimeoutRef.current) {
        clearTimeout(scrollTimeoutRef.current);
      }
    };
  }, []);

  const handleDeleteMessage = (messageId: string) => {
    console.log('Delete message:', messageId);
  };

  const handleToggleReplies = (messageId: string) => {
    console.log('Toggle replies:', messageId);
  };

  const handleReply = (messageId: string) => {
    console.log('Reply to message:', messageId);
  };

  return (
    <MessageListContainer 
      ref={containerRef}
      onScroll={handleScroll}
    >
      <MessagesWrapper>
        {messages.map(message => (
          <MessageWrapper
            key={message.id}
            $isSelected={message.id === selectedMessageId}
            data-message-id={message.id}
          >
            <Message
              content={message.content}
              sender={users[message.userId]?.username || message.userId}
              timestamp={message.createdAt}
              userId={message.userId}
              currentUserId={currentUser?.id}
              onDelete={() => handleDeleteMessage(message.id)}
              replyCount={message.replyCount || 0}
              isExpanded={message.isExpanded || false}
              onToggleReplies={() => handleToggleReplies(message.id)}
              onReply={() => handleReply(message.id)}
              isReply={false}
            />
            {message.isExpanded && message.replies && (
              <MessageReplies
                parentId={message.id}
                replies={message.replies}
                currentUserId={currentUser?.id}
                isExpanded={message.isExpanded}
                onToggleReplies={() => handleToggleReplies(message.id)}
                onDelete={handleDeleteMessage}
              />
            )}
          </MessageWrapper>
        ))}
      </MessagesWrapper>
    </MessageListContainer>
  );
});

MessageList.displayName = 'MessageList';

export default MessageList;
