import React from 'react';
import styled from 'styled-components';
import MessageOptions from '../../chat/MessageOptions';

export interface ChatMessageProps {
  content: string;
  sender: string;
  timestamp: string;
  userId: string;
  currentUserId?: string;
  onDelete: () => void;
  replyCount: number;
  isExpanded: boolean;
  onToggleReplies: () => void;
  onReply: () => void;
  isReply?: boolean;
}

const MessageContainer = styled.div<{ isReply?: boolean }>`
  display: flex;
  flex-direction: column;
  padding: 0.5rem 1rem;
  margin: ${({ isReply }) => isReply ? '0' : '0.5rem 0'};
  background-color: ${({ isReply }) => isReply ? '#2a2a2a' : '#333'};
  border-radius: 4px;
  position: relative;
`;

const MessageHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
`;

const Sender = styled.span`
  font-weight: bold;
  color: #fff;
`;

const Timestamp = styled.span`
  color: #999;
  font-size: 0.8rem;
`;

const MessageContent = styled.div`
  color: #fff;
  word-break: break-word;
  white-space: pre-wrap;
`;

const MessageFooter = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 0.5rem;
`;

const ReplyCount = styled.button`
  background: none;
  border: none;
  color: #999;
  cursor: pointer;
  padding: 0.25rem 0.5rem;
  font-size: 0.8rem;
  display: flex;
  align-items: center;
  gap: 0.25rem;

  &:hover {
    color: #fff;
  }
`;

const ChatMessage: React.FC<ChatMessageProps> = ({
  content,
  sender,
  timestamp,
  userId,
  currentUserId,
  onDelete,
  replyCount,
  isExpanded,
  onToggleReplies,
  onReply,
  isReply = false,
}) => {
  return (
    <MessageContainer isReply={isReply}>
      <MessageHeader>
        <Sender>{sender}</Sender>
        <Timestamp>{new Date(timestamp).toLocaleString()}</Timestamp>
      </MessageHeader>
      <MessageContent>{content}</MessageContent>
      <MessageFooter>
        {!isReply && replyCount > 0 && (
          <ReplyCount onClick={onToggleReplies}>
            {replyCount} {replyCount === 1 ? 'reply' : 'replies'} {isExpanded ? '▼' : '▶'}
          </ReplyCount>
        )}
        {userId === currentUserId && (
          <MessageOptions onDelete={onDelete} onReply={onReply} />
        )}
      </MessageFooter>
    </MessageContainer>
  );
};

export default ChatMessage;
