import React, { useState, KeyboardEvent } from 'react';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';
import wsService from '../../../services/websocket';

interface MessageInputProps {
  channelId: number | null;
}

const InputContainer = styled.div`
  padding: 16px 32px;
  background: ${props => props.theme.colors.background};
  display: flex;
  justify-content: center;
`;

const StyledInput = styled.input<{ disabled?: boolean }>`
  width: 100%;
  max-width: 800px;
  padding: 12px 16px;
  background: ${props => props.disabled ? props.theme.colors.backgroundSecondary : props.theme.colors.background};
  color: ${props => props.disabled ? props.theme.colors.textSecondary : props.theme.colors.text};
  border: 2px solid ${props => props.disabled ? props.theme.colors.borderSecondary : props.theme.colors.border};
  border-radius: 4px;
  font-family: 'Courier New', monospace;
  font-size: 14px;
  cursor: ${props => props.disabled ? 'not-allowed' : 'text'};

  &:focus {
    outline: none;
    border-color: ${props => props.disabled ? props.theme.colors.borderSecondary : props.theme.colors.primary};
    box-shadow: ${props => props.disabled ? 'none' : `0 0 0 1px ${props.theme.colors.primary}`};
  }

  &::placeholder {
    color: ${props => props.theme.colors.textSecondary};
  }
`;

const MessageInput: React.FC<MessageInputProps> = ({ channelId }) => {
  const [message, setMessage] = useState('');
  const dispatch = useDispatch();

  const handleKeyPress = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && message.trim() && channelId) {
      const wsState = wsService.getChatSocketState();
      console.log('WebSocket state:', {
        state: wsState,
        isConnected: wsState === WebSocket.OPEN,
        channelId,
        content: message.trim()
      });

      if (wsState === WebSocket.OPEN) {
        wsService.sendMessage(channelId, message.trim());
        setMessage('');
      } else {
        console.error('WebSocket is not connected. State:', wsState);
      }
    }
  };

  return (
    <InputContainer>
      <StyledInput
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder={channelId ? "> Type your message and press Enter..." : "Select a channel to start messaging"}
        aria-label="Message input"
        disabled={!channelId}
      />
    </InputContainer>
  );
};

export default MessageInput;
