import React from 'react';
import styled, { keyframes } from 'styled-components';

interface InputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  label?: string;
  error?: string;
  fullWidth?: boolean;
}

const blink = keyframes`
  0%, 100% { opacity: 1; }
  50% { opacity: 0; }
`;

const InputWrapper = styled.div<{ fullWidth?: boolean }>`
  display: flex;
  flex-direction: column;
  width: ${props => props.fullWidth ? '100%' : 'auto'};
  margin-bottom: 16px;
`;

const Label = styled.label`
  font-family: 'VT323', monospace;
  color: ${props => props.theme.colors.text};
  margin-bottom: 4px;
  text-transform: uppercase;
`;

const InputContainer = styled.div`
  position: relative;
  display: flex;
  align-items: center;
`;

const StyledInput = styled.input<{ hasError?: boolean }>`
  font-family: 'VT323', monospace;
  font-size: 1rem;
  padding: 8px;
  width: 100%;
  background-color: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  border: 2px solid ${props => props.hasError ? props.theme.colors.error : props.theme.colors.border};
  outline: none;
  caret-color: transparent;

  &:focus {
    border-color: ${props => props.hasError ? props.theme.colors.error : props.theme.colors.primary};
  }

  &::placeholder {
    color: ${props => props.theme.colors.text}80;
  }
`;

const Cursor = styled.span<{ isVisible: boolean }>`
  position: absolute;
  display: ${props => props.isVisible ? 'block' : 'none'};
  right: ${props => props.isVisible ? '8px' : 'auto'};
  color: ${props => props.theme.colors.text};
  animation: ${blink} 1s step-end infinite;
  pointer-events: none;
  user-select: none;
  font-family: monospace;
`;

const ErrorText = styled.span`
  font-family: 'VT323', monospace;
  color: ${props => props.theme.colors.error};
  margin-top: 4px;
  font-size: 0.875rem;
`;

const Input: React.FC<InputProps> = ({
  label,
  error,
  fullWidth = false,
  ...props
}) => {
  const [isFocused, setIsFocused] = React.useState(false);
  const [cursorPosition, setCursorPosition] = React.useState(0);
  const inputRef = React.useRef<HTMLInputElement>(null);

  const handleFocus = (e: React.FocusEvent<HTMLInputElement>) => {
    setIsFocused(true);
    if (props.onFocus) props.onFocus(e);
  };

  const handleBlur = (e: React.FocusEvent<HTMLInputElement>) => {
    setIsFocused(false);
    if (props.onBlur) props.onBlur(e);
  };

  const handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    if (inputRef.current) {
      setCursorPosition(inputRef.current.selectionStart || 0);
    }
  };

  const calculateCursorPosition = () => {
    if (!inputRef.current) return { left: '8px' };
    const text = inputRef.current.value.substring(0, cursorPosition);
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    if (context) {
      context.font = '16px VT323';
      const textWidth = context.measureText(text).width;
      return { left: `${textWidth + 8}px` };
    }
    return { left: '8px' };
  };

  return (
    <InputWrapper fullWidth={fullWidth}>
      {label && <Label>{label}</Label>}
      <InputContainer>
        <StyledInput
          ref={inputRef}
          hasError={!!error}
          onFocus={handleFocus}
          onBlur={handleBlur}
          onInput={handleInput}
          {...props}
        />
        <Cursor 
          isVisible={isFocused}
          style={calculateCursorPosition()}
        >
          â–ˆ
        </Cursor>
      </InputContainer>
      {error && <ErrorText>{error}</ErrorText>}
    </InputWrapper>
  );
};

export default Input;
