import { Message, User, StoreMessage } from '../../types';
import { store } from '../../store';
import { addMessage, updateMessage } from '../../store/messages/messagesSlice';
import { updateUserStatus } from '../../store/chat/chatSlice';

// Get WebSocket URL from environment variable or fallback to localhost
const WS_BASE_URL = process.env.REACT_APP_WS_URL || 'ws://localhost:8000/ws';

// Helper function to get the correct WebSocket URL based on the current protocol
const getWebSocketUrl = () => {
  // If we're on HTTPS, use WSS
  if (window.location.protocol === 'https:' && WS_BASE_URL.startsWith('ws:')) {
    return WS_BASE_URL.replace('ws:', 'wss:');
  }
  return WS_BASE_URL;
};

interface WebSocketMessage {
  type: 'NEW_MESSAGE' | 'UPDATE_MESSAGE' | 'USER_STATUS' | 'PING' | 'PONG' | 'JOIN_CHANNEL' | 'LEAVE_CHANNEL';
  channelId?: string;
  message?: StoreMessage;
  id?: string;
  updates?: Partial<StoreMessage>;
  userId?: string;
  status?: 'online' | 'offline' | 'away' | 'busy';
}

export class WebSocketService {
  private static instance: WebSocketService | null = null;
  private chatSocket: WebSocket | null = null;
  private presenceSocket: WebSocket | null = null;
  private pingInterval: NodeJS.Timeout | null = null;
  private reconnectAttempts = 0;
  private maxReconnectAttempts = 5;
  private reconnectTimeout: NodeJS.Timeout | null = null;
  private channels: Set<string> = new Set();

  constructor() {
    if (WebSocketService.instance) {
      return WebSocketService.instance;
    }
    WebSocketService.instance = this;
  }

  private getWebSocketUrl(endpoint: string): string {
    const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('No token found');
    }
    return `${protocol}//localhost:8000/ws?token=${token}`;
  }

  public connect() {
    this.connectToChat();
    this.connectToPresence();
    this.startPingInterval();
  }

  private connectToChat() {
    const url = this.getWebSocketUrl('chat');
    console.log('Connecting to chat WebSocket at:', url);
    this.chatSocket = new WebSocket(url);
    
    this.chatSocket.onopen = () => {
      console.log('Chat WebSocket connected');
      this.reconnectAttempts = 0;
    };

    this.chatSocket.onclose = () => {
      console.log('Chat WebSocket disconnected');
      this.handleReconnect();
    };

    this.chatSocket.onerror = (error) => {
      console.error('Chat WebSocket error:', error);
    };

    this.chatSocket.onmessage = (event) => {
      const message = JSON.parse(event.data);
      this.handleMessage(message);
    };
  }

  private connectToPresence() {
    const url = this.getWebSocketUrl('presence');
    console.log('Connecting to presence WebSocket at:', url);
    this.presenceSocket = new WebSocket(url);
    
    this.presenceSocket.onopen = () => {
      console.log('Presence WebSocket connected');
      this.reconnectAttempts = 0;
    };

    this.presenceSocket.onclose = () => {
      console.log('Presence WebSocket disconnected');
      this.handleReconnect();
    };

    this.presenceSocket.onerror = (error) => {
      console.error('Presence WebSocket error:', error);
    };

    this.presenceSocket.onmessage = (event) => {
      const message = JSON.parse(event.data);
      this.handlePresenceMessage(message);
    };
  }

  private handleReconnect() {
    if (this.reconnectAttempts < this.maxReconnectAttempts) {
      this.reconnectAttempts++;
      console.log(`Attempting to reconnect (${this.reconnectAttempts}/${this.maxReconnectAttempts})`);
      this.reconnectTimeout = setTimeout(() => {
        this.connect();
      }, 5000);
    }
  }

  private startPingInterval() {
    this.pingInterval = setInterval(() => {
      if (this.chatSocket?.readyState === WebSocket.OPEN) {
        this.chatSocket.send(JSON.stringify({ type: 'PING' }));
      }
      if (this.presenceSocket?.readyState === WebSocket.OPEN) {
        this.presenceSocket.send(JSON.stringify({ type: 'PING' }));
      }
    }, 30000);
  }

  private stopPingInterval() {
    if (this.pingInterval) {
      clearInterval(this.pingInterval);
      this.pingInterval = null;
    }
  }

  public disconnect() {
    if (this.chatSocket) {
      this.chatSocket.close();
      this.chatSocket = null;
    }
    if (this.presenceSocket) {
      this.presenceSocket.close();
      this.presenceSocket = null;
    }
    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }
    this.stopPingInterval();
  }

  private handleMessage(message: any) {
    // Handle different message types
    switch (message.type) {
      case 'NEW_MESSAGE':
        // Handle new message
        break;
      case 'UPDATE_MESSAGE':
        // Handle message update
        break;
      case 'DELETE_MESSAGE':
        // Handle message deletion
        break;
      case 'PONG':
        // Handle ping response
        break;
      default:
        console.warn('Unknown message type:', message.type);
    }
  }

  private handlePresenceMessage(message: any) {
    // Handle different presence message types
    switch (message.type) {
      case 'USER_STATUS':
        // Handle user status update
        break;
      case 'PONG':
        // Handle ping response
        break;
      default:
        console.warn('Unknown presence message type:', message.type);
    }
  }

  public joinChannel(channelId: string) {
    if (this.chatSocket?.readyState === WebSocket.OPEN) {
      this.chatSocket.send(JSON.stringify({
        type: 'JOIN_CHANNEL',
        channelId
      }));
      this.channels.add(channelId);
    }
  }

  public leaveChannel(channelId: string) {
    if (this.chatSocket?.readyState === WebSocket.OPEN) {
      this.chatSocket.send(JSON.stringify({
        type: 'LEAVE_CHANNEL',
        channelId
      }));
      this.channels.delete(channelId);
    }
  }
}

export default new WebSocketService();
