import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../../../store/auth/authSlice';
import {
  setActiveChannel,
  setChannels,
  addMessage,
  setMessages,
  setUsers,
  updateUserStatus,
  setLoading,
  setError
} from '../../../store/chat/chatSlice';
import Button from '../../common/Button';
import ChatMessage from '../../common/ChatMessage';
import UserListItem from '../../common/UserListItem';
import ChannelListItem from '../../common/ChannelListItem';
import CreateChannelModal from '../../chat/CreateChannelModal';
import MessageInput from '../../chat/MessageInput';
import wsService from '../../../services/websocket';
import { getChannels, getChannelMessages, getChannelUsers } from '../../../services/api/chat';
import { Message, WebSocketMessage, User, RootState } from '../../../types';

const MainContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  font-family: 'VT323', monospace;
`;

const Sidebar = styled.div`
  width: 200px;
  border-right: 2px solid ${props => props.theme.colors.border};
  display: flex;
  flex-direction: column;
`;

const ChannelList = styled.div`
  flex: 1;
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const UserList = styled.div`
  height: 200px;
  padding: 16px;
  border-top: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const ChatArea = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
`;

const ChatHeader = styled.div`
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  display: flex;
  justify-content: space-between;
  align-items: center;

  h1 {
    margin: 0;
    text-transform: uppercase;
  }
`;

const ChatMessages = styled.div`
  flex: 1;
  padding: 16px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 2px;
`;

const ChatInput = styled.div`
  border-top: 2px solid ${props => props.theme.colors.border};
`;

const LogoutButton = styled.button`
  background: none;
  border: 2px solid ${props => props.theme.colors.error};
  color: ${props => props.theme.colors.error};
  padding: 4px 8px;
  font-family: 'VT323', monospace;
  cursor: pointer;
  text-transform: uppercase;

  &:hover {
    background: ${props => props.theme.colors.error};
    color: ${props => props.theme.colors.background};
  }
`;

const ChannelHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;

  h2 {
    margin: 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const CreateChannelButton = styled(Button)`
  padding: 2px 8px;
  font-size: 0.875rem;
`;

const NoChannelMessage = styled.div`
  text-align: center;
  padding: 20px;
  color: ${props => props.theme.colors.secondary};
  font-family: 'Courier New', monospace;
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 8px;
  color: ${props => props.theme.colors.textSecondary};
  font-family: 'Courier New', monospace;
  font-style: italic;
`;

interface RawMessage {
  id: number;
  content: string;
  sender_id: number;
  channel_id: number;
  created_at: string;
  is_system?: boolean;
}

interface ApiMessage {
  id: number;
  content: string;
  sender_id: number;
  channel_id: number;
  created_at: string;
  is_system?: boolean;
}

const transformMessage = (msg: ApiMessage, users: { [key: string]: User }): Message => {
  return {
    id: msg.id,
    content: msg.content,
    sender: users[msg.sender_id.toString()] || {
      id: msg.sender_id,
      username: 'Unknown User',
      email: '',
      full_name: 'Unknown User',
      status: 'offline',
      last_seen: msg.created_at
    },
    channel_id: msg.channel_id,
    created_at: msg.created_at,
    updated_at: msg.created_at
  };
};

const MainLayout: React.FC = () => {
  const dispatch = useDispatch();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);

  const activeChannelId = useSelector((state: RootState) => state.chat.activeChannelId);
  const channels = useSelector((state: RootState) => state.chat.channels);
  const users = useSelector((state: RootState) => state.chat.users);
  const messages = useSelector((state: RootState) => {
    const channelMessages = state.chat.messages[activeChannelId?.toString() || ''] || [];
    return channelMessages;
  });

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    const messageHandler = (data: WebSocketMessage) => {
      if (data.type === 'message' && data.id && data.content && data.sender_id && data.channel_id) {
        const rawMessage: RawMessage = {
          id: data.id,
          content: data.content,
          sender_id: data.sender_id,
          channel_id: data.channel_id,
          created_at: data.created_at || new Date().toISOString(),
          is_system: data.is_system
        };

        const transformedMessage = transformMessage(rawMessage, users);
        dispatch(addMessage({ 
          channelId: rawMessage.channel_id.toString(), 
          message: transformedMessage 
        }));

        if (rawMessage.channel_id === activeChannelId) {
          scrollToBottom();
        }
      }
    };

    wsService.on('message', messageHandler);
    return () => {
      wsService.off('message', messageHandler);
    };
  }, [dispatch, users, activeChannelId]);

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        dispatch(setLoading(true));
        const channelsData = await getChannels();
        dispatch(setChannels(channelsData));

        if (channelsData.length > 0) {
          const firstChannelId = channelsData[0].id;
          dispatch(setActiveChannel(firstChannelId));

          const [messagesData, usersData] = await Promise.all([
            getChannelMessages(firstChannelId),
            getChannelUsers(firstChannelId)
          ]);

          const userMap = usersData.reduce((acc: { [key: string]: User }, user: User) => {
            acc[user.id.toString()] = user;
            return acc;
          }, {});

          dispatch(setUsers(userMap));
          dispatch(setMessages({ 
            channelId: firstChannelId.toString(), 
            messages: (messagesData as ApiMessage[]).map(msg => transformMessage(msg, userMap))
          }));
        }
      } catch (error) {
        dispatch(setError(error instanceof Error ? error.message : 'Failed to fetch initial data'));
      } finally {
        dispatch(setLoading(false));
      }
    };

    fetchInitialData();
  }, [dispatch]);

  const handleChannelClick = async (channelId: number) => {
    try {
      dispatch(setLoading(true));
      dispatch(setActiveChannel(channelId));

      const [messagesData, usersData] = await Promise.all([
        getChannelMessages(channelId),
        getChannelUsers(channelId)
      ]);

      const userMap = usersData.reduce((acc: { [key: string]: User }, user: User) => {
        acc[user.id.toString()] = user;
        return acc;
      }, {});

      dispatch(setUsers(userMap));
      dispatch(setMessages({ 
        channelId: channelId.toString(), 
        messages: (messagesData as ApiMessage[]).map(msg => transformMessage(msg, userMap))
      }));

      scrollToBottom();
    } catch (error) {
      dispatch(setError(error instanceof Error ? error.message : 'Failed to fetch channel data'));
    } finally {
      dispatch(setLoading(false));
    }
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  return (
    <MainContainer>
      <Sidebar>
        <ChannelList>
          <ChannelHeader>
            <h2>Channels</h2>
            <CreateChannelButton onClick={() => setIsCreateModalOpen(true)}>
              +
            </CreateChannelButton>
          </ChannelHeader>
          {channels.map(channel => (
            <ChannelListItem
              key={channel.id}
              channel={channel}
              isActive={channel.id === activeChannelId}
              onClick={() => handleChannelClick(channel.id)}
            />
          ))}
        </ChannelList>
        <UserList>
          <h2>Users</h2>
          {Object.values(users).map(user => (
            <UserListItem key={user.id} user={user} />
          ))}
        </UserList>
      </Sidebar>

      <ChatArea>
        <ChatHeader>
          <h1>Sermo</h1>
          <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
        </ChatHeader>

        <ChatMessages>
          {messages.map((msg: Message) => (
            <div key={msg.id} id={`message-${msg.id}`}>
              <ChatMessage
                content={msg.content}
                sender={msg.sender.username}
                timestamp={msg.created_at}
              />
            </div>
          ))}
          <div ref={messagesEndRef} />
        </ChatMessages>

        {activeChannelId && (
          <ChatInput>
            <MessageInput channelId={activeChannelId} />
          </ChatInput>
        )}
      </ChatArea>

      {isCreateModalOpen && (
        <CreateChannelModal onClose={() => setIsCreateModalOpen(false)} />
      )}
    </MainContainer>
  );
};

export default MainLayout;
