import React, { forwardRef, ForwardedRef, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import { 
  RootState, 
  StoreMessage, 
  User, 
  Message as ApiMessage, 
  WebSocketMessage, 
  WebSocketMessageWithMessage,
  WebSocketReplyMessage,
  WebSocketDeleteMessage
} from '../../../types';
import { transformMessage } from '../../../utils/messageTransform';
import Message from '../Message';
import { deleteMessage, toggleExpanded, prependMessages, addMessage, setMessages, setReplies, updateMessage } from '../../../store/messages/messagesSlice';
import { deleteMessage as deleteMessageApi, getChannelMessages, createReply, getReplies } from '../../../services/api/chat';
import { toast } from 'react-toastify';
import wsService from '../../../services/websocket';

const Container = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 16px;
`;

interface MessageListProps {
  messages: StoreMessage[];
  selectedMessageId?: string;
}

interface MessageProps {
  message: StoreMessage;
  isSelected: boolean;
}

// Type guard functions
const isMessageWithMessage = (message: WebSocketMessage): message is WebSocketMessageWithMessage => {
  return (message.type === 'message' || message.type === 'message_sent' || message.type === 'message_updated') &&
         'message' in message && message.message !== undefined;
};

const isReplyMessage = (message: WebSocketMessage): message is WebSocketReplyMessage => {
  return message.type === 'new_reply' && 'message' in message && 'parentId' in message;
};

const isDeleteMessage = (message: WebSocketMessage): message is WebSocketDeleteMessage => {
  return message.type === 'message_deleted';
};

const MessageList = forwardRef<HTMLDivElement, MessageListProps>(
  (props: MessageListProps, ref: ForwardedRef<HTMLDivElement>) => {
    const { messages, selectedMessageId } = props;
    const dispatch = useDispatch();
    const activeChannelId = useSelector((state: RootState) => state.chat.activeChannelId);

    // WebSocket message handler
    const handleWebSocketMessage = (message: WebSocketMessage) => {
      // Check if message belongs to current channel
      if (isMessageWithMessage(message)) {
        const channelId = message.message.channel_id?.toString();
        if (channelId && channelId !== activeChannelId) {
          console.log('Ignoring message from different channel:', channelId, 'current:', activeChannelId);
          return; // Ignore messages from other channels
        }
      }

      switch (message.type) {
        case 'message':
        case 'message_sent':
          if (isMessageWithMessage(message)) {
            const transformedMessage = transformMessage(message.message);
            console.log('Adding new message:', transformedMessage);

            dispatch(addMessage({
              channelId: transformedMessage.channelId,
              message: transformedMessage
            }));
          }
          break;

        case 'new_reply':
          if (isReplyMessage(message)) {
            console.log('Adding new reply:', message);
            const transformedReply = transformMessage(message.message);

            dispatch(setReplies({
              channelId: transformedReply.channelId,
              messageId: message.parentId,
              replies: [transformedReply]
            }));
          }
          break;

        case 'message_deleted':
          if (isDeleteMessage(message)) {
            dispatch(deleteMessage({
              channelId: message.channelId,
              messageId: message.messageId
            }));
          }
          break;

        case 'message_updated':
          if (isMessageWithMessage(message)) {
            const transformedUpdatedMessage = transformMessage(message.message);
            dispatch(updateMessage({
              channelId: transformedUpdatedMessage.channelId,
              id: transformedUpdatedMessage.id,
              message: transformedUpdatedMessage
            }));
          }
          break;

        default:
          console.warn('Unknown message type:', message.type);
          break;
      }
    };

    // Add WebSocket message handler
    useEffect(() => {
      if (!activeChannelId) return;

      const unsubscribe = wsService.onMessage(handleWebSocketMessage);
      return () => unsubscribe();
    }, [activeChannelId, messages, dispatch]);

    return (
      <Container ref={ref}>
        {messages.map((message) => (
          <Message
            key={message.id}
            message={message}
            isSelected={message.id === selectedMessageId}
          />
        ))}
      </Container>
    );
  }
);

MessageList.displayName = 'MessageList';

export default MessageList;
