import React, { useEffect, useState, useRef, useCallback, useMemo } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../../../store/auth/authSlice';
import {
  setActiveChannel,
  setChannels,
  setUsers,
  updateUserStatus,
  addChannel,
  removeChannel,
  updateChannelUnreadCount,
  setError
} from '../../../store/chat/chatSlice';
import {
  addMessage,
  setMessages,
  setReplies,
  deleteMessage,
  updateMessage,
  toggleExpanded
} from '../../../store/messages/messagesSlice';
import Button from '../../common/Button';
import UserListItem from '../../chat/UserListItem';
import ChannelListItem from '../../chat/ChannelListItem';
import CreateChannelModal from '../../chat/CreateChannelModal';
import MessageInput from '../../chat/MessageInput';
import MessageList from '../../chat/MessageList';
import SearchBar from '../../common/SearchBar';
import SearchResults from '../../common/SearchResults';
import wsService from '../../../services/websocket';
import { getChannels, getChannelUsers, getChannelMessages, joinChannel, getReplies } from '../../../services/api/chat';
import { searchAll } from '../../../services/api/search';
import { RootState, WebSocketMessage, StoreMessage, Channel, User, Message, WebSocketChannelMessage, WebSocketStatusMessage, SearchResult } from '../../../types';
import ChannelSettings from '../../chat/ChannelSettings';
import { AppDispatch } from '../../../store';
import { store } from '../../../store';
import { transformMessage } from '../../../utils/messageTransform';

const MainContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  font-family: 'VT323', monospace;
`;

const Sidebar = styled.div`
  width: 200px;
  border-right: 2px solid ${props => props.theme.colors.border};
  display: flex;
  flex-direction: column;
`;

const ChannelList = styled.div`
  flex: 1;
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const UserList = styled.div`
  height: 200px;
  padding: 16px;
  border-top: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const ChatArea = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
`;

const ChatHeader = styled.div`
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  display: flex;
  justify-content: space-between;
  align-items: center;

  h1 {
    margin: 0;
    text-transform: uppercase;
  }
`;

const ChatInput = styled.div`
  border-top: 2px solid ${props => props.theme.colors.border};
`;

const LogoutButton = styled.button`
  background: none;
  border: 2px solid ${props => props.theme.colors.error};
  color: ${props => props.theme.colors.error};
  padding: 4px 8px;
  font-family: 'VT323', monospace;
  cursor: pointer;
  text-transform: uppercase;

  &:hover {
    background: ${props => props.theme.colors.error};
    color: ${props => props.theme.colors.background};
  }
`;

const ChannelHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;

  h2 {
    margin: 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const CreateChannelButton = styled(Button)`
  padding: 2px 8px;
  font-size: 0.875rem;
`;

const ChannelGroup = styled.div`
  margin-bottom: 16px;

  &:last-child {
    margin-bottom: 0;
  }
`;

const GroupTitle = styled.h3`
  margin: 0 0 8px 0;
  padding: 4px 8px;
  font-size: 0.875rem;
  text-transform: uppercase;
  color: ${props => props.theme.colors.textLight};
  border-bottom: 1px solid ${props => props.theme.colors.border};
  font-family: 'Courier New', monospace;
`;

const ChannelActions = styled.div`
  display: flex;
  gap: 8px;
`;

const SettingsButton = styled(Button)`
  padding: 2px 8px;
  font-size: 0.875rem;
`;

const SearchContainer = styled.div`
  position: relative;
  margin-right: 16px;
`;

const MainLayout: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isConnecting, setIsConnecting] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult | null>(null);
  const [searchError, setSearchError] = useState<string | undefined>();
  const messageListRef = useRef<HTMLDivElement>(null);
  const [selectedMessageId, setSelectedMessageId] = useState<string | null>(null);
  const isChannelSwitching = useRef<boolean>(false);

  // Memoize selectors
  const { channels, activeChannelId, users, channelMessages } = useSelector((state: RootState) => {
    const currentChannelId = state.chat.activeChannelId as string | null;
    return {
      channels: state.chat.channels,
      activeChannelId: currentChannelId,
      users: state.chat.users as { [key: string]: User },
      channelMessages: currentChannelId ? state.messages.messagesByChannel[currentChannelId] || [] : []
    };
  });

  // Get active channel
  const activeChannel = useMemo<Channel | undefined>(() => {
    if (!activeChannelId) return undefined;
    return channels.find((channel: Channel) => channel.id === activeChannelId);
  }, [channels, activeChannelId]);

  // Handle channel switching
  const handleChannelSwitch = useCallback(async (targetChannelId: string): Promise<void> => {
    if (isChannelSwitching.current || targetChannelId === activeChannelId) return;

    try {
      isChannelSwitching.current = true;
      setIsConnecting(true);

      // 1. Cleanup old connection and state
      if (activeChannelId) {
        await wsService.disconnect();
        dispatch(setMessages({ 
          channelId: activeChannelId, 
          messages: [] 
        }));
      }

      // 2. Set new channel
      dispatch(setActiveChannel(targetChannelId));

      // 3. Fetch new channel data
      const [messages, channelUsers] = await Promise.all([
        getChannelMessages(targetChannelId),
        getChannelUsers(targetChannelId)
      ]);

      // 4. Update store with new data
      dispatch(setMessages({ 
        channelId: targetChannelId, 
        messages: messages.map((message: Message) => transformMessage(message))
      }));

      // Convert users array to object
      const usersObject = channelUsers.reduce<{ [key: string]: User }>((acc, user) => {
        acc[user.id] = user;
        return acc;
      }, {});
      dispatch(setUsers(usersObject));

      // 5. Establish new WebSocket connection
      await wsService.connect(targetChannelId);
      await wsService.joinChannel(targetChannelId);

    } catch (error) {
      console.error('Channel switch failed:', error);
      dispatch(setError('Failed to switch channels. Please try again.'));
    } finally {
      isChannelSwitching.current = false;
      setIsConnecting(false);
    }
  }, [activeChannelId, dispatch]);

  // Handle WebSocket connection errors
  const handleConnectionError = useCallback((error: Error) => {
    console.error('WebSocket connection error:', error);
    dispatch(setError('Lost connection to chat server. Attempting to reconnect...'));
  }, [dispatch]);

  // Monitor WebSocket connection status
  const handleConnectionStatus = useCallback(() => {
    const status = wsService.getChatSocketState();
    if (status === WebSocket.OPEN) {
      dispatch(setError(null));
    }
  }, [dispatch]);

  // Set up WebSocket error handling and status monitoring
  useEffect(() => {
    wsService.onError(handleConnectionError);

    // Check connection status periodically
    const statusInterval = setInterval(handleConnectionStatus, 5000);

    return () => {
      clearInterval(statusInterval);
    };
  }, [handleConnectionError, handleConnectionStatus]);

  // Handle channel selection from search results
  const handleSelectMessage = async (targetChannelId: string, messageId: string) => {
    try {
      if (targetChannelId !== activeChannelId) {
        await handleChannelSwitch(targetChannelId);
      }

      setSelectedMessageId(messageId);
      setSearchResults(null);

      // Scroll to the selected message
      const scrollToMessage = () => {
        const messageElement = document.getElementById(`message-${messageId}`);
        if (messageElement) {
          messageElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
          messageElement.classList.add('highlight');
          setTimeout(() => messageElement.classList.remove('highlight'), 2000);
        } else {
          requestAnimationFrame(scrollToMessage);
        }
      };
      requestAnimationFrame(scrollToMessage);
    } catch (error) {
      console.error('Error navigating to message:', error);
      dispatch(setError('Failed to navigate to message. Please try again.'));
    }
  };

  // WebSocket message handler
  const handleWebSocketMessage = useCallback((message: WebSocketMessage) => {
    console.log('MainLayout received WebSocket message:', message);

    if (!activeChannelId) return;

    // Check if message is from another channel
    if ('message' in message && 
        (message.type === 'message' || message.type === 'message_sent' || 
         message.type === 'new_reply' || message.type === 'message_updated') && 
        message.message.channel_id.toString() !== activeChannelId) {
      console.log('Ignoring message from different channel:', message.message.channel_id, 'current:', activeChannelId);
      return; // Ignore messages from other channels
    }

    switch (message.type) {
      case 'message':
      case 'message_sent':
        if ('message' in message) {
          const transformedMessage = transformMessage(message.message);
          console.log('Adding new message:', transformedMessage);

          dispatch(addMessage({
            channelId: activeChannelId,
            message: transformedMessage
          }));

          // Auto-scroll for new messages
          if (messageListRef.current) {
            const container = messageListRef.current;
            const isAtBottom = container.scrollHeight - container.scrollTop <= container.clientHeight + 100;
            if (isAtBottom) {
              requestAnimationFrame(() => {
                container.scrollTop = container.scrollHeight;
              });
            }
          }
        }
        break;

      case 'new_reply':
        if ('message' in message && message.parentId) {
          console.log('Adding new reply:', message);
          const transformedReply = transformMessage(message.message);

          dispatch(setReplies({
            channelId: activeChannelId,
            messageId: message.parentId.toString(),
            replies: [transformedReply]
          }));

          // Auto-expand parent message
          const parentMessage = channelMessages.find(m => m.id === message.parentId);
          if (parentMessage && !parentMessage.isExpanded) {
            dispatch(toggleExpanded({
              channelId: activeChannelId,
              messageId: message.parentId.toString()
            }));
          }
        }
        break;

      case 'message_deleted':
        dispatch(deleteMessage({
          channelId: message.channelId,
          messageId: message.messageId
        }));
        break;

      case 'message_updated':
        if ('message' in message) {
          const transformedMessage = transformMessage(message.message);
          dispatch(updateMessage({
            channelId: transformedMessage.channelId,
            id: transformedMessage.id,
            message: transformedMessage
          }));
        }
        break;

      case 'user_status':
        dispatch(updateUserStatus({
          userId: message.user_id,
          status: message.status
        }));
        break;

      case 'channel_created':
        dispatch(addChannel(message.channel));
        break;

      case 'channel_deleted':
        dispatch(removeChannel(message.channelId));
        break;

      case 'unread_count_updated':
        dispatch(updateChannelUnreadCount({
          channelId: message.channelId,
          count: message.count
        }));
        break;
    }
  }, [activeChannelId, channelMessages, dispatch]);

  // Set up WebSocket connection and message handling
  useEffect(() => {
    if (!activeChannelId) return;

    const unsubscribe = wsService.onMessage(handleWebSocketMessage);
    return () => unsubscribe();
  }, [activeChannelId, handleWebSocketMessage]);

  // Handle search
  const handleSearch = async (query: string) => {
    if (!query.trim()) {
      setSearchResults(null);
      return;
    }

    setIsSearching(true);
    setSearchError(undefined);

    try {
      const results = await searchAll(query);
      setSearchResults(results);
    } catch (error) {
      console.error('Search failed:', error);
      setSearchError('Search failed. Please try again.');
    } finally {
      setIsSearching(false);
    }
  };

  // Handle logout
  const handleLogout = () => {
    dispatch(logout());
  };

  // Sort and group channels
  const sortedChannels = [...channels].sort((a: Channel, b: Channel) => {
    // Sort by public/private first
    if (a.is_public !== b.is_public) {
      return a.is_public ? -1 : 1;
    }
    // Then sort by name
    return a.name.localeCompare(b.name);
  });

  const publicChannels = sortedChannels.filter((channel: Channel) => channel.is_public && !channel.is_direct_message);
  const privateChannels = sortedChannels.filter((channel: Channel) => !channel.is_public && !channel.is_direct_message);
  const directMessages = sortedChannels.filter((channel: Channel) => channel.is_direct_message);

  // Initial data fetch
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        console.log('Fetching initial data...');
        const fetchedChannels = await getChannels();
        dispatch(setChannels(fetchedChannels));

        if (fetchedChannels.length > 0) {
          const firstChannelId = fetchedChannels[0].id;
          const channelUsers = await getChannelUsers(firstChannelId);
          const usersObject = channelUsers.reduce<{ [key: string]: User }>((acc, user) => {
            acc[user.id] = user;
            return acc;
          }, {});
          dispatch(setUsers(usersObject));
          await handleChannelSwitch(firstChannelId);
        }
      } catch (error) {
        console.error('Failed to fetch initial data:', error);
        dispatch(setError('Failed to load initial data. Please refresh the page.'));
      }
    };

    fetchInitialData();
  }, [dispatch, handleChannelSwitch]);

  // Handle channel selection
  const handleChannelSelect = useCallback((targetChannelId: string): void => {
    if (targetChannelId !== activeChannelId) {
      handleChannelSwitch(targetChannelId).catch(error => {
        console.error('Error selecting channel:', error);
        dispatch(setError('Failed to switch channels. Please try again.'));
      });
    }
  }, [activeChannelId, handleChannelSwitch, dispatch]);

  return (
    <MainContainer>
      <Sidebar>
        <ChannelList>
          <ChannelHeader>
            <h2>Channels</h2>
            <CreateChannelButton onClick={() => setIsCreateModalOpen(true)}>
              Create
            </CreateChannelButton>
          </ChannelHeader>
          {channels.map((channel: Channel) => (
            <ChannelListItem
              key={channel.id}
              channel={channel}
              isActive={channel.id === activeChannelId}
              onSelect={handleChannelSelect}
            />
          ))}
        </ChannelList>
        <UserList>
          <h2>Users</h2>
          {(Object.values(users) as User[]).map((user: User) => (
            <UserListItem
              key={user.id}
              username={user.username}
              status={user.status}
            />
          ))}
        </UserList>
      </Sidebar>
      <ChatArea>
        <ChatHeader>
          {activeChannel && (
            <>
              <h1>{activeChannel.name}</h1>
              <ChannelActions>
                <SettingsButton onClick={() => setIsSettingsOpen(true)}>
                  Settings
                </SettingsButton>
              </ChannelActions>
            </>
          )}
        </ChatHeader>
        <MessageList
          messages={channelMessages}
          selectedMessageId={selectedMessageId}
          ref={messageListRef}
        />
        {activeChannel && (
          <ChatInput>
            <MessageInput channelId={activeChannel.id} />
          </ChatInput>
        )}
      </ChatArea>

      {isCreateModalOpen && (
        <CreateChannelModal
          isOpen={isCreateModalOpen}
          onClose={() => setIsCreateModalOpen(false)}
        />
      )}

      {isSettingsOpen && activeChannel && (
        <ChannelSettings
          isOpen={isSettingsOpen}
          channel={activeChannel}
          onClose={() => setIsSettingsOpen(false)}
        />
      )}
    </MainContainer>
  );
};

export default MainLayout;
