import { Message } from '../../types';
import { apiRequest } from './utils';

export const getChannelMessages = async (channelId: number, limit: number = 50): Promise<Message[]> => {
  return apiRequest<Message[]>(`/api/channels/${channelId}/messages?limit=${limit}`);
};

export const sendMessage = async (channelId: number, content: string): Promise<Message> => {
  return apiRequest<Message>(`/api/channels/${channelId}/messages`, {
    method: 'POST',
    body: JSON.stringify({ content }),
  });
};

export const updateMessage = async (messageId: number, content: string): Promise<Message> => {
  return apiRequest<Message>(`/api/messages/${messageId}`, {
    method: 'PUT',
    body: JSON.stringify({ content }),
  });
};

export const deleteMessage = async (messageId: number): Promise<void> => {
  await apiRequest(`/api/messages/${messageId}`, {
    method: 'DELETE',
  });
};

export const getReplies = async (messageId: number): Promise<Message[]> => {
  return apiRequest<Message[]>(`/api/messages/${messageId}/replies`);
};

export const createReply = async (messageId: number, content: string): Promise<Message> => {
  return apiRequest<Message>(`/api/messages/${messageId}/replies`, {
    method: 'POST',
    body: JSON.stringify({ content }),
  });
};
