import React, { useState } from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { RootState } from '../../../store/rootReducer';
import Sidebar from '../Sidebar';
import Chat from '../../chat/Chat';
import CreateChannelModal from '../../chat/CreateChannelModal';

const Container = styled.div`
  display: flex;
  height: 100vh;
  background-color: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
`;

const MainContent = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const NoChannelSelected = styled.div`
  display: flex;
  flex: 1;
  justify-content: center;
  align-items: center;
  font-family: 'VT323', monospace;
  color: ${props => props.theme.colors.textDim};
  font-size: 1.5rem;
  text-align: center;
  padding: 2rem;
  background-color: ${props => props.theme.colors.backgroundDark};
  border: 2px solid ${props => props.theme.colors.border};
  margin: 1rem;

  > div {
    max-width: 600px;
  }
`;

const MainLayout: React.FC = () => {
  const [isCreateChannelModalOpen, setIsCreateChannelModalOpen] = useState(false);
  const activeChannelId = useSelector((state: RootState) => state.chat?.activeChannelId ?? null);

  return (
    <Container>
      <Sidebar onCreateChannel={() => setIsCreateChannelModalOpen(true)} />
      <MainContent>
        {activeChannelId ? (
          <Chat channelId={activeChannelId} />
        ) : (
          <NoChannelSelected>
            <div>
              Welcome to SERMO! ðŸ‘¾<br />
              Select a channel from the sidebar or create a new one to start chatting.
            </div>
          </NoChannelSelected>
        )}
      </MainContent>
      <CreateChannelModal
        isOpen={isCreateChannelModalOpen}
        onClose={() => setIsCreateChannelModalOpen(false)}
      />
    </Container>
  );
};

export default MainLayout;
