import React, { useState } from 'react';
import styled from 'styled-components';

interface MessageOptionsProps {
  onDelete: () => void;
  onReply: () => void;
  onClose: () => void;
}

const OptionsContainer = styled.div`
  position: relative;
  display: inline-block;
`;

const OptionsMenu = styled.div`
  position: absolute;
  right: 0;
  top: 100%;
  background-color: #2a2a2a;
  border: 1px solid #444;
  border-radius: 4px;
  padding: 0.25rem;
  z-index: 10;
  min-width: 120px;
`;

const MenuItem = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
  padding: 0.5rem;
  width: 100%;
  text-align: left;
  display: block;
  font-family: inherit;

  &:hover {
    background-color: #444;
  }
`;

const MessageOptions: React.FC<MessageOptionsProps> = ({
  onDelete,
  onReply,
  onClose,
}) => {
  const handleDelete = () => {
    onDelete();
    onClose();
  };

  const handleReply = () => {
    onReply();
    onClose();
  };

  return (
    <OptionsContainer>
      <OptionsMenu>
        <MenuItem onClick={handleReply}>Reply</MenuItem>
        <MenuItem onClick={handleDelete}>Delete</MenuItem>
      </OptionsMenu>
    </OptionsContainer>
  );
};

export default MessageOptions;
