import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import ChatMessage from '../../common/ChatMessage';
import { getChannelMessages } from '../../../services/api/chat';
import { fetchMessagesStart, fetchMessagesSuccess, fetchMessagesFailure } from '../../../store/messages/messagesSlice';
import { Message as ApiMessage } from '../../../types';
import { Message as StoreMessage } from '../../../store/types';
import { RootState } from '../../../store/rootReducer';

const MessageListContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  padding: 16px;
  gap: 2px;
  background: ${props => props.theme.colors.background};
  border: 2px solid ${props => props.theme.colors.border};
  font-family: 'Courier New', monospace;
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 8px;
  color: ${props => props.theme.colors.textSecondary};
  font-family: 'Courier New', monospace;
  font-style: italic;
`;

const ErrorMessage = styled.div`
  text-align: center;
  padding: 8px;
  color: ${props => props.theme.colors.error};
  font-family: 'Courier New', monospace;
  border: 1px solid ${props => props.theme.colors.error};
  margin: 8px;
`;

const NoMessagesMessage = styled.div`
  text-align: center;
  padding: 20px;
  color: ${props => props.theme.colors.secondary};
  font-family: 'Courier New', monospace;
  border: 1px dashed ${props => props.theme.colors.border};
  margin: 16px;
`;

interface MessageListProps {
  channelId: number | null;
}

const MESSAGES_PER_PAGE = 30;

// Transform API message to store message format
const transformMessage = (msg: ApiMessage): StoreMessage => ({
  id: String(msg.id),
  content: msg.content,
  channelId: String(msg.channel_id),
  userId: String(msg.sender_id),
  reactions: [],
  createdAt: msg.created_at,
  updatedAt: msg.created_at,
});

const MessageList: React.FC<MessageListProps> = ({ channelId }) => {
  const dispatch = useDispatch();
  const containerRef = useRef<HTMLDivElement>(null);
  const [isLoadingMore, setIsLoadingMore] = useState(false);
  const [hasMoreMessages, setHasMoreMessages] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [shouldScrollToBottom, setShouldScrollToBottom] = useState(true);

  const messages = useSelector((state: RootState) => 
    channelId && state.messages?.messagesByChannel ? 
    state.messages.messagesByChannel[String(channelId)] || [] : []
  );
  const users = useSelector((state: RootState) => state.chat?.users || {});
  const isLoading = useSelector((state: RootState) => state.messages?.loading);

  // Initial message load
  useEffect(() => {
    if (channelId) {
      dispatch(fetchMessagesStart());
      setIsLoadingMore(true);
      setHasMoreMessages(true);
      setError(null);
      setShouldScrollToBottom(true);

      getChannelMessages(channelId, MESSAGES_PER_PAGE)
        .then(newMessages => {
          if (newMessages.length < MESSAGES_PER_PAGE) {
            setHasMoreMessages(false);
          }
          const transformedMessages = newMessages.map(transformMessage);
          dispatch(fetchMessagesSuccess({ 
            channelId: String(channelId), 
            messages: transformedMessages 
          }));
        })
        .catch(error => {
          console.error('Failed to fetch messages:', error);
          setError('Failed to load messages. Please try again.');
          dispatch(fetchMessagesFailure(error.message));
        })
        .finally(() => {
          setIsLoadingMore(false);
        });
    }
  }, [channelId, dispatch]);

  // Handle scroll to load more messages
  const handleScroll = async (e: React.UIEvent<HTMLDivElement>) => {
    const div = e.currentTarget;
    const isNearTop = div.scrollTop <= 100;

    if (isNearTop && !isLoadingMore && hasMoreMessages && channelId) {
      setIsLoadingMore(true);
      setError(null);
      setShouldScrollToBottom(false);

      try {
        const olderMessages = await getChannelMessages(
          channelId, 
          MESSAGES_PER_PAGE,
          messages.length
        );

        if (olderMessages.length < MESSAGES_PER_PAGE) {
          setHasMoreMessages(false);
        }

        if (olderMessages.length > 0) {
          const transformedMessages = olderMessages.map(transformMessage);
          dispatch(fetchMessagesSuccess({ 
            channelId: String(channelId), 
            messages: [...messages, ...transformedMessages]
          }));
        }
      } catch (error) {
        console.error('Failed to fetch older messages:', error);
        setError('Failed to load more messages. Please try again.');
      } finally {
        setIsLoadingMore(false);
      }
    }
  };

  // Auto-scroll to bottom on new messages
  useEffect(() => {
    if (containerRef.current && messages.length > 0 && shouldScrollToBottom) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [messages, shouldScrollToBottom]);

  // Reset scroll behavior when channel changes
  useEffect(() => {
    setShouldScrollToBottom(true);
  }, [channelId]);

  if (!channelId) {
    return (
      <MessageListContainer>
        <NoMessagesMessage>
          Select a channel to view messages
        </NoMessagesMessage>
      </MessageListContainer>
    );
  }

  return (
    <MessageListContainer
      ref={containerRef}
      onScroll={handleScroll}
    >
      {error && (
        <ErrorMessage>
          {error}
        </ErrorMessage>
      )}

      {isLoadingMore && (
        <LoadingMessage>Loading older messages...</LoadingMessage>
      )}

      {!hasMoreMessages && messages.length > 0 && (
        <LoadingMessage>
          You've reached the beginning of this conversation
        </LoadingMessage>
      )}

      {messages.map((msg: StoreMessage) => (
        <div key={msg.id} id={`message-${msg.id}`}>
          <ChatMessage
            content={msg.content}
            sender={users[Number(msg.userId)]?.username || 'Unknown'}
            timestamp={msg.createdAt}
            isSystem={false}
          />
        </div>
      ))}

      {!isLoading && messages.length === 0 && (
        <NoMessagesMessage>
          No messages yet. Start the conversation!
        </NoMessagesMessage>
      )}
    </MessageListContainer>
  );
};

export default MessageList;
