import React from 'react';
import styled from 'styled-components';
import { API_URL } from '../../../services/api/utils';
import { Attachment } from '../../../types';

interface FilePreviewProps {
  filename: string;
  fileType: string;
  filePath: string;
  fileSize?: number;
  fileId: number;
}

const PreviewContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px;
  background-color: ${props => props.theme.colors.inputBackground};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: 4px;
  margin-top: 4px;
  max-width: 400px;
`;

const FileIcon = styled.span`
  font-family: monospace;
  font-size: 24px;
`;

const FileInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2px;
  flex: 1;
  min-width: 0;
`;

const FileName = styled.span`
  font-family: 'Courier New', monospace;
  font-size: 12px;
  color: ${props => props.theme.colors.text};
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const FileType = styled.span`
  font-family: 'Courier New', monospace;
  font-size: 10px;
  color: ${props => props.theme.colors.textLight};
`;

const FileSize = styled(FileType)`
  color: ${props => props.theme.colors.textDim};
`;

const DownloadLink = styled.a`
  font-family: monospace;
  font-size: 16px;
  color: ${props => props.theme.colors.text};
  text-decoration: none;
  padding: 4px 8px;
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: 4px;
  background: none;
  cursor: pointer;
  
  &:hover {
    background-color: ${props => props.theme.colors.hover};
  }
`;

const ImagePreview = styled.img`
  max-width: 300px;
  max-height: 200px;
  border-radius: 4px;
  margin-top: 4px;
`;

const FilePreview: React.FC<FilePreviewProps> = ({ filename, fileType, filePath, fileSize, fileId }) => {
  const isImage = fileType.startsWith('image/');
  
  const getFileIcon = () => {
    switch (fileType) {
      case 'image/jpeg':
      case 'image/png':
      case 'image/gif':
        return 'üñºÔ∏è';
      case 'application/pdf':
        return 'üìÑ';
      case 'text/plain':
        return 'üìù';
      default:
        return 'üìé';
    }
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes < 1024) return `${bytes} B`;
    if (bytes < 1024 * 1024) return `${(bytes / 1024).toFixed(1)} KB`;
    return `${(bytes / (1024 * 1024)).toFixed(1)} MB`;
  };

  const downloadUrl = `${API_URL}/files/download/${fileId}`;

  return (
    <PreviewContainer>
      <FileIcon>{getFileIcon()}</FileIcon>
      <FileInfo>
        <FileName>{filename}</FileName>
        <FileType>{fileType}</FileType>
        {fileSize && <FileSize>{formatFileSize(fileSize)}</FileSize>}
        {isImage && <ImagePreview src={downloadUrl} alt={filename} />}
      </FileInfo>
      <DownloadLink href={downloadUrl} download={filename} target="_blank" rel="noopener noreferrer">
        ‚¨áÔ∏è
      </DownloadLink>
    </PreviewContainer>
  );
};

export default FilePreview;
