import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from '../../../store/auth/authSlice';
import {
  setActiveChannel,
  setChannels,
  setUsers,
  updateUserStatus
} from '../../../store/chat/chatSlice';
import { addMessage } from '../../../store/messages/messagesSlice';
import Button from '../../common/Button';
import UserListItem from '../../common/UserListItem';
import ChannelListItem from '../../common/ChannelListItem';
import CreateChannelModal from '../../chat/CreateChannelModal';
import MessageInput from '../../chat/MessageInput';
import MessageList from '../../chat/MessageList';
import wsService, { WebSocketMessage } from '../../../services/websocket';
import { getChannels, getChannelUsers } from '../../../services/api/chat';

const MainContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  font-family: 'VT323', monospace;
`;

const Sidebar = styled.div`
  width: 200px;
  border-right: 2px solid ${props => props.theme.colors.border};
  display: flex;
  flex-direction: column;
`;

const ChannelList = styled.div`
  flex: 1;
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const UserList = styled.div`
  height: 200px;
  padding: 16px;
  border-top: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const ChatArea = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
`;

const ChatHeader = styled.div`
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  display: flex;
  justify-content: space-between;
  align-items: center;

  h1 {
    margin: 0;
    text-transform: uppercase;
  }
`;

const ChatInput = styled.div`
  border-top: 2px solid ${props => props.theme.colors.border};
`;

const LogoutButton = styled.button`
  background: none;
  border: 2px solid ${props => props.theme.colors.error};
  color: ${props => props.theme.colors.error};
  padding: 4px 8px;
  font-family: 'VT323', monospace;
  cursor: pointer;
  text-transform: uppercase;

  &:hover {
    background: ${props => props.theme.colors.error};
    color: ${props => props.theme.colors.background};
  }
`;

const ChannelHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;

  h2 {
    margin: 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const CreateChannelButton = styled(Button)`
  padding: 2px 8px;
  font-size: 0.875rem;
`;

const MainLayout: React.FC = () => {
  const dispatch = useDispatch();
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);

  const activeChannelId = useSelector((state: any) => state.chat.activeChannelId);
  const channels = useSelector((state: any) => state.chat.channels);
  const users = useSelector((state: any) => state.chat.users);
  const activeChannel = channels.find((channel: any) => channel.id === activeChannelId);

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const fetchedChannels = await getChannels();
        dispatch(setChannels(fetchedChannels));

        if (fetchedChannels.length > 0) {
          const channelUsers = await getChannelUsers(fetchedChannels[0].id);
          dispatch(setUsers(channelUsers));
          dispatch(setActiveChannel(fetchedChannels[0].id));
        }
      } catch (error) {
        console.error('Failed to fetch initial data:', error);
      }
    };

    fetchInitialData();

    // Connect WebSocket
    wsService.connect();

    // Set up WebSocket handlers
    const messageHandler = (data: WebSocketMessage) => {
      console.log('Received WebSocket message:', data);
      if (data.type === 'new_message' && data.message) {
        const message = {
          id: String(data.message.id),
          content: data.message.content,
          channelId: String(data.message.channel_id),
          userId: String(data.message.sender_id),
          reactions: [],
          createdAt: data.message.created_at || new Date().toISOString(),
          updatedAt: data.message.created_at || new Date().toISOString(),
        };
        dispatch(addMessage(message));
      }
    };

    const presenceHandler = (data: WebSocketMessage) => {
      if (data.type === 'presence_update' && data.user_id && data.status) {
        dispatch(updateUserStatus({
          userId: data.user_id,
          status: data.status
        }));
      }
    };

    wsService.onMessage(messageHandler);
    wsService.onPresence(presenceHandler);

    return () => {
      wsService.disconnect();
    };
  }, [dispatch]);

  const handleChannelClick = async (channelId: number) => {
    if (channelId !== activeChannelId) {
      dispatch(setActiveChannel(channelId));
      try {
        const channelUsers = await getChannelUsers(channelId);
        dispatch(setUsers(channelUsers));
      } catch (error) {
        console.error('Failed to fetch channel users:', error);
      }
    }
  };

  const handleLogout = () => {
    dispatch(logout());
  };

  return (
    <MainContainer>
      <Sidebar>
        <ChannelList>
          <ChannelHeader>
            <h2>Channels</h2>
            <CreateChannelButton
              variant="secondary"
              size="small"
              onClick={() => setIsCreateModalOpen(true)}
            >
              +New
            </CreateChannelButton>
          </ChannelHeader>
          {channels.map((channel: any) => (
            <ChannelListItem
              key={channel.id}
              name={channel.name}
              isActive={channel.id === activeChannelId}
              hasUnread={false}
              isDirect={channel.is_direct_message}
              onClick={() => handleChannelClick(channel.id)}
            />
          ))}
        </ChannelList>
        <UserList>
          <h2>Online Users</h2>
          {Object.values(users).map((user: any) => (
            <UserListItem
              key={user.id}
              username={user.username}
              status={user.status}
            />
          ))}
        </UserList>
      </Sidebar>
      <ChatArea>
        <ChatHeader>
          <h1>{activeChannel ? `${activeChannel.is_direct_message ? '@' : '#'}${activeChannel.name}` : 'Select a channel'}</h1>
          <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
        </ChatHeader>
        <MessageList channelId={activeChannelId} />
        <ChatInput>
          <MessageInput channelId={activeChannelId} />
        </ChatInput>
      </ChatArea>
      {isCreateModalOpen && (
        <CreateChannelModal
          isOpen={isCreateModalOpen}
          onClose={() => setIsCreateModalOpen(false)}
        />
      )}
    </MainContainer>
  );
};

export default MainLayout;
