import React, { useState, KeyboardEvent } from 'react';
import styled from 'styled-components';
import wsService from '../../../services/websocket';
import { sendMessage } from '../../../services/api/chat';

interface MessageInputProps {
  channelId: string | null;
}

const InputContainer = styled.div`
  padding: 8px;
  background-color: ${props => props.theme.colors.background};
  border-top: 1px solid ${props => props.theme.colors.border};
`;

const Input = styled.input`
  width: 100%;
  padding: 8px;
  background-color: ${props => props.theme.colors.inputBackground};
  color: ${props => props.theme.colors.text};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: 4px;
  font-family: 'Courier New', monospace;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
  }
`;

const ErrorText = styled.div`
  color: ${props => props.theme.colors.error};
  font-size: 12px;
  margin-top: 4px;
`;

const MessageInput: React.FC<MessageInputProps> = ({ channelId }) => {
  const [message, setMessage] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleKeyPress = async (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && message.trim() && channelId) {
      setError(null);
      setIsLoading(true);

      try {
        // Send via WebSocket first
        await wsService.sendMessage(channelId, message.trim());

        // Also send via API to ensure message is stored
        await sendMessage({
          channelId,
          content: message.trim()
        });

        setMessage('');
      } catch (error) {
        console.error('Failed to send message:', error);
        setError('Failed to send message. Please try again.');
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessage(e.target.value);
    if (error) {
      setError(null);
    }
  };

  return (
    <InputContainer>
      <Input
        type="text"
        value={message}
        onChange={handleChange}
        onKeyPress={handleKeyPress}
        placeholder={channelId ? "Type a message..." : "Select a channel to start chatting..."}
        disabled={isLoading || !channelId}
      />
      {error && <ErrorText>{error}</ErrorText>}
    </InputContainer>
  );
};

export default MessageInput;
