import { Reaction } from '../../types';
import { store } from '../../store';
import { addMessage, updateMessage, addReaction, removeReaction } from '../../store/messages/messagesSlice';
import { updateUserStatus } from '../../store/chat/chatSlice';

// Get WebSocket URL from environment variable or fallback to localhost
const WS_BASE_URL = process.env.REACT_APP_WS_URL || 'ws://localhost:8000/ws';

// Helper function to get the correct WebSocket URL based on the current protocol
const getWebSocketUrl = () => {
  // If we're on HTTPS, use WSS
  if (window.location.protocol === 'https:' && WS_BASE_URL.startsWith('ws:')) {
    return WS_BASE_URL.replace('ws:', 'wss:');
  }
  return WS_BASE_URL;
};

interface WebSocketMessage {
  type: 'new_message' | 'update_message' | 'user_status' | 'ping' | 'pong' | 'join_channel' | 'leave_channel' | 'reaction_added' | 'reaction_removed' | 'REACTION_ADDED' | 'REACTION_REMOVED';
  channelId?: string;
  message?: any;
  id?: string;
  updates?: any;
  userId?: string;
  status?: 'online' | 'offline' | 'away' | 'busy';
  isReply?: boolean;
  parentId?: string;
  messageId?: string;
  reaction?: Reaction;
  emoji?: string;
  data?: {
    channel_id: number | string;
    message_id: string;
    user_id: string;
    emoji: string;
    reaction: Reaction;
  };
}

export class WebSocketService {
  private static instance: WebSocketService | null = null;
  private ws: WebSocket | null = null;
  private pingInterval: NodeJS.Timeout | null = null;
  private reconnectAttempts = 0;
  private maxReconnectAttempts = 5;
  private reconnectTimeout: NodeJS.Timeout | null = null;
  private channels: Set<string> = new Set();

  constructor() {
    if (WebSocketService.instance) {
      return WebSocketService.instance;
    }
    WebSocketService.instance = this;
  }

  private getAuthToken(): string | null {
    const state = store.getState();
    return state.auth?.token || null;
  }

  public connect() {
    if (this.ws?.readyState === WebSocket.OPEN) {
      console.log('WebSocket already connected');
      return;
    }

    const token = this.getAuthToken();
    if (!token) {
      console.warn('No auth token available, skipping WebSocket connection');
      return;
    }

    const wsUrl = `${getWebSocketUrl()}?token=${token}`;
    console.log('Connecting to WebSocket:', wsUrl);

    try {
      this.ws = new WebSocket(wsUrl);

      this.ws.onopen = () => {
        console.log('WebSocket connected successfully');
        this.reconnectAttempts = 0;
        this.startPingInterval();

        // Rejoin all channels
        this.channels.forEach(channelId => {
          console.log('Rejoining channel:', channelId);
          this.joinChannel(channelId);
        });
      };

      this.ws.onmessage = (event) => {
        console.log('Raw WebSocket message received:', event.data);
        this.handleMessage(event);
      };

      this.ws.onclose = (event) => {
        console.log('WebSocket disconnected with code:', event.code, 'reason:', event.reason);
        this.stopPingInterval();
        this.handleReconnect();
      };

      this.ws.onerror = (error) => {
        console.error('WebSocket error:', error);
      };
    } catch (error) {
      console.error('Error creating WebSocket connection:', error);
    }
  }

  private handleReconnect() {
    if (this.reconnectAttempts < this.maxReconnectAttempts) {
      this.reconnectAttempts++;
      console.log(`Attempting to reconnect (${this.reconnectAttempts}/${this.maxReconnectAttempts})`);
      this.reconnectTimeout = setTimeout(() => {
        this.connect();
      }, 5000);
    }
  }

  private startPingInterval() {
    this.pingInterval = setInterval(() => {
      if (this.ws?.readyState === WebSocket.OPEN) {
        this.ws.send(JSON.stringify({ type: 'PING' }));
      }
    }, 30000);
  }

  private stopPingInterval() {
    if (this.pingInterval) {
      clearInterval(this.pingInterval);
      this.pingInterval = null;
    }
  }

  public disconnect() {
    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }
    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }
    this.stopPingInterval();
  }

  private handleMessage(event: MessageEvent) {
    try {
      console.log('Raw WebSocket message received:', event.data);
      const data = JSON.parse(event.data) as WebSocketMessage;
      console.log('Parsed WebSocket message:', data);

      // Normalize the message type to lowercase
      const messageType = data.type.toLowerCase();
      console.log('Normalized message type:', messageType);

      // For reaction events, check if we're subscribed to the channel
      if (messageType === 'reaction_added' || messageType === 'reaction_removed') {
        const channelId = data.data?.channel_id?.toString();
        console.log('Reaction event received for channel:', channelId);
        console.log('Currently subscribed channels:', Array.from(this.channels));
        if (channelId && !this.channels.has(channelId)) {
          console.warn('Received reaction event for unsubscribed channel:', channelId);
          return;
        }
      }

      switch (messageType) {
        case 'new_message':
          if (data.channelId && data.message) {
            if (data.isReply && data.parentId) {
              store.dispatch(addMessage({
                channelId: data.channelId,
                message: {
                  ...data.message,
                  parentId: data.parentId
                }
              }));
            } else {
              store.dispatch(addMessage({
                channelId: data.channelId,
                message: data.message
              }));
            }
          }
          break;

        case 'update_message':
          if (data.channelId && data.id && data.updates) {
            store.dispatch(updateMessage({
              channelId: data.channelId,
              messageId: data.id,
              message: data.updates
            }));
          }
          break;

        case 'user_status':
          if (data.userId && data.status) {
            store.dispatch(updateUserStatus({
              userId: data.userId,
              status: data.status
            }));
          }
          break;

        case 'reaction_added':
          console.log('Processing reaction_added event:', data);
          if (data.data?.channel_id && data.data?.message_id) {
            const channelId = data.data.channel_id.toString();
            const messageId = data.data.message_id.toString();
            const reaction = {
              id: messageId + '_' + data.data.user_id + '_' + data.data.emoji,
              messageId: messageId,
              userId: data.data.user_id?.toString() || '',
              emoji: data.data.emoji || '',
              createdAt: new Date().toISOString(),
            };

            console.log('Handling reaction_added:', {
              channelId,
              messageId,
              reaction,
              rawData: data.data
            });

            // Get current state
            const state = store.getState();
            const message = state.messages.messagesByChannel[channelId]?.find(
              m => m.id === messageId
            );
            console.log('Current message state:', message);

            if (!message) {
              console.warn('Message not found in store:', messageId);
              return;
            }

            store.dispatch(addReaction({
              channelId,
              messageId,
              reaction
            }));

            // Get updated state
            const updatedState = store.getState();
            const updatedMessage = updatedState.messages.messagesByChannel[channelId]?.find(
              m => m.id === messageId
            );
            console.log('Updated message state:', updatedMessage);
          } else {
            console.warn('Invalid reaction_added data:', {
              channelId: data.data?.channel_id,
              messageId: data.data?.message_id,
              userId: data.data?.user_id,
              emoji: data.data?.emoji
            });
          }
          break;

        case 'reaction_removed':
          if (data.data?.channel_id && data.data?.message_id && data.data?.user_id && data.data?.emoji) {
            const channelId = data.data.channel_id.toString();
            const messageId = data.data.message_id;
            const userId = data.data.user_id;
            const emoji = data.data.emoji;

            console.log('Handling reaction_removed:', {
              channelId,
              messageId,
              userId,
              emoji
            });

            // Get current state
            const state = store.getState();
            const message = state.messages.messagesByChannel[channelId]?.find(
              m => m.id === messageId
            );
            console.log('Current message state:', message);

            store.dispatch(removeReaction({
              channelId,
              messageId,
              userId,
              emoji
            }));

            // Get updated state
            const updatedState = store.getState();
            const updatedMessage = updatedState.messages.messagesByChannel[channelId]?.find(
              m => m.id === messageId
            );
            console.log('Updated message state:', updatedMessage);
          } else {
            console.warn('Invalid reaction_removed data:', {
              channelId: data.data?.channel_id,
              messageId: data.data?.message_id,
              userId: data.data?.user_id,
              emoji: data.data?.emoji
            });
          }
          break;

        case 'pong':
          // Handle pong response
          break;

        default:
          console.warn('Unknown message type:', messageType, 'original type:', data.type);
      }
    } catch (error) {
      console.error('Error handling WebSocket message:', error);
      console.error('Raw message data:', event.data);
    }
  }

  public joinChannel(channelId: string) {
    console.log('Joining channel:', channelId);
    if (this.ws?.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify({
        type: 'join_channel',
        channelId
      }));
      this.channels.add(channelId);
      console.log('Current channels:', Array.from(this.channels));
    } else {
      console.warn('WebSocket not connected, cannot join channel:', channelId);
    }
  }

  public leaveChannel(channelId: string) {
    console.log('Leaving channel:', channelId);
    if (this.ws?.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify({
        type: 'leave_channel',
        channelId
      }));
      this.channels.delete(channelId);
      console.log('Current channels:', Array.from(this.channels));
    } else {
      console.warn('WebSocket not connected, cannot leave channel:', channelId);
    }
  }
}

export default new WebSocketService();
