import { apiRequest, API_URL } from './utils';
import { getAuthToken } from './auth';

export interface FileResponse {
  id: string;
  filename: string;
  file_type: string;
  file_path: string;
  message_id?: number;
  created_at: string;
}

export class FileUploadError extends Error {
  constructor(message: string, public code: string) {
    super(message);
    this.name = 'FileUploadError';
  }
}

/**
 * Uploads a file to the server
 * @param file The file to upload
 * @param messageId Optional message ID to attach the file to
 * @returns Promise<FileResponse>
 * @throws {FileUploadError} When upload fails with specific error details
 */
export const uploadFile = async (file: File, messageId?: string): Promise<FileResponse> => {
  console.log(`Uploading file ${file.name}...`);
  
  // Validate file size
  if (file.size > 50 * 1024 * 1024) {
    throw new FileUploadError(
      'File size exceeds 50MB limit',
      'FILE_TOO_LARGE'
    );
  }

  // Validate file type
  const allowedTypes = ['image/jpeg', 'image/png', 'image/gif', 'application/pdf', 'text/plain'];
  if (!allowedTypes.includes(file.type)) {
    throw new FileUploadError(
      'File type not supported. Allowed types: jpg, png, gif, pdf, txt',
      'INVALID_FILE_TYPE'
    );
  }

  try {
    const formData = new FormData();
    formData.append('file', file);
    if (messageId) {
      // Convert messageId to integer and validate
      const messageIdInt = parseInt(messageId, 10);
      if (isNaN(messageIdInt)) {
        throw new FileUploadError(
          'Invalid message ID format',
          'INVALID_MESSAGE_ID'
        );
      }
      formData.append('message_id', messageIdInt.toString());
    }

    const token = getAuthToken();
    if (!token) {
      throw new FileUploadError(
        'Authentication required. Please log in.',
        'AUTH_ERROR'
      );
    }

    const response = await fetch(`${API_URL}/files/upload`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`,
        // Let browser set Content-Type with boundary for FormData
      },
      credentials: 'include', // Important for CORS with authentication
      mode: 'cors', // Explicit CORS mode
      body: formData,
    });

    if (!response.ok) {
      const errorText = await response.text();
      console.error('Upload failed:', errorText);
      
      if (response.status === 413) {
        throw new FileUploadError(
          'File size too large for server. Maximum size: 50MB',
          'SERVER_FILE_TOO_LARGE'
        );
      }
      if (response.status === 415) {
        throw new FileUploadError(
          'File type not accepted by server',
          'SERVER_INVALID_TYPE'
        );
      }
      if (response.status === 401) {
        throw new FileUploadError(
          'Authentication error. Please try logging in again.',
          'AUTH_ERROR'
        );
      }
      if (response.status === 503) {
        throw new FileUploadError(
          'Server is temporarily unavailable. Please try again later.',
          'SERVER_ERROR'
        );
      }
      if (response.status === 500) {
        throw new FileUploadError(
          'Server error occurred. Please try again later.',
          'SERVER_ERROR'
        );
      }

      throw new FileUploadError(
        'Failed to upload file. Server error.',
        'UPLOAD_FAILED'
      );
    }

    const responseData = await response.json();
    console.log('File uploaded successfully:', responseData);
    return responseData;
  } catch (error) {
    console.error('Error uploading file:', error);
    
    if (error instanceof FileUploadError) {
      throw error;
    }

    if (error instanceof TypeError && error.message.includes('NetworkError')) {
      throw new FileUploadError(
        'Network error occurred. Please check your connection and try again.',
        'NETWORK_ERROR'
      );
    }

    // Generic error case
    throw new FileUploadError(
      'Failed to upload file. Please try again.',
      'UPLOAD_FAILED'
    );
  }
};
