import { Message, User, StoreMessage } from '../../types';
import { store } from '../../store';
import { addMessage, updateMessage } from '../../store/messages/messagesSlice';
import { updateUserStatus } from '../../store/chat/chatSlice';

// Get WebSocket URL from environment variable or fallback to localhost
const WS_BASE_URL = process.env.REACT_APP_WS_URL || 'ws://localhost:8000/ws';

// Helper function to get the correct WebSocket URL based on the current protocol
const getWebSocketUrl = () => {
  // If we're on HTTPS, use WSS
  if (window.location.protocol === 'https:' && WS_BASE_URL.startsWith('ws:')) {
    return WS_BASE_URL.replace('ws:', 'wss:');
  }
  return WS_BASE_URL;
};

interface WebSocketMessage {
  type: 'NEW_MESSAGE' | 'UPDATE_MESSAGE' | 'USER_STATUS' | 'PING' | 'PONG' | 'JOIN_CHANNEL' | 'LEAVE_CHANNEL';
  channelId?: string;
  message?: StoreMessage;
  id?: string;
  updates?: Partial<StoreMessage>;
  userId?: string;
  status?: 'online' | 'offline' | 'away' | 'busy';
}

export class WebSocketService {
  private static instance: WebSocketService;
  private chatSocket: WebSocket | null = null;
  private presenceSocket: WebSocket | null = null;
  private pingInterval: NodeJS.Timeout | null = null;
  private ws: WebSocket | null = null;
  private reconnectAttempts = 0;
  private maxReconnectAttempts = 5;
  private reconnectTimeout: NodeJS.Timeout | null = null;
  private channels: Set<string> = new Set();

  private constructor() {
    // Private constructor to enforce singleton pattern
  }

  connect() {
    if (this.ws?.readyState === WebSocket.OPEN) return;

    const token = this.getAuthToken();
    if (!token) {
      console.error('No auth token available for WebSocket connection');
      return;
    }

    // Construct WebSocket URL with token
    const wsUrl = `${getWebSocketUrl()}?token=${token}`;
    
    console.log('Connecting to WebSocket:', wsUrl);
    this.ws = new WebSocket(wsUrl);
    
    this.ws.onopen = () => {
      console.log('WebSocket connected successfully');
      this.reconnectAttempts = 0;
      this.startPingInterval();
      
      // Rejoin all channels
      this.channels.forEach(channelId => {
        this.joinChannel(channelId);
      });
    };

    this.ws.onmessage = this.handleMessage.bind(this);
    this.ws.onclose = (event) => {
      console.log('WebSocket disconnected with code:', event.code);
      this.stopPingInterval();
      this.attemptReconnect();
    };
    this.ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };
  }

  private getAuthToken(): string | null {
    const state = store.getState();
    return state.auth?.token || null;
  }

  private handleMessage(event: MessageEvent) {
    try {
      const data = JSON.parse(event.data) as WebSocketMessage;
      console.log('WebSocket message received:', data);

      switch (data.type) {
        case 'NEW_MESSAGE':
          if (data.channelId && data.message) {
            store.dispatch(addMessage({
              channelId: data.channelId,
              message: data.message
            }));
          }
          break;

        case 'UPDATE_MESSAGE':
          if (data.channelId && data.id && data.updates) {
            const updatedMessage = {
              id: data.id,
              ...data.updates
            } as StoreMessage;
            
            store.dispatch(updateMessage({
              channelId: data.channelId,
              id: data.id,
              message: updatedMessage
            }));
          }
          break;

        case 'USER_STATUS':
          if (data.userId && data.status) {
            store.dispatch(updateUserStatus({
              userId: data.userId,
              status: data.status
            }));
          }
          break;

        case 'PONG':
          // Handle pong response
          break;

        default:
          console.warn('Unknown message type:', data.type);
      }
    } catch (error) {
      console.error('Error handling WebSocket message:', error);
    }
  }

  private attemptReconnect() {
    if (this.reconnectAttempts >= this.maxReconnectAttempts) {
      console.error('Max reconnection attempts reached');
      return;
    }

    this.reconnectAttempts++;
    const delay = Math.min(1000 * Math.pow(2, this.reconnectAttempts), 10000);
    
    this.reconnectTimeout = setTimeout(() => {
      console.log(`Attempting to reconnect (${this.reconnectAttempts}/${this.maxReconnectAttempts})`);
      this.connect();
    }, delay);
  }

  private startPingInterval() {
    this.pingInterval = setInterval(() => {
      this.send({ type: 'PING' });
    }, 30000);
  }

  private stopPingInterval() {
    if (this.pingInterval) {
      clearInterval(this.pingInterval);
      this.pingInterval = null;
    }
  }

  send(data: WebSocketMessage) {
    if (this.ws?.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify(data));
    } else {
      console.warn('WebSocket is not connected');
    }
  }

  joinChannel(channelId: string) {
    this.channels.add(channelId);
    this.send({
      type: 'JOIN_CHANNEL',
      channelId
    });
  }

  leaveChannel(channelId: string) {
    this.channels.delete(channelId);
    this.send({
      type: 'LEAVE_CHANNEL',
      channelId
    });
  }

  disconnect() {
    this.channels.clear();
    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }
    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }
    this.stopPingInterval();
  }

  private getWebSocketUrl(endpoint: string): string {
    const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('No token found');
    }
    return `${protocol}//localhost:8000/ws?token=${token}`;
  }

  private connectToChat() {
    const url = this.getWebSocketUrl('chat');
    console.log('Connecting to chat WebSocket at:', url);
    this.chatSocket = new WebSocket(url);
    // ... existing code ...
  }

  private connectToPresence() {
    const url = this.getWebSocketUrl('presence');
    console.log('Connecting to presence WebSocket at:', url);
    this.presenceSocket = new WebSocket(url);
    // ... existing code ...
  }
}

export default new WebSocketService();
