import React from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { StoreMessage, RootState } from '../../../types';
import Message from '../Message';

interface MessageRepliesProps {
  parentId: string;
}

const ReplyWrapper = styled.div`
  margin-left: 24px;
  border-left: 2px solid ${props => props.theme.colors.border};
  padding-left: 8px;
`;

const MessageReplies: React.FC<MessageRepliesProps> = ({ parentId }) => {
  const { replies, users, currentUser } = useSelector((state: RootState) => ({
    replies: Object.values(state.messages.messagesByChannel)
      .flat()
      .filter((msg: StoreMessage) => msg.parentId === parentId),
    users: state.chat.users,
    currentUser: state.auth.user
  }));

  const handleDelete = (messageId: string) => {
    // TODO: Implement delete functionality
    console.log('Delete reply:', messageId);
  };

  const handleToggleReplies = () => {
    // No-op for replies
  };

  const handleReply = () => {
    // No-op for replies
  };

  return (
    <div>
      {replies.map((reply) => (
        <ReplyWrapper key={reply.id}>
          <Message
            content={reply.content}
            sender={users[reply.userId]?.username || reply.userId}
            timestamp={reply.createdAt}
            userId={reply.userId}
            currentUserId={currentUser?.id}
            onDelete={() => handleDelete(reply.id)}
            replyCount={0}
            isExpanded={false}
            onToggleReplies={handleToggleReplies}
            onReply={handleReply}
            isReply={true}
          />
        </ReplyWrapper>
      ))}
    </div>
  );
};

export default MessageReplies;
