import React from 'react';
import styled from 'styled-components';
import { formatTimestamp } from '../../../utils/dateUtils';

interface MessageProps {
  content: string;
  sender: string;
  timestamp: string;
  userId: string;
  currentUserId?: string;
  onDelete?: () => void;
  replyCount?: number;
  isExpanded?: boolean;
  onToggleReplies?: () => void;
  onReply?: () => void;
  isPending?: boolean;
}

const MessageContainer = styled.div<{ $isPending?: boolean }>`
  display: flex;
  flex-direction: column;
  padding: 4px 8px;
  border-radius: 4px;
  background-color: ${props => props.$isPending ? props.theme.colors.hover : 'transparent'};
  opacity: ${props => props.$isPending ? 0.7 : 1};
  transition: background-color 0.2s ease, opacity 0.2s ease;

  &:hover {
    background-color: ${props => props.theme.colors.hover};
  }
`;

const MessageHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 4px;
`;

const SenderName = styled.span`
  font-weight: bold;
  color: ${props => props.theme.colors.primary};
`;

const Timestamp = styled.span`
  color: ${props => props.theme.colors.textLight};
  font-size: 0.8em;
`;

const PendingIndicator = styled.span`
  color: ${props => props.theme.colors.textLight};
  font-size: 0.8em;
  font-style: italic;
`;

const MessageContent = styled.div`
  color: ${props => props.theme.colors.text};
  white-space: pre-wrap;
  word-break: break-word;
`;

const MessageActions = styled.div`
  display: flex;
  gap: 8px;
  margin-top: 4px;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  color: ${props => props.theme.colors.textLight};
  font-size: 0.8em;
  cursor: pointer;
  padding: 2px 4px;
  border-radius: 2px;

  &:hover {
    color: ${props => props.theme.colors.primary};
    background-color: ${props => props.theme.colors.hover};
  }
`;

const Message: React.FC<MessageProps> = ({
  content,
  sender,
  timestamp,
  userId,
  currentUserId,
  onDelete,
  replyCount = 0,
  isExpanded = false,
  onToggleReplies,
  onReply,
  isPending = false
}) => {
  return (
    <MessageContainer $isPending={isPending}>
      <MessageHeader>
        <SenderName>{sender}</SenderName>
        <Timestamp>{formatTimestamp(timestamp)}</Timestamp>
        {isPending && <PendingIndicator>Sending...</PendingIndicator>}
      </MessageHeader>
      <MessageContent>{content}</MessageContent>
      <MessageActions>
        {onReply && (
          <ActionButton onClick={onReply}>
            Reply
          </ActionButton>
        )}
        {replyCount > 0 && onToggleReplies && (
          <ActionButton onClick={onToggleReplies}>
            {isExpanded ? 'Hide' : 'Show'} {replyCount} {replyCount === 1 ? 'reply' : 'replies'}
          </ActionButton>
        )}
        {currentUserId === userId && onDelete && (
          <ActionButton onClick={onDelete}>
            Delete
          </ActionButton>
        )}
      </MessageActions>
    </MessageContainer>
  );
};

export default Message;
