import React from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { StoreMessage, RootState } from '../../../types';
import ChatMessage from '../../common/ChatMessage';

interface MessageRepliesProps {
  parentId: string;
  replies: StoreMessage[];
  isExpanded: boolean;
  onToggleReplies: (messageId: string) => void;
  onDelete: (messageId: string) => void;
  currentUserId?: string;
  depth?: number;
}

const RepliesContainer = styled.div<{ isExpanded: boolean; depth?: number }>`
  display: ${props => props.isExpanded ? 'block' : 'none'};
  margin-left: ${props => (props.depth || 1) * 24}px;
  position: relative;
  border-left: 2px solid ${props => props.theme.colors.border};
  margin-top: 2px;
  margin-bottom: 2px;
  padding-left: 8px;

  &:before {
    content: '';
    position: absolute;
    left: -2px;
    top: 0;
    width: 8px;
    height: 2px;
    background-color: ${props => props.theme.colors.border};
  }
`;

const ReplyWrapper = styled.div`
  position: relative;
  padding: 2px 0;
  background-color: ${props => props.theme.colors.backgroundDark};

  &:hover {
    background-color: ${props => props.theme.colors.hover};
  }

  &:last-child {
    &:after {
      content: '';
      position: absolute;
      left: -10px;
      bottom: 50%;
      width: 8px;
      height: 2px;
      background-color: ${props => props.theme.colors.border};
    }
  }
`;

const MessageReplies: React.FC<MessageRepliesProps> = ({
  parentId,
  replies,
  isExpanded,
  onToggleReplies,
  onDelete,
  currentUserId,
  depth = 1,
}) => {
  const users = useSelector((state: RootState) => state.chat?.users || {});
  const messages = useSelector((state: RootState) => 
    state.messages?.messagesByChannel[replies[0]?.channelId || ''] || []
  );

  // Get nested replies for each reply
  const getNestedReplies = (messageId: string) => 
    messages.filter(m => m.parentId === messageId);

  return (
    <RepliesContainer isExpanded={isExpanded} depth={depth}>
      {replies.map((reply) => {
        const nestedReplies = getNestedReplies(reply.id);

        return (
          <React.Fragment key={reply.id}>
            <ReplyWrapper>
              <ChatMessage
                content={reply.content}
                sender={users[reply.userId]?.username || reply.userId}
                timestamp={reply.createdAt}
                userId={reply.userId}
                currentUserId={currentUserId}
                onDelete={() => onDelete(reply.id)}
                replyCount={nestedReplies.length}
                isExpanded={reply.isExpanded || false}
                onToggleReplies={() => onToggleReplies(reply.id)}
                onReply={() => {}}
                isReply
              />
            </ReplyWrapper>
            {nestedReplies.length > 0 && (
              <MessageReplies
                parentId={reply.id}
                replies={nestedReplies}
                isExpanded={reply.isExpanded || false}
                onToggleReplies={onToggleReplies}
                onDelete={onDelete}
                currentUserId={currentUserId}
                depth={depth + 1}
              />
            )}
          </React.Fragment>
        );
      })}
    </RepliesContainer>
  );
};

export default MessageReplies;
