import React, { useState } from 'react';
import styled from 'styled-components';

interface MessageOptionsProps {
  onDelete: () => void;
  onReply: () => void;
}

const OptionsContainer = styled.div`
  position: relative;
  display: inline-block;
`;

const MenuTrigger = styled.button`
  background: none;
  border: none;
  color: #999;
  cursor: pointer;
  padding: 0.25rem;
  font-size: 1rem;
  line-height: 1;

  &:hover {
    color: #fff;
  }
`;

const OptionsMenu = styled.div<{ isOpen: boolean }>`
  position: absolute;
  right: 0;
  top: 100%;
  background-color: #2a2a2a;
  border: 1px solid #444;
  border-radius: 4px;
  padding: 0.25rem;
  display: ${({ isOpen }) => isOpen ? 'block' : 'none'};
  z-index: 10;
  min-width: 120px;
`;

const MenuItem = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
  padding: 0.5rem;
  width: 100%;
  text-align: left;
  display: block;

  &:hover {
    background-color: #444;
  }
`;

const MessageOptions: React.FC<MessageOptionsProps> = ({
  onDelete,
  onReply,
}) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleClose = () => {
    setIsOpen(false);
  };

  const handleDelete = () => {
    onDelete();
    handleClose();
  };

  const handleReply = () => {
    onReply();
    handleClose();
  };

  return (
    <OptionsContainer>
      <MenuTrigger onClick={() => setIsOpen(!isOpen)}>â‹®</MenuTrigger>
      <OptionsMenu isOpen={isOpen}>
        <MenuItem onClick={handleReply}>Reply</MenuItem>
        <MenuItem onClick={handleDelete}>Delete</MenuItem>
      </OptionsMenu>
    </OptionsContainer>
  );
};

export default MessageOptions;
