import { WebSocketMessage, WebSocketChannelJoinMessage, WebSocketChannelMessage, StoreMessage } from '../../types';
import { getAuthToken } from '../api/auth';
import { store } from '../../store';
import { addMessage, setReplies } from '../../store/messages/messagesSlice';

// Add WebSocket message types
interface WebSocketJoinMessage {
  type: 'join_channel';
  channel_id: string;
}

interface WebSocketJoinResponse {
  type: 'channel_joined' | 'channel_join_error';
  channelId: string;
  error?: string;
}

class WebSocketService {
  private socket: WebSocket | null = null;
  private messageHandlers: ((message: WebSocketMessage) => void)[] = [];
  private errorHandlers: ((error: Error) => void)[] = [];
  private currentChannelId: string | null = null;
  private joinedChannels: Set<string> = new Set();
  private messageQueue: WebSocketMessage[] = [];
  private reconnectAttempts = 0;
  private maxReconnectAttempts = 5;
  private reconnectTimeout: NodeJS.Timeout | null = null;
  private isReconnecting = false;
  private connectionPromise: Promise<void> | null = null;

  public async connect(channelId: string): Promise<void> {
    // If already connected to the right channel, do nothing
    if (this.isConnected() && this.isConnectedToChannel(channelId)) {
      return;
    }

    // If there's a pending connection, wait for it
    if (this.connectionPromise) {
      await this.connectionPromise;
      if (this.isConnected() && this.isConnectedToChannel(channelId)) {
        return;
      }
    }

    // Reset reconnection state and clear any existing connection
    this.disconnect();
    
    this.currentChannelId = channelId;
    
    try {
      this.connectionPromise = new Promise((resolve, reject) => {
        const token = getAuthToken();
        if (!token) {
          console.log('No authentication token found, skipping WebSocket connection');
          reject(new Error('No authentication token found'));
          return;
        }

        console.log(`Connecting to WebSocket: ws://localhost:8000/ws/chat?token=${token}`);
        this.socket = new WebSocket(`ws://localhost:8000/ws/chat?token=${token}`);

        // Set a connection timeout
        const connectionTimeout = setTimeout(() => {
          if (this.socket?.readyState !== WebSocket.OPEN) {
            console.log('WebSocket connection timeout');
            this.socket?.close();
            reject(new Error('WebSocket connection timeout'));
          }
        }, 5000);

        this.socket.onopen = async () => {
          console.log('WebSocket connection opened');
          clearTimeout(connectionTimeout);
          this.reconnectAttempts = 0;
          this.isReconnecting = false;

          try {
            await this.joinChannel(channelId);
            resolve();
          } catch (error) {
            console.error('Failed to join channel:', error);
            reject(error);
          }
        };

        this.socket.onclose = (event) => {
          console.log(`WebSocket disconnected with code: ${event.code}`);
          clearTimeout(connectionTimeout);
          this.socket = null;
          this.joinedChannels.clear();

          if (event.code !== 1000) {
            if (!this.isReconnecting && this.reconnectAttempts < this.maxReconnectAttempts) {
              console.log('Connection lost, attempting to reconnect...');
              this.attemptReconnect();
            }
          }
        };

        this.socket.onerror = (error) => {
          console.error('WebSocket error:', error);
          clearTimeout(connectionTimeout);
          this.handleError(new Error('WebSocket connection error'));
        };

        this.socket.onmessage = (event) => {
          try {
            const message = JSON.parse(event.data) as WebSocketMessage;
            this.messageHandlers.forEach(handler => handler(message));
          } catch (error) {
            console.error('Error parsing WebSocket message:', error);
          }
        };
      });

      await this.connectionPromise;
    } catch (error) {
      console.error('WebSocket connection failed:', error);
      this.handleError(error instanceof Error ? error : new Error('WebSocket connection failed'));
      throw error;
    } finally {
      this.connectionPromise = null;
    }
  }

  public async joinChannel(channelId: string): Promise<void> {
    const token = getAuthToken();
    if (!token) {
      console.log('No authentication token found, skipping channel join');
      return;
    }

    if (!this.isConnected()) {
      await this.connect(channelId);
      return;
    }

    if (this.isConnectedToChannel(channelId)) {
      return;
    }

    return new Promise((resolve, reject) => {
      const joinMessage: WebSocketJoinMessage = {
        type: 'join_channel',
        channel_id: channelId
      };

      const timeoutId = setTimeout(() => {
        this.socket?.removeEventListener('message', handleJoinResponse);
        reject(new Error('Channel join timeout'));
      }, 5000);

      const handleJoinResponse = (event: MessageEvent) => {
        try {
          const data = JSON.parse(event.data);
          console.log('Received WebSocket message during join:', data);
          
          // Check if this is a channel join response
          if (data.type === 'channel_joined') {
            console.log('Received channel joined response');
            clearTimeout(timeoutId);
            this.joinedChannels.add(channelId);
            this.socket?.removeEventListener('message', handleJoinResponse);
            resolve();
          } else if (data.type === 'channel_join_error') {
            console.log('Received channel join error:', data.error);
            clearTimeout(timeoutId);
            this.socket?.removeEventListener('message', handleJoinResponse);
            reject(new Error(data.error || 'Failed to join channel'));
          } else if (data.type === 'error' && data.message) {
            console.log('Received error message:', data.message);
            clearTimeout(timeoutId);
            this.socket?.removeEventListener('message', handleJoinResponse);
            reject(new Error(data.message));
          }
          // Ignore other message types
        } catch (error) {
          console.log('Error parsing WebSocket message:', error);
          // Don't reject here, just ignore non-JSON messages
        }
      };

      try {
        console.log('Sending join channel message:', joinMessage);
        this.socket?.addEventListener('message', handleJoinResponse);
        this.socket?.send(JSON.stringify(joinMessage));
      } catch (error) {
        console.error('Error sending join message:', error);
        clearTimeout(timeoutId);
        this.socket?.removeEventListener('message', handleJoinResponse);
        reject(error);
      }
    });
  }

  private handleError(error: Error): void {
    console.error('WebSocket error:', error);
    this.errorHandlers.forEach(handler => handler(error));
  }

  public isConnected(): boolean {
    return this.socket !== null && this.socket.readyState === WebSocket.OPEN;
  }

  private isConnectedToChannel(channelId: string): boolean {
    return this.isConnected() && this.joinedChannels.has(channelId);
  }

  public onMessage(handler: (message: WebSocketMessage) => void): () => void {
    this.messageHandlers.push(handler);
    return () => {
      this.messageHandlers = this.messageHandlers.filter(h => h !== handler);
    };
  }

  public onError(handler: (error: Error) => void): () => void {
    this.errorHandlers.push(handler);
    return () => {
      this.errorHandlers = this.errorHandlers.filter(h => h !== handler);
    };
  }

  public disconnect(): void {
    this.isReconnecting = false;
    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }
    if (this.socket) {
      this.socket.close(1000);
      this.socket = null;
    }
    this.joinedChannels.clear();
    this.messageHandlers = [];
    this.errorHandlers = [];
    this.reconnectAttempts = 0;
  }

  private attemptReconnect(): void {
    if (this.isReconnecting || this.reconnectAttempts >= this.maxReconnectAttempts) {
      console.log('Skipping reconnect: already reconnecting or max attempts reached');
      return;
    }

    this.isReconnecting = true;
    this.reconnectAttempts++;

    console.log(`Attempting to reconnect (attempt ${this.reconnectAttempts}/${this.maxReconnectAttempts})...`);

    if (this.currentChannelId) {
      const backoffTime = Math.min(1000 * Math.pow(2, this.reconnectAttempts - 1), 10000);
      
      if (this.reconnectTimeout) {
        clearTimeout(this.reconnectTimeout);
      }

      this.reconnectTimeout = setTimeout(async () => {
        try {
          await this.connect(this.currentChannelId!);
          this.isReconnecting = false;
        } catch (error) {
          console.error('Reconnection failed:', error);
          if (this.reconnectAttempts < this.maxReconnectAttempts) {
            this.isReconnecting = false; // Reset flag to allow next attempt
            this.attemptReconnect();
          } else {
            console.log('Max reconnection attempts reached');
            this.isReconnecting = false;
            this.handleError(new Error('Failed to reconnect after maximum attempts'));
          }
        }
      }, backoffTime);
    } else {
      this.isReconnecting = false;
    }
  }

  public getChatSocketState(): number {
    return this.socket?.readyState || WebSocket.CLOSED;
  }

  public async sendMessage(channelId: string, content: string): Promise<void> {
    // Ensure we're connected to the right channel
    if (!this.isConnectedToChannel(channelId)) {
      console.log('Not connected to the right channel, attempting to connect...');
      try {
        await this.connect(channelId);
      } catch (error) {
        console.error('Failed to connect to channel:', error);
        throw new Error('Failed to send message: Could not connect to channel');
      }
    }

    // Double check connection state after potential reconnection
    if (!this.isConnected()) {
      console.error('WebSocket is still not connected after reconnection attempt');
      throw new Error('WebSocket is not connected');
    }

    if (!this.isConnectedToChannel(channelId)) {
      console.error('Still not connected to the correct channel after reconnection attempt');
      throw new Error('Not connected to the correct channel');
    }

    const message = {
      type: 'message',
      channel_id: channelId,
      content: content
    };

    try {
      console.log('Sending message:', message);
      this.socket!.send(JSON.stringify(message));
    } catch (error) {
      console.error('Failed to send message:', error);
      throw new Error('Failed to send message: ' + (error instanceof Error ? error.message : 'Unknown error'));
    }
  }
}

const wsService = new WebSocketService();
export default wsService;
