export interface Message {
  id: string;
  content: string;
  channelId: string;
  userId: string;
  createdAt: string;
  updatedAt: string;
  reactions: any[];
  attachments: any[];
  replyCount: number;
  isExpanded: boolean;
  parentId?: string;
}

export interface RawMessage {
  id: number;
  content: string;
  channel_id: number;
  sender_id: number;
  created_at: string;
  updated_at?: string;
  reply_count?: number;
  parent_id?: number;
}

export const transformMessage = (msg: RawMessage): Message => ({
  id: msg.id.toString(),
  content: msg.content,
  channelId: msg.channel_id.toString(),
  userId: msg.sender_id.toString(),
  createdAt: new Date(msg.created_at).toISOString(),
  updatedAt: msg.updated_at ? new Date(msg.updated_at).toISOString() : new Date(msg.created_at).toISOString(),
  reactions: [],
  attachments: [],
  replyCount: msg.reply_count || 0,
  isExpanded: false,
  ...(msg.parent_id ? { parentId: msg.parent_id.toString() } : {})
});
