import React from 'react';
import styled, { css } from 'styled-components';

interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'danger';
  size?: 'small' | 'medium' | 'large';
  fullWidth?: boolean;
}

const getVariantStyles = (variant: ButtonProps['variant'] = 'primary') => {
  switch (variant) {
    case 'secondary':
      return css`
        background-color: transparent;
        border: 2px solid ${props => props.theme.colors.secondary};
        color: ${props => props.theme.colors.secondary};

        &:hover:not(:disabled) {
          background-color: ${props => props.theme.colors.secondary};
          color: ${props => props.theme.colors.background};
        }
      `;
    case 'danger':
      return css`
        background-color: transparent;
        border: 2px solid ${props => props.theme.colors.error};
        color: ${props => props.theme.colors.error};

        &:hover:not(:disabled) {
          background-color: ${props => props.theme.colors.error};
          color: ${props => props.theme.colors.background};
        }
      `;
    default:
      return css`
        background-color: transparent;
        border: 2px solid ${props => props.theme.colors.primary};
        color: ${props => props.theme.colors.primary};

        &:hover:not(:disabled) {
          background-color: ${props => props.theme.colors.primary};
          color: ${props => props.theme.colors.background};
        }
      `;
  }
};

const getSizeStyles = (size: ButtonProps['size'] = 'medium') => {
  switch (size) {
    case 'small':
      return css`
        padding: 0.25rem 0.5rem;
        font-size: 0.875rem;
      `;
    case 'large':
      return css`
        padding: 0.75rem 1.5rem;
        font-size: 1.125rem;
      `;
    default:
      return css`
        padding: 0.5rem 1rem;
        font-size: 1rem;
      `;
  }
};

const StyledButton = styled.button<ButtonProps>`
  font-family: 'VT323', monospace;
  cursor: pointer;
  transition: all 0.2s ease;
  text-transform: uppercase;
  position: relative;
  overflow: hidden;
  width: ${props => props.fullWidth ? '100%' : 'auto'};

  ${props => getVariantStyles(props.variant)}
  ${props => getSizeStyles(props.size)}

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 2px ${props => props.theme.colors.background},
                0 0 0 4px ${props => props.theme.colors.primary};
  }

  /* Retro effect on hover */
  &:before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 2px;
    background: linear-gradient(
      90deg,
      transparent,
      ${props => props.theme.colors.primary}
    );
    animation: btn-anim1 1s linear infinite;
  }

  @keyframes btn-anim1 {
    0% {
      left: -100%;
    }
    50%, 100% {
      left: 100%;
    }
  }
`;

const Button: React.FC<ButtonProps> = ({ children, ...props }) => {
  return <StyledButton {...props}>{children}</StyledButton>;
};

export default Button;
