import React from 'react';
import styled from 'styled-components';
import { Channel } from '../../../types';

export interface ChannelListItemProps {
  channel: Channel;
  isActive: boolean;
  onSelect: (channelId: string) => void;
}

const Container = styled.div<{ $isActive: boolean }>`
  padding: 4px 8px;
  cursor: pointer;
  font-family: 'Courier New', monospace;
  background-color: ${props => props.$isActive ? props.theme.colors.hover : 'transparent'};
  color: ${props => props.$isActive ? props.theme.colors.primary : props.theme.colors.text};

  &:hover {
    background-color: ${props => props.theme.colors.hover};
  }
`;

const ChannelName = styled.span`
  margin-left: 4px;
`;

const UnreadCount = styled.span`
  margin-left: 8px;
  color: ${props => props.theme.colors.warning};
`;

const ChannelListItem: React.FC<ChannelListItemProps> = ({ channel, isActive, onSelect }) => {
  const { id, name, unreadCount, is_direct_message, is_public } = channel;

  const getChannelPrefix = () => {
    if (is_direct_message) return '@';
    if (is_public) return '#';
    return 'ðŸ”’';
  };

  return (
    <Container
      $isActive={isActive}
      onClick={() => onSelect(id)}
    >
      {getChannelPrefix()}
      <ChannelName>{name}</ChannelName>
      {unreadCount > 0 && <UnreadCount>({unreadCount})</UnreadCount>}
    </Container>
  );
};

export default ChannelListItem;
