import { RawMessage, Message, StoreMessage } from '../types';

export const transformMessage = (msg: RawMessage | Message): StoreMessage => {
  const id = msg.id.toString();
  const channelId = msg.channel_id.toString();
  const userId = msg.sender_id.toString();
  const createdAt = new Date(msg.created_at).toISOString();
  const updatedAt = msg.updated_at ? new Date(msg.updated_at).toISOString() : createdAt;
  const parentId = msg.parent_id ? msg.parent_id.toString() : undefined;
  
  return {
    id,
    content: msg.content,
    channelId,
    userId,
    reactions: [],
    attachments: [],
    createdAt,
    updatedAt,
    replyCount: msg.reply_count || 0,
    isExpanded: false,
    ...(parentId ? { parentId } : {})
  };
};
