import React from 'react';
import styled from 'styled-components';
import MessageOptions from '../../chat/MessageOptions';
import { StoreMessage } from '../../../types';

export interface ChatMessageProps {
  message: StoreMessage;
  onDelete: () => void;
  onToggleReplies: () => void;
  onReply: () => void;
}

const MessageContainer = styled.div<{ $isReply?: boolean }>`
  font-family: 'Courier New', monospace;
  padding: 2px 0;
  color: #fff;
  position: relative;

  &:hover {
    background-color: #2a2a2a;
  }
`;

const MessageContent = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 8px;
  white-space: pre-wrap;
  word-wrap: break-word;
  position: relative;
  padding-right: 40px; /* Make room for the options menu */
`;

const MessageText = styled.div`
  flex: 1;
  min-width: 0; /* Allow text to wrap */
`;

const OptionsWrapper = styled.div`
  position: absolute;
  right: 8px;
  top: 0;
`;

const Timestamp = styled.span`
  color: #888;
`;

const Sender = styled.span`
  color: #0f0;
  font-weight: bold;
`;

const ReplyCount = styled.button`
  background: none;
  border: none;
  color: #888;
  cursor: pointer;
  font-family: inherit;
  padding: 0 4px;
  font-size: inherit;

  &:hover {
    color: #fff;
  }
`;

const ChatMessage: React.FC<ChatMessageProps> = ({
  message,
  onDelete,
  onToggleReplies,
  onReply,
}) => {
  const {
    content,
    userId,
    createdAt,
    replyCount = 0,
    isExpanded = false,
    isReply = false
  } = message;

  return (
    <MessageContainer $isReply={isReply}>
      <MessageContent>
        <MessageText>
          <span style={{ color: '#888' }}>{`${userId}:`}</span> {content}
        </MessageText>
        <OptionsWrapper>
          <MessageOptions
            onDelete={onDelete}
            onReply={onReply}
            replyCount={replyCount}
            isExpanded={isExpanded}
            onToggleReplies={onToggleReplies}
          />
        </OptionsWrapper>
      </MessageContent>
    </MessageContainer>
  );
};

export default ChatMessage;
