import {
  setActiveChannel,
  setChannels,
  setUsers,
  updateUserStatus
} from '../../../store/chat/chatSlice';
import { addMessage, setMessages } from '../../../store/messages/messagesSlice';
import Button from '../../common/Button';
import UserListItem from '../../chat/UserListItem';
import ChannelListItem from '../../chat/ChannelListItem';
import CreateChannelModal from '../../chat/CreateChannelModal';
import MessageInput from '../../chat/MessageInput';
import MessageList from '../../chat/MessageList';
import SearchBar from '../../common/SearchBar';
import SearchResults from '../../common/SearchResults';
import wsService from '../../../services/websocket';
import { getChannels, getChannelUsers, getChannelMessages, joinChannel } from '../../../services/api/chat';
import { searchAll } from '../../../services/api/search';
import { RootState, WebSocketMessage, StoreMessage, Channel, User, WebSocketChannelMessage, WebSocketStatusMessage, SearchResult, Message } from '../../../types';
import ChannelSettings from '../../chat/ChannelSettings';
import { AppDispatch } from '../../../store';

const MainContainer = styled.div`
  display: flex;
