  font-family: 'VT323', monospace;
`;

const Sidebar = styled.div`
  width: 200px;
  border-right: 2px solid ${props => props.theme.colors.border};
  display: flex;
  flex-direction: column;
`;

const ChannelList = styled.div`
  flex: 1;
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const UserList = styled.div`
  height: 200px;
  padding: 16px;
  border-top: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const ChatArea = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
`;

const ChatHeader = styled.div`
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  display: flex;
  justify-content: space-between;
  align-items: center;

  h1 {
    margin: 0;
    text-transform: uppercase;
  }
`;

const ChatInput = styled.div`
  border-top: 2px solid ${props => props.theme.colors.border};
`;

const LogoutButton = styled.button`
  background: none;
  border: 2px solid ${props => props.theme.colors.error};
  color: ${props => props.theme.colors.error};
  padding: 4px 8px;
  font-family: 'VT323', monospace;
  cursor: pointer;
  text-transform: uppercase;

  &:hover {
    background: ${props => props.theme.colors.error};
    color: ${props => props.theme.colors.background};
  }
`;

const ChannelHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;

  h2 {
    margin: 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const CreateChannelButton = styled(Button)`
  padding: 2px 8px;
  font-size: 0.875rem;
`;

const ChannelGroup = styled.div`
  margin-bottom: 16px;

  &:last-child {
    margin-bottom: 0;
  }
`;

const GroupTitle = styled.h3`
  margin: 0 0 8px 0;
  padding: 4px 8px;
  font-size: 0.875rem;
  text-transform: uppercase;
  color: ${props => props.theme.colors.textLight};
  border-bottom: 1px solid ${props => props.theme.colors.border};
  font-family: 'Courier New', monospace;
`;

const ChannelActions = styled.div`
  display: flex;
  gap: 8px;
`;

const SettingsButton = styled(Button)`
  padding: 2px 8px;
  font-size: 0.875rem;
`;

const SearchContainer = styled.div`
  position: relative;
  margin-right: 16px;
`;

const MainLayout: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [isConnecting, setIsConnecting] = useState(false);
  const [isSearching, setIsSearching] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult | null>(null);
  const [searchError, setSearchError] = useState<string | undefined>();
  const messageListRef = useRef<HTMLDivElement>(null);
  const [selectedMessageId, setSelectedMessageId] = useState<string | null>(null);
  const isChannelSwitching = useRef<boolean>(false);

  const { channels, activeChannelId, users } = useSelector((state: RootState) => ({
    channels: state.chat.channels,
    activeChannelId: state.chat.activeChannelId,
    users: state.chat.users as { [key: string]: User }
  }));
  const activeChannel = channels.find(channel => channel.id === activeChannelId);

  // Helper function to transform messages in chunks
  const transformMessagesInChunks = useCallback((messages: Message[]): StoreMessage[] => {
    // Transform all messages at once but with minimal processing
    return messages.map(msg => ({
      id: msg.id.toString(),
      content: msg.content,
      channelId: msg.channel_id.toString(),
      userId: msg.sender_id.toString(),
      reactions: [],
      attachments: [],
      createdAt: msg.created_at,
      updatedAt: msg.created_at,
      replyCount: 'reply_count' in msg ? msg.reply_count || 0 : 0,
      isExpanded: false,
      ...('parent_id' in msg && msg.parent_id ? { parentId: msg.parent_id.toString() } : {})
    } as StoreMessage));
  }, []);

  // Transform single messages (for WebSocket events)
  const transformToStoreMessage = useCallback((msg: Message | WebSocketChannelMessage['message']): StoreMessage => ({
    id: msg.id.toString(),
    content: msg.content,
    channelId: msg.channel_id.toString(),
    userId: msg.sender_id.toString(),
    reactions: [],
    attachments: [],
    createdAt: msg.created_at,
    updatedAt: msg.created_at,
    replyCount: 'reply_count' in msg ? msg.reply_count || 0 : 0,
    isExpanded: false,
    ...('parent_id' in msg && msg.parent_id ? { parentId: msg.parent_id.toString() } : {})
  }), []);

  // Initial data fetch
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const fetchedChannels = await getChannels();
        dispatch(setChannels(fetchedChannels));

        if (fetchedChannels.length > 0) {
          const channelUsers = await getChannelUsers(fetchedChannels[0].id);
          dispatch(setUsers(channelUsers));
          dispatch(setActiveChannel(fetchedChannels[0].id));
        }
      } catch (error) {
        console.error('Failed to fetch initial data:', error);
      }
    };

    fetchInitialData();

    return () => {
      wsService.disconnect();
    };
  }, [dispatch]);

  // Handle channel switching and WebSocket connection
  useEffect(() => {
    let isMounted = true;
    let connectionTimeout: NodeJS.Timeout;

    const connectToChannel = async () => {
      if (!activeChannelId || isConnecting) return;

      setIsConnecting(true);
      console.log('[DEBUG] Connecting to channel:', activeChannelId);

      try {
        // First disconnect from any existing connection
        wsService.disconnect();

        // Connect to the new channel with retries
        let retryCount = 0;
        const maxRetries = 3;

        while (retryCount < maxRetries) {
          try {
            await wsService.connect(activeChannelId);
            if (!isMounted) return;

            await wsService.joinChannel(activeChannelId);
            if (!isMounted) return;

            console.log('[DEBUG] Successfully connected to channel:', activeChannelId);
            break;
          } catch (error) {
            retryCount++;
            console.log(`Connection attempt ${retryCount} failed:`, error);
            if (retryCount === maxRetries) throw error;
            await new Promise(resolve => setTimeout(resolve, Math.min(1000 * Math.pow(2, retryCount), 5000)));
          }
        }

        // After successful connection, fetch channel data
        console.log('[DEBUG] Fetching channel data...');
        const [channelUsers, channelMessages] = await Promise.all([
          getChannelUsers(activeChannelId),
          getChannelMessages(activeChannelId)
        ]);

        if (!isMounted) return;

        console.log('[DEBUG] Setting users:', channelUsers);
        dispatch(setUsers(channelUsers));

        // Transform and set messages
        console.log('[DEBUG] Processing messages:', channelMessages.length);
        const transformedMessages = channelMessages.map(transformToStoreMessage);
        console.log('[DEBUG] Setting messages for channel:', activeChannelId, transformedMessages);
        dispatch(setMessages({ channelId: activeChannelId, messages: transformedMessages }));

        // Scroll to bottom after messages are loaded
        requestAnimationFrame(() => {
          if (messageListRef.current) {
            messageListRef.current.scrollTop = messageListRef.current.scrollHeight;
          }
        });
      } catch (error) {
        console.error('[DEBUG] Error connecting to channel:', error);
        if (isMounted) {
          dispatch(setMessages({ channelId: activeChannelId, messages: [] }));
        }
      } finally {
        if (isMounted) {
          setIsConnecting(false);
        }
      }
    };

    if (activeChannelId) {
      connectionTimeout = setTimeout(() => {
        connectToChannel();
      }, 100);
    }

    return () => {
      isMounted = false;
      clearTimeout(connectionTimeout);
    };
  }, [activeChannelId, dispatch, isConnecting, transformToStoreMessage]);

  // Handle WebSocket messages
  useEffect(() => {
    const handleWebSocketMessage = (message: WebSocketMessage) => {
      console.log('Received WebSocket message:', message);

      // Handle broadcast messages
      if (message.type === 'message' || message.type === 'new_reply') {
        // Type guard to ensure we have a channel message
        const isChannelMessage = (msg: WebSocketMessage): msg is WebSocketChannelMessage => {
          return (msg.type === 'message' || msg.type === 'new_reply') && 'message' in msg;
        };

        if (!isChannelMessage(message)) {
          console.error('Invalid message format:', message);
          return;
        }

        try {
          const storeMessage = transformToStoreMessage(message.message);
          if (message.type === 'new_reply' && message.parentId) {
            storeMessage.parentId = message.parentId.toString();
          }

          console.log('Dispatching message to store:', storeMessage);
          dispatch(addMessage(storeMessage));

          // If this is a new message and we're at the bottom, scroll to it
          if (messageListRef.current) {
            const { scrollTop, scrollHeight, clientHeight } = messageListRef.current;
            const isAtBottom = scrollTop + clientHeight >= scrollHeight - 50;
            if (isAtBottom) {
              setTimeout(() => {
                if (messageListRef.current) {
                  messageListRef.current.scrollTop = messageListRef.current.scrollHeight;
                }
              }, 100);
            }
          }
        } catch (error) {
          console.error('Error handling WebSocket message:', error);
        }
      }

      // Handle status messages
      if (message.type === 'user_status' || message.type === 'presence_update') {
        const isStatusMessage = (msg: WebSocketMessage): msg is WebSocketStatusMessage => {
          return (msg.type === 'user_status' || msg.type === 'presence_update') && 'user_id' in msg && 'status' in msg;
        };

        if (!isStatusMessage(message)) {
          console.error('Invalid status message format:', message);
          return;
        }

        dispatch(updateUserStatus({
          userId: message.user_id.toString(),
          status: message.status
        }));
      }
    };

    const unsubscribe = wsService.onMessage(handleWebSocketMessage);
    return () => unsubscribe();
  }, [dispatch, transformToStoreMessage]);

  // Handle search
  const handleSearch = async (query: string) => {
    if (!query.trim()) {
      setSearchResults(null);
      return;
    }

    setIsSearching(true);
    setSearchError(undefined);

    try {
      const results = await searchAll(query);
      setSearchResults(results);
    } catch (error) {
      console.error('Search failed:', error);
      setSearchError('Failed to perform search');
    } finally {
      setIsSearching(false);
    }
  };

  // Handle channel selection
  const handleChannelClick = async (channelId: string) => {
    if (channelId === activeChannelId) return;
    if (isChannelSwitching.current) {
      console.log('[DEBUG] Channel switch already in progress, ignoring request');
      return;
    }

    const previousChannelId = activeChannelId;
    isChannelSwitching.current = true;

    try {
      const channel = channels.find(c => c.id === channelId);
      if (!channel) {
        console.error('Channel not found:', channelId);
        return;
      }

      // Update UI immediately and let the WebSocket effect handle the connection
      dispatch(setActiveChannel(channelId));
      dispatch(setMessages({ channelId, messages: [] }));
    } catch (error) {
      console.error('Error switching channel:', error);
      if (previousChannelId) {
        dispatch(setActiveChannel(previousChannelId));
      }
    } finally {
      isChannelSwitching.current = false;
    }
  };

  // Handle message selection from search results
  const handleSelectMessage = async (channelId: string, messageId: string) => {
    try {
      // Switch to the channel if it's different
      if (channelId !== activeChannelId) {
        // Clear current messages and show loading state
        dispatch(setMessages({ channelId, messages: [] }));
        dispatch(setActiveChannel(channelId));

        // Start channel switch in background
        handleChannelClick(channelId).catch(error => {
          console.error('Error switching channel:', error);
        });
      }

      // Set the selected message ID immediately
      setSelectedMessageId(messageId);
      setSearchResults(null);

      // Scroll to message using requestAnimationFrame for better performance
      const scrollToMessage = () => {
        const messageElement = document.getElementById(`message-${messageId}`);
        if (messageElement) {
          messageElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
          messageElement.classList.add('highlight');
          setTimeout(() => messageElement.classList.remove('highlight'), 2000);
        } else {
          // If element not found yet, try again in next frame
          requestAnimationFrame(scrollToMessage);
        }
      };
      requestAnimationFrame(scrollToMessage);
    } catch (error) {
      console.error('Error navigating to message:', error);
    }
  };

  // Handle logout
  const handleLogout = () => {
    dispatch(logout());
  };

  // Sort and group channels
  const sortedChannels = [...channels].sort((a, b) => {
    // Sort by public/private first
    if (a.is_public !== b.is_public) {
      return a.is_public ? -1 : 1;
    }
    // Then sort by name
    return a.name.localeCompare(b.name);
  });

  const publicChannels = sortedChannels.filter(channel => channel.is_public && !channel.is_direct_message);
  const privateChannels = sortedChannels.filter(channel => !channel.is_public && !channel.is_direct_message);
  const directMessages = sortedChannels.filter(channel => channel.is_direct_message);

  // Get messages for the active channel
  const messages = useSelector((state: RootState) => {
    console.log('[DEBUG] MainLayout - Current Redux state:', state);
    const channelMessages = state.messages.messagesByChannel[activeChannelId || ''] || [];
    console.log('[DEBUG] MainLayout - Selected messages for channel:', activeChannelId, channelMessages);
    return channelMessages;
  });

  return (
    <MainContainer>
      <Sidebar>
        <ChannelList>
          <ChannelHeader>
            <h2>Channels</h2>
            <CreateChannelButton
              variant="secondary"
              size="small"
              onClick={() => setIsCreateModalOpen(true)}
            >
              +New
            </CreateChannelButton>
          </ChannelHeader>

          {publicChannels.length > 0 && (
            <ChannelGroup>
              <GroupTitle>Public Channels</GroupTitle>
              {publicChannels.map((channel: Channel) => (
                <ChannelListItem
                  key={channel.id}
                  name={channel.name}
                  isActive={channel.id === activeChannelId}
                  hasUnread={channel.unreadCount > 0}
                  isDirect={channel.is_direct_message}
                  isPublic={channel.is_public}
                  onClick={() => handleChannelClick(channel.id)}
                />
              ))}
            </ChannelGroup>
          )}

          {privateChannels.length > 0 && (
            <ChannelGroup>
              <GroupTitle>Private Channels</GroupTitle>
              {privateChannels.map((channel: Channel) => (
                <ChannelListItem
                  key={channel.id}
                  name={channel.name}
                  isActive={channel.id === activeChannelId}
                  hasUnread={channel.unreadCount > 0}
                  isDirect={channel.is_direct_message}
                  isPublic={channel.is_public}
                  onClick={() => handleChannelClick(channel.id)}
                />
              ))}
            </ChannelGroup>
          )}

          {directMessages.length > 0 && (
            <ChannelGroup>
              <GroupTitle>Direct Messages</GroupTitle>
              {directMessages.map((channel: Channel) => (
                <ChannelListItem
                  key={channel.id}
                  name={channel.name}
                  isActive={channel.id === activeChannelId}
                  hasUnread={channel.unreadCount > 0}
                  isDirect={channel.is_direct_message}
                  isPublic={channel.is_public}
                  onClick={() => handleChannelClick(channel.id)}
                />
              ))}
            </ChannelGroup>
          )}
        </ChannelList>
        <UserList>
          <h2>Online Users</h2>
          {Object.values(users).map((user) => (
            <UserListItem
              key={user.id}
              username={user.username}
              status={user.status}
            />
          ))}
        </UserList>
      </Sidebar>
      <ChatArea>
        <ChatHeader>
          <h1>{activeChannel?.name || 'Select a Channel'}</h1>
          <ChannelActions>
            <SearchContainer>
              <SearchBar 
                onSearch={handleSearch}
                placeholder="Search messages, files, and channels..."
              />
              {searchResults && (
                <SearchResults
                  results={searchResults}
                  isLoading={isSearching}
                  onClose={() => setSearchResults(null)}
                  onSelectChannel={(channelId) => {
                    dispatch(setActiveChannel(channelId));
                    setSearchResults(null);
                  }}
                  onSelectMessage={handleSelectMessage}
                  onSelectFile={(fileId) => {
                    // TODO: Implement file selection
                    console.log('Selected file:', fileId);
                    setSearchResults(null);
                  }}
                />
              )}
            </SearchContainer>
            {activeChannel && !activeChannel.is_direct_message && (
              <SettingsButton onClick={() => setIsSettingsOpen(true)}>
                Settings
              </SettingsButton>
            )}
            <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
          </ChannelActions>
        </ChatHeader>

        <MessageList
          ref={messageListRef}
          messages={messages}
          selectedMessageId={selectedMessageId}
        />

        <ChatInput>
          <MessageInput channelId={activeChannelId || ''} />
        </ChatInput>
      </ChatArea>
      {isCreateModalOpen && (
        <CreateChannelModal
          isOpen={isCreateModalOpen}
          onClose={() => setIsCreateModalOpen(false)}
        />
      )}
      {isSettingsOpen && activeChannel && (
        <ChannelSettings
          isOpen={isSettingsOpen}
          onClose={() => setIsSettingsOpen(false)}
          channel={activeChannel}
        />
      )}
    </MainContainer>
  );
};

export default MainLayout;
