import React, { useEffect, useRef, forwardRef, useState, useCallback, useMemo } from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import { RootState, StoreMessage, User, WebSocketMessage, RawMessage, isMessageData } from '../../../types';
import type { Message } from '../../../types';
import { transformMessage } from '../../../utils/messageTransform';
import MessageComponent from '../Message';
import MessageReplies from '../MessageReplies';
import ReplyModal from '../ReplyModal';
import { addMessage, updateMessage, deleteMessage, setReplies, toggleExpanded, setExpanded } from '../../../store/messages/messagesSlice';
import { deleteMessage as deleteMessageApi, getReplies as getRepliesApi } from '../../../services/api';

const MessageListContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 16px;
  overflow-y: auto;
  height: 100%;
`;

const MessageWrapper = styled.div<{ $isSelected?: boolean }>`
  display: flex;
  flex-direction: column;
  gap: 4px;
  background-color: ${props => props.$isSelected ? props.theme.colors.hover : 'transparent'};
  border-radius: 4px;
  padding: 4px;
`;

interface MessageListProps {
  messages: StoreMessage[];
  selectedMessageId?: number;
}

const MessageList: React.FC<MessageListProps> = ({ messages, selectedMessageId }) => {
  const dispatch = useDispatch();
  const users = useSelector((state: RootState) => state.users.users);
  const currentUser = useSelector((state: RootState) => state.auth.user);
  const activeChannelId = useSelector((state: RootState) => state.channels.activeChannelId);
  const selectedMessageRef = useRef<HTMLDivElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const loadedRepliesRef = useRef<Set<number>>(new Set());
  const [replyModalOpen, setReplyModalOpen] = useState(false);
  const [selectedMessage, setSelectedMessage] = useState<StoreMessage | null>(null);

  const handleReply = (message: StoreMessage) => {
    setSelectedMessage(message);
    setReplyModalOpen(true);
  };

  const handleDelete = async (messageId: number) => {
    try {
      await deleteMessageApi(messageId);
      if (activeChannelId) {
        dispatch(deleteMessage({ channelId: Number(activeChannelId), messageId }));
      }
    } catch (error) {
      console.error('Error deleting message:', error);
    }
  };

  const handleLoadReplies = async (messageId: number) => {
    if (!loadedRepliesRef.current.has(messageId) && activeChannelId) {
      try {
        const replies = await getRepliesApi(messageId);
        const transformedReplies = replies.map(transformMessage);
        dispatch(setReplies({ 
          channelId: Number(activeChannelId), 
          parentMessageId: messageId, 
          replies: transformedReplies 
        }));
        loadedRepliesRef.current.add(messageId);
      } catch (error) {
        console.error('Error loading replies:', error);
      }
    }
  };

  const handleToggleReplies = (messageId: number) => {
    if (activeChannelId) {
      dispatch(toggleExpanded({ channelId: Number(activeChannelId), messageId }));
    }
  };

  return (
    <MessageListContainer ref={containerRef}>
      {messages.map((message) => (
        <MessageWrapper
          key={message.id}
          $isSelected={message.id === selectedMessageId}
          ref={message.id === selectedMessageId ? selectedMessageRef : undefined}
        >
          <MessageComponent
            content={message.content}
            sender={users[message.userId]?.username || message.userId.toString()}
            timestamp={message.createdAt}
            currentUserId={currentUser?.id ? Number(currentUser.id) : undefined}
            userId={message.userId}
            onReply={() => handleReply(message)}
            onDelete={() => handleDelete(message.id)}
            replyCount={message.replyCount}
            isExpanded={message.isExpanded}
            onToggleReplies={() => handleToggleReplies(message.id)}
          />
          {message.replyCount > 0 && (
            <MessageReplies
              parentId={message.id}
              replies={message.replies || []}
              isExpanded={message.isExpanded}
              onToggleReplies={() => handleToggleReplies(message.id)}
              onDelete={handleDelete}
              currentUserId={currentUser?.id ? Number(currentUser.id) : undefined}
            />
          )}
        </MessageWrapper>
      ))}
      {selectedMessage && (
        <ReplyModal
          isOpen={replyModalOpen}
          onClose={() => setReplyModalOpen(false)}
          parentMessage={selectedMessage}
        />
      )}
    </MessageListContainer>
  );
};

export default MessageList;
