import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import ChatMessage from '../../common/ChatMessage';
import { getChannelMessages } from '../../../services/api/chat';
import { fetchMessagesSuccess } from '../../../store/messages/messagesSlice';
import { RootState } from '../../../store/rootReducer';

const MessageListContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  display: flex;
  flex-direction: column-reverse; // Makes messages start from bottom
  padding: 16px;
  gap: 2px;
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 8px;
  color: ${props => props.theme.colors.textSecondary};
  font-family: 'Courier New', monospace;
  font-style: italic;
`;

const NoMessagesMessage = styled.div`
  text-align: center;
  padding: 20px;
  color: ${props => props.theme.colors.secondary};
  font-family: 'Courier New', monospace;
`;

interface MessageListProps {
  channelId: number | null;
}

const MESSAGES_PER_PAGE = 30;

const MessageList: React.FC<MessageListProps> = ({ channelId }) => {
  const dispatch = useDispatch();
  const containerRef = useRef<HTMLDivElement>(null);
  const [isLoadingMore, setIsLoadingMore] = useState(false);
  const [hasMoreMessages, setHasMoreMessages] = useState(true);

  console.log('MessageList render:', { channelId, isLoadingMore, hasMoreMessages });

  const messages = useSelector((state: RootState) => {
    if (!channelId || !state.messages?.messagesByChannel) return [];
    return state.messages.messagesByChannel[String(channelId)] || [];
  });
  
  const users = useSelector((state: RootState) => state.chat?.users || {});

  console.log('Current state:', { 
    channelId, 
    messagesCount: messages.length,
    users: Object.keys(users).length,
    messages,
    users 
  });

  // Initial message load
  useEffect(() => {
    console.log('MessageList useEffect triggered:', { channelId });
    
    if (channelId) {
      console.log('Starting to fetch messages for channel:', channelId);
      setIsLoadingMore(true);
      setHasMoreMessages(true);
      
      getChannelMessages(channelId, MESSAGES_PER_PAGE)
        .then(newMessages => {
          console.log('Received messages from API:', newMessages);
          if (newMessages.length < MESSAGES_PER_PAGE) {
            console.log('No more messages available');
            setHasMoreMessages(false);
          }
          dispatch(fetchMessagesSuccess({ 
            channelId: String(channelId), 
            messages: newMessages 
          }));
        })
        .catch(error => {
          console.error('Failed to fetch messages:', error);
        })
        .finally(() => {
          console.log('Finished fetching messages');
          setIsLoadingMore(false);
        });
    }
  }, [channelId, dispatch]);

  if (!channelId) {
    console.log('No channel selected');
    return (
      <MessageListContainer>
        <NoMessagesMessage>Select a channel to view messages</NoMessagesMessage>
      </MessageListContainer>
    );
  }

  if (isLoadingMore && messages.length === 0) {
    console.log('Loading initial messages...');
    return (
      <MessageListContainer>
        <LoadingMessage>Loading messages...</LoadingMessage>
      </MessageListContainer>
    );
  }

  if (!isLoadingMore && messages.length === 0) {
    console.log('No messages found');
    return (
      <MessageListContainer>
        <NoMessagesMessage>No messages yet</NoMessagesMessage>
      </MessageListContainer>
    );
  }

  console.log('Rendering messages:', messages);

  return (
    <MessageListContainer ref={containerRef}>
      {messages.map(message => {
        const user = users[message.sender_id];
        console.log('Rendering message:', { message, user });
        return (
          <ChatMessage
            key={message.id}
            content={message.content}
            sender={user?.username || 'Unknown User'}
            timestamp={message.created_at}
            isSystem={message.is_system}
          />
        );
      })}
    </MessageListContainer>
  );
};

export default MessageList;
