import React, { useState } from 'react';
import styled from 'styled-components';
import { FileResponse, getMessageFiles } from '../../../services/api/files';
import FilePreview from '../FilePreview';

export interface Reaction {
  emoji: string;
  count: number;
  hasOwn: boolean;
  userId: string;
}

export interface ChatMessageProps {
  id: number;
  content: string;
  sender: string;
  timestamp: string;
  userId: string;
  currentUserId?: string;
  reactions: Reaction[];
  attachments: FileResponse[];
  has_attachments: boolean;
  onReply?: (messageId: number) => void;
  onReact?: (messageId: number, emoji: string) => void;
  onDelete?: (messageId: number) => void;
}

const MessageContainer = styled.div`
  padding: 8px;
  margin: 4px 0;
  border-radius: 4px;
  background: ${({ theme }) => theme.colors.background};
  &:hover {
    background: ${({ theme }) => theme.colors.backgroundHover};
  }
`;

const MessageContent = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
`;

const MessageHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
`;

const SenderName = styled.span`
  font-weight: bold;
  color: ${({ theme }) => theme.colors.primary};
`;

const Timestamp = styled.span`
  color: ${({ theme }) => theme.colors.textDim};
  font-size: 0.8em;
`;

const MessageText = styled.p`
  margin: 0;
  white-space: pre-wrap;
  word-break: break-word;
`;

interface AttachmentIconProps {
  isLoading: boolean;
}

const AttachmentIcon = styled.button<AttachmentIconProps>`
  background: none;
  border: none;
  color: ${({ theme }) => theme.colors.textDim};
  cursor: ${({ isLoading }) => isLoading ? 'wait' : 'pointer'};
  padding: 4px;
  font-size: 1em;
  &:hover {
    color: ${({ theme }) => theme.colors.text};
  }
`;

const AttachmentsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-top: 8px;
`;

const ReactionsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 4px;
  margin-top: 4px;
`;

interface ReactionBadgeProps {
  $hasOwn: boolean;
}

const ReactionBadge = styled.button<ReactionBadgeProps>`
  background: ${({ $hasOwn }) => $hasOwn ? '#2a2a2a' : 'none'};
  border: 1px solid #444;
  color: ${({ theme }) => theme.colors.text};
  padding: 2px 6px;
  border-radius: 12px;
  cursor: pointer;
  font-size: 0.9em;
  display: flex;
  align-items: center;
  gap: 4px;
  &:hover {
    background: #2a2a2a;
  }
`;

const ErrorMessage = styled.div`
  color: ${({ theme }) => theme.colors.error};
  font-size: 0.9em;
  margin-top: 4px;
`;

export const ChatMessage: React.FC<ChatMessageProps> = ({
  id,
  content,
  sender,
  timestamp,
  userId,
  currentUserId,
  reactions,
  attachments,
  has_attachments,
  onReply,
  onReact,
  onDelete
}): JSX.Element => {
  const [showAttachments, setShowAttachments] = useState(false);
  const [isLoadingFiles, setIsLoadingFiles] = useState(false);
  const [attachmentError, setAttachmentError] = useState<string | null>(null);
  const [messageFiles, setMessageFiles] = useState<FileResponse[]>(attachments || []);

  const handleAttachmentToggle = async () => {
    setShowAttachments(!showAttachments);
    if (!showAttachments && has_attachments && messageFiles.length === 0) {
      setIsLoadingFiles(true);
      setAttachmentError(null);
      try {
        const files = await getMessageFiles(id);
        setMessageFiles(files);
      } catch (error) {
        console.error('Error loading files:', error);
        setAttachmentError('Failed to load attachments');
      } finally {
        setIsLoadingFiles(false);
      }
    }
  };

  const groupedReactions = reactions.reduce<Record<string, Reaction>>((acc, reaction) => {
    acc[reaction.emoji] = reaction;
    return acc;
  }, {});

  const handleReactionClick = (emoji: string) => {
    if (onReact) {
      onReact(id, emoji);
    }
  };

  return (
    <MessageContainer>
      <MessageContent>
        <MessageHeader>
          <SenderName>{sender}</SenderName>
          <Timestamp>{timestamp}</Timestamp>
        </MessageHeader>
        <MessageText>{content}</MessageText>
        {has_attachments && (
          <AttachmentIcon
            isLoading={isLoadingFiles}
            onClick={handleAttachmentToggle}
            title={isLoadingFiles ? 'Loading attachments...' : 'Show attachments'}
          >
            ðŸ“Ž
          </AttachmentIcon>
        )}
        {attachmentError && <ErrorMessage>{attachmentError}</ErrorMessage>}
        {showAttachments && messageFiles.length > 0 && (
          <AttachmentsContainer>
            {messageFiles.map(attachment => (
              <FilePreview
                key={attachment.id}
                filename={attachment.filename}
                fileType={attachment.file_type}
                filePath={attachment.file_path}
                fileSize={attachment.file_size}
              />
            ))}
          </AttachmentsContainer>
        )}
      </MessageContent>
      {Object.entries(groupedReactions).length > 0 && (
        <ReactionsContainer>
          {Object.entries(groupedReactions).map(([emoji, reaction]) => (
            <ReactionBadge
              key={emoji}
              onClick={() => handleReactionClick(emoji)}
              $hasOwn={reaction.hasOwn}
            >
              {emoji} {reaction.count}
            </ReactionBadge>
          ))}
        </ReactionsContainer>
      )}
    </MessageContainer>
  );
};

export default ChatMessage;
