import React from 'react';
import styled from 'styled-components';
import { Channel } from '../../../types';

interface ChannelListItemProps {
  channel: Channel;
  isActive: boolean;
  onClick: () => void;
}

const Container = styled.div<{ isActive: boolean }>`
  padding: 8px;
  cursor: pointer;
  background-color: ${props => props.isActive ? props.theme.colors.primary : 'transparent'};
  color: ${props => props.isActive ? props.theme.colors.background : props.theme.colors.text};
  border-left: 2px solid ${props => props.isActive ? props.theme.colors.accent : 'transparent'};

  &:hover {
    background-color: ${props => props.isActive ? props.theme.colors.primary : props.theme.colors.hover};
  }
`;

const ChannelName = styled.span`
  font-family: 'DOS VGA 437', monospace;
`;

const Prefix = styled.span`
  margin-right: 4px;
  opacity: 0.7;
`;

const ChannelListItem: React.FC<ChannelListItemProps> = ({ channel, isActive, onClick }) => {
  const prefix = channel.is_direct_message ? '@' : '#';
  
  return (
    <Container isActive={isActive} onClick={onClick}>
      <Prefix>{prefix}</Prefix>
      <ChannelName>{channel.name}</ChannelName>
    </Container>
  );
};

export default ChannelListItem;
