import 'styled-components';

declare module 'styled-components' {
  export interface DefaultTheme {
    colors: {
      primary: string;
      secondary: string;
      background: string;
      backgroundDark: string;
      text: string;
      textSecondary: string;
      textDim: string;
      border: string;
      hover: string;
      success: string;
      warning: string;
      error: string;
      disabled: string;
      accent: string;
    };
    zIndex: {
      modal: number;
      dropdown: number;
      tooltip: number;
    };
  }
}

export const theme = {
  colors: {
    primary: '#00FF00',
    secondary: '#008800',
    background: '#000000',
    backgroundDark: '#001100',
    text: '#33FF33',
    textSecondary: '#008800',
    textDim: '#004400',
    border: '#33FF33',
    hover: '#001100',
    success: '#00FF00',
    warning: '#FFFF00',
    error: '#FF0000',
    disabled: '#333333',
    accent: '#0000FF'
  },
  zIndex: {
    modal: 1000,
    dropdown: 100,
    tooltip: 200
  }
};
