import React, { useEffect, useRef, forwardRef } from 'react';
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { RootState, StoreMessage, User } from '../../../types';
import Message from '../Message';
import MessageReplies from '../MessageReplies';

interface MessageListProps {
  messages: StoreMessage[];
  selectedMessageId?: string | null;
}

const MessageListContainer = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 16px;
  display: flex;
  flex-direction: column;
  height: 100%;
`;

const MessagesWrapper = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-top: auto;
`;

const LoadingIndicator = styled.div`
  text-align: center;
  padding: 8px;
  color: ${props => props.theme.colors.textLight};
  font-family: 'Courier New', monospace;
`;

const MessageWrapper = styled.div<{ $isSelected?: boolean }>`
  transition: all 0.3s ease;
  padding: 4px;
  border-radius: 4px;
  background-color: ${props => props.$isSelected ? '#3a3a3a' : 'transparent'};
  border-left: ${props => props.$isSelected ? '2px solid #666' : '2px solid transparent'};

  &.highlight {
    animation: flash 1s;
  }

  @keyframes flash {
    0% { background-color: #4a4a4a; }
    100% { background-color: ${props => props.$isSelected ? '#3a3a3a' : 'transparent'}; }
  }
`;

const MessageList = forwardRef<HTMLDivElement, MessageListProps>((props, ref) => {
  const { messages, selectedMessageId } = props;
  const containerRef = useRef<HTMLDivElement>(null);
  const highlightTimeoutRef = useRef<NodeJS.Timeout>();

  const { currentUser, users } = useSelector((state: RootState) => ({
    currentUser: state.auth.user,
    users: state.chat.users as { [key: string]: User }
  }));

  // Scroll to selected message with highlight effect
  useEffect(() => {
    if (selectedMessageId && containerRef.current) {
      const messageElement = containerRef.current.querySelector(`[data-message-id="${selectedMessageId}"]`) as HTMLElement;
      if (messageElement) {
        // Scroll the message into view
        messageElement.scrollIntoView({
          behavior: 'smooth',
          block: 'center'
        });

        // Add highlight effect
        messageElement.classList.add('highlight');

        // Clear any existing timeout
        if (highlightTimeoutRef.current) {
          clearTimeout(highlightTimeoutRef.current);
        }

        // Remove highlight class after animation
        highlightTimeoutRef.current = setTimeout(() => {
          messageElement.classList.remove('highlight');
        }, 1000);
      }
    }

    // Cleanup
    return () => {
      if (highlightTimeoutRef.current) {
        clearTimeout(highlightTimeoutRef.current);
      }
    };
  }, [selectedMessageId]);

  return (
    <MessageListContainer ref={containerRef}>
      <MessagesWrapper>
        {messages.map(message => (
          <MessageWrapper
            key={message.id}
            $isSelected={message.id === selectedMessageId}
            data-message-id={message.id}
          >
            <Message
              message={message}
              currentUser={currentUser}
              users={users}
            />
            {message.isExpanded && message.replies && (
              <MessageReplies
                replies={message.replies}
                users={users}
                currentUser={currentUser}
              />
            )}
          </MessageWrapper>
        ))}
      </MessagesWrapper>
    </MessageListContainer>
  );
});

MessageList.displayName = 'MessageList';

export default MessageList;
