import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useSelector, useDispatch } from 'react-redux';
import { StoreMessage, RootState } from '../../../types';
import { transformMessage } from '../../../utils/messageTransform';
import Message from '../Message';
import { setReplies } from '../../../store/messages/messagesSlice';
import { getReplies } from '../../../services/api/chat';

interface MessageRepliesProps {
  parentId: number;
  replies: StoreMessage[];
  isExpanded: boolean;
  onToggleReplies: (messageId: number) => void;
  onDelete: (messageId: number) => void;
  currentUserId?: string;
}

const RepliesContainer = styled.div`
  padding-left: 24px;
  margin-top: 4px;
  display: flex;
  flex-direction: column;
  gap: 4px;
`;

const ReplyWrapper = styled.div`
  padding: 4px;
  border-radius: 4px;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: ${props => props.theme.colors.hover};
  }
`;

const MessageReplies: React.FC<MessageRepliesProps> = ({
  parentId,
  replies,
  isExpanded,
  onToggleReplies,
  onDelete,
  currentUserId
}) => {
  const users = useSelector((state: RootState) => state.chat.users);

  return (
    <RepliesContainer>
      {replies.map(reply => (
        <ReplyWrapper key={reply.id}>
          <Message
            content={reply.content}
            sender={users[reply.userId]?.username || reply.userId.toString()}
            timestamp={reply.createdAt}
            userId={reply.userId.toString()}
            currentUserId={currentUserId}
            onDelete={() => onDelete(reply.id)}
            replyCount={0}
            isExpanded={false}
            onToggleReplies={() => {}}
            onReply={() => {}}
          />
        </ReplyWrapper>
      ))}
    </RepliesContainer>
  );
};

export default MessageReplies;
