import React, { useState, KeyboardEvent } from 'react';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';
import wsService from '../../../services/websocket';
import { sendMessage } from '../../../services/api/chat';
import { addMessage } from '../../../store/messages/messagesSlice';
import { Message as ApiMessage } from '../../../types';
import { Message as StoreMessage } from '../../../store/types';

interface MessageInputProps {
  channelId: number;
}

const InputContainer = styled.div`
  padding: 16px 32px;
  background: ${props => props.theme.colors.background};
  display: flex;
  justify-content: center;
`;

const StyledInput = styled.input`
  width: 100%;
  max-width: 800px;
  padding: 12px 16px;
  background: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  border: 2px solid ${props => props.theme.colors.border};
  border-radius: 4px;
  font-family: 'Courier New', monospace;
  font-size: 14px;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
    box-shadow: 0 0 0 1px ${props => props.theme.colors.primary};
  }

  &::placeholder {
    color: ${props => props.theme.colors.textSecondary};
  }
`;

// Transform API message to store message format
const transformMessage = (msg: ApiMessage): StoreMessage => ({
  id: String(msg.id),
  content: msg.content,
  channelId: String(msg.channel_id),
  userId: String(msg.sender_id),
  reactions: [],
  createdAt: msg.created_at,
  updatedAt: msg.created_at,
});

const MessageInput: React.FC<MessageInputProps> = ({ channelId }) => {
  const [message, setMessage] = useState('');
  const [isSending, setIsSending] = useState(false);
  const dispatch = useDispatch();

  const handleSendMessage = async (content: string) => {
    if (isSending || !content.trim()) return;

    try {
      setIsSending(true);
      console.log('Sending message:', {
        channelId,
        content: content.trim(),
        wsReadyState: wsService.getChatSocketState()
      });
      
      // Send via REST API
      const sentMessage = await sendMessage(channelId, content.trim());
      dispatch(addMessage(transformMessage(sentMessage)));
      
      // Also send via WebSocket for real-time updates
      wsService.sendMessage(channelId, content.trim());
      
      setMessage('');
    } catch (error) {
      console.error('Failed to send message:', error);
    } finally {
      setIsSending(false);
    }
  };

  const handleKeyPress = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' && message.trim()) {
      handleSendMessage(message);
    }
  };

  return (
    <InputContainer>
      <StyledInput
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="> Type your message and press Enter..."
        aria-label="Message input"
        disabled={isSending}
      />
    </InputContainer>
  );
};

export default MessageInput;
