import React, { useState, useCallback, useMemo, useRef } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { RootState, SearchResult } from '../../../types';
import { setActiveChannel } from '../../../store/chat/chatSlice';
import { setSelectedMessageId } from '../../../store/messages/messagesSlice';
import { logout } from '../../../store/auth/authSlice';
import { searchAll } from '../../../services/api/search';
import MessageList from '../../chat/MessageList';
import SearchBar from '../../common/SearchBar';
import SearchResults from '../../common/SearchResults';
import CreateChannelModal from '../../modals/CreateChannelModal';
import ChannelSettings from '../../modals/ChannelSettings';
import ChannelListItem from '../../common/ChannelListItem';
import UserListItem from '../../common/UserListItem';

const MainContainer = styled.div`
  display: flex;
  height: 100vh;
  background-color: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  font-family: 'VT323', monospace;
`;

const Sidebar = styled.div`
  width: 200px;
  border-right: 2px solid ${props => props.theme.colors.border};
  display: flex;
  flex-direction: column;
`;

const ChannelList = styled.div`
  flex: 1;
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const UserList = styled.div`
  height: 200px;
  padding: 16px;
  border-top: 2px solid ${props => props.theme.colors.border};
  overflow-y: auto;

  h2 {
    margin: 0 0 16px 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const ChatArea = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
`;

const ChatHeader = styled.div`
  padding: 16px;
  border-bottom: 2px solid ${props => props.theme.colors.border};
  display: flex;
  justify-content: space-between;
  align-items: center;

  h1 {
    margin: 0;
    text-transform: uppercase;
  }
`;

const ChatInput = styled.div`
  border-top: 2px solid ${props => props.theme.colors.border};
`;

const LogoutButton = styled.button`
  background: none;
  border: 2px solid ${props => props.theme.colors.error};
  color: ${props => props.theme.colors.error};
  padding: 4px 8px;
  font-family: 'VT323', monospace;
  cursor: pointer;
  text-transform: uppercase;

  &:hover {
    background: ${props => props.theme.colors.error};
    color: ${props => props.theme.colors.background};
  }
`;

const ChannelHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 16px;

  h2 {
    margin: 0;
    text-transform: uppercase;
    color: ${props => props.theme.colors.primary};
  }
`;

const CreateChannelButton = styled(Button)`
  padding: 2px 8px;
  font-size: 0.875rem;
`;

const ChannelGroup = styled.div`
  margin-bottom: 16px;

  &:last-child {
    margin-bottom: 0;
  }
`;

const GroupTitle = styled.h3`
  margin: 0 0 8px 0;
  padding: 4px 8px;
  font-size: 0.875rem;
  text-transform: uppercase;
  color: ${props => props.theme.colors.textLight};
  border-bottom: 1px solid ${props => props.theme.colors.border};
  font-family: 'Courier New', monospace;
`;

const ChannelActions = styled.div`
  display: flex;
  gap: 8px;
`;

const SettingsButton = styled(Button)`
  padding: 2px 8px;
  font-size: 0.875rem;
`;

const SearchContainer = styled.div`
  position: relative;
  margin-right: 16px;
`;

// Constants
const PAGE_SIZE = 50;

const MainLayout: React.FC = () => {
  // Local state
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [searchResults, setSearchResults] = useState<SearchResult | null>(null);
  const [isSearching, setIsSearching] = useState(false);
  const [searchError, setSearchError] = useState<string | null>(null);
  const messageListRef = useRef<HTMLDivElement>(null);

  // Redux state
  const dispatch = useDispatch();
  const {
    channels,
    activeChannel,
    activeChannelId,
    channelMessages,
    currentUser,
    users
  } = useSelector((state: RootState) => ({
    channels: state.chat.channels,
    activeChannel: state.chat.channels.find(c => c.id === state.chat.activeChannelId),
    activeChannelId: state.chat.activeChannelId,
    channelMessages: state.messages.messagesByChannel[state.chat.activeChannelId || ''] || [],
    currentUser: state.auth.user,
    users: state.chat.users
  }));

  // Handle search
  const handleSearch = async (query: string) => {
    if (!query.trim()) {
      setSearchResults(null);
      return;
    }

    setIsSearching(true);
    try {
      const results = await searchAll(query);
      setSearchResults(results);
    } catch (error) {
      console.error('Search error:', error);
      setSearchError('Failed to search');
    } finally {
      setIsSearching(false);
    }
  };

  // Handle search result selection
  const handleSelectChannel = (channelId: string) => {
    dispatch(setActiveChannel(channelId));
    setSearchResults(null);
  };

  const handleSelectMessage = async (channelId: string, messageId: string) => {
    try {
      // Switch to the channel if needed
      if (channelId !== activeChannelId) {
        dispatch(setActiveChannel(channelId));
      }

      // Set the selected message in Redux
      dispatch(setSelectedMessageId({ channelId, messageId }));
      setSearchResults(null);
    } catch (error) {
      console.error('Error selecting message:', error);
    }
  };

  const handleSelectFile = (fileId: string) => {
    // TODO: Implement file selection
    console.log('Selected file:', fileId);
    setSearchResults(null);
  };

  // Handle logout
  const handleLogout = () => {
    dispatch(logout());
  };

  // Sort channels
  const sortedChannels = useMemo(() => {
    return [...channels].sort((a, b) => a.name.localeCompare(b.name));
  }, [channels]);

  const publicChannels = useMemo(() => 
    sortedChannels.filter(channel => channel.is_public && !channel.is_direct_message),
    [sortedChannels]
  );

  const privateChannels = useMemo(() => 
    sortedChannels.filter(channel => !channel.is_public && !channel.is_direct_message),
    [sortedChannels]
  );

  const directMessages = useMemo(() => 
    sortedChannels.filter(channel => channel.is_direct_message),
    [sortedChannels]
  );

  // Handle channel selection
  const handleChannelSelect = useCallback(async (channelId: string) => {
    if (channelId === activeChannelId) return;
    dispatch(setActiveChannel(channelId));
  }, [activeChannelId, dispatch]);

  return (
    <MainContainer>
      <Sidebar>
        <ChannelList>
          <CreateChannelButton onClick={() => setIsCreateModalOpen(true)}>
            Create Channel
          </CreateChannelButton>

          {/* Public Channels */}
          <ChannelGroup>
            <GroupTitle>Public Channels</GroupTitle>
            {publicChannels.map(channel => (
              <ChannelListItem
                key={channel.id}
                name={channel.name}
                isActive={channel.id === activeChannelId}
                hasUnread={channel.unreadCount > 0}
                isPublic={true}
                onClick={() => handleChannelSelect(channel.id)}
              />
            ))}
          </ChannelGroup>

          {/* Private Channels */}
          <ChannelGroup>
            <GroupTitle>Private Channels</GroupTitle>
            {privateChannels.map(channel => (
              <ChannelListItem
                key={channel.id}
                name={channel.name}
                isActive={channel.id === activeChannelId}
                hasUnread={channel.unreadCount > 0}
                isPublic={false}
                onClick={() => handleChannelSelect(channel.id)}
              />
            ))}
          </ChannelGroup>

          {/* Direct Messages */}
          <ChannelGroup>
            <GroupTitle>Direct Messages</GroupTitle>
            {directMessages.map(channel => (
              <ChannelListItem
                key={channel.id}
                name={channel.name}
                isActive={channel.id === activeChannelId}
                hasUnread={channel.unreadCount > 0}
                isDirect={true}
                isPublic={false}
                onClick={() => handleChannelSelect(channel.id)}
              />
            ))}
          </ChannelGroup>
        </ChannelList>

        <UserList>
          <h2>Users</h2>
          {Object.values(users).map(user => (
            <UserListItem
              key={user.id}
              username={user.username}
              status={user.status}
            />
          ))}
        </UserList>
      </Sidebar>

      <ChatArea>
        <ChatHeader>
          {activeChannel && (
            <>
              <h1>{activeChannel.name}</h1>
              <ChannelActions>
                <SearchContainer>
                  <SearchBar onSearch={handleSearch} />
                  {isSearching && <div>Searching...</div>}
                  {searchResults && (
                    <SearchResults
                      results={searchResults}
                      isLoading={isSearching}
                      onClose={() => setSearchResults(null)}
                      onSelectChannel={handleSelectChannel}
                      onSelectMessage={handleSelectMessage}
                      onSelectFile={handleSelectFile}
                    />
                  )}
                </SearchContainer>
                <SettingsButton onClick={() => setIsSettingsOpen(true)}>
                  Settings
                </SettingsButton>
                <LogoutButton onClick={handleLogout}>
                  Logout
                </LogoutButton>
              </ChannelActions>
            </>
          )}
        </ChatHeader>

        <MessageList
          ref={messageListRef}
          messages={channelMessages}
        />

        <ChatInput>
          <MessageInput
            channelId={activeChannelId}
          />
        </ChatInput>
      </ChatArea>

      {isCreateModalOpen && (
        <CreateChannelModal
          isOpen={isCreateModalOpen}
          onClose={() => setIsCreateModalOpen(false)}
        />
      )}

      {isSettingsOpen && activeChannel && (
        <ChannelSettings
          isOpen={isSettingsOpen}
          channel={activeChannel}
          onClose={() => setIsSettingsOpen(false)}
        />
      )}
    </MainContainer>
  );
};

export default MainLayout;
